css:
@-moz-document domain("www.zerohedge.com") {
body {
  background: rgba(0,0,0,0.8);
}

.node-teaser.sticky {
  background: rgba(255,255,255,0.2);
}

.node-teaser .teaser-title a {
 	color: rgba(255,255,255,0.7);   
}

.teaser-text {
 	color: rgba(255,255,255,0.65);
}

.teaser-text a {
 color: rgba(45,150,230,1);
}

.teaser-text a:hover {
 color: rgba(45,150,230,1);
	opacity: 0.8;
}

.link-created span span {
  color: rgba(255,255,255,0.65);
}

.teaser-header .title a {
	color: rgba(255,255,255,0.65); 
}

.teaser-submitted_username a {
 	color: rgba(25,150,255,0.9);
}


.teaser-submitted_username a:hover {
 	color: rgba(25,150,255,0.9);
  opacity: 0.75;
}

.node-teaser .teaser-details .links ul a:hover, .node-teaser .teaser-details .links li a:hover{
  color: rgba(45,150,230,1);
	opacity: 0.8;
}

#site-header {
  background: #444444;

}

.content p {
  color: rgba(255,255,255,0.75);
}

.content p a {
  color: rgba(45,150,230,1);
}

.content ul {
  color: rgba(255,255,255,0.75);
}


.content ul li {
  color: rgba(255,255,255,0.75);
}


.content ul li a {
  color: rgba(40,140,220,0.9) !important;
}


.content ul li a:hover {
  color: rgba(255,255,255,0.9) !important;
  opacity: 0.8;
}

.content p a:hover {
  color: rgba(45,150,230,1);
  opacity: 0.8;
}

.article-list .title {
  color: rgba(255,255,255,0.7);
}

.node.node-full .a2a_kit {
  display: none;
}

#site-header .desktop-menu a {
  color: rgba(255,255,255,0.7);
}

.node.node-full .submitted a {
  color: rgba(45,150,230,1);
}

.node.node-full .submitted a:hover {
  color: rgba(45,150,230,1);
  opacity: 0.8;
}

.comment-content p {
  color: rgba(255,255,255,0.7);
}

.comment-content p a {
  color: rgba(45,150,230,1);
}


.comment-content p a:hover {
  color: rgba(45,150,230,1);
  opacity: 0.8;
}


.comment-header_author a {
  color: rgba(45,150,230,1);
}

.comment-header_author a:hover {
  color: rgba(45,150,230,1);
  opacity: 0.8;
}

#sidebar-left .block, #sidebar-right .block {
  background: rgba(255,255,255,0.1);
}

li.leaf a {
 	color: rgba(25,160,255,0.9);
}

.field-content {
  color: rgba(255,255,255,0.6);
}

.content ol li {
  color: rgba(255,255,255,0.6);
}

.field-content a {
 	color: rgba(25,150,255,0.9);
}

span.username-right {
    color: rgba(40,140,220,0.9);
}

a.accordion-section-title {
    color: rgba(40,140,220,0.9);
}

.account-right a {
    color: rgba(255,255,255,0.5);
}

.profile {
    color: rgba(255,255,255,0.5);
}
}
