css:
@-moz-document domain("fuliba.net") {
html{color:#000;background:#FFF}body,div,dl,dt,dd,ul,ol,li,h1,h2,h3,h4,h5,h6,pre,code,form,fieldset,legend,input,textarea,p,blockquote,th,td{margin:0;padding:0}table{border-collapse:collapse;border-spacing:0}fieldset,img{border:0}address,caption,cite,code,dfn,em,strong,th,var{font-style:normal;font-weight:normal}ol,ul{list-style:none}caption,th{text-align:left}h1,h2,h3,h4,h5,h6{font-size:100%;font-weight:normal}q:before,q:after{content:''}abbr,acronym{border:0;font-variant:normal}sup{vertical-align:text-top}sub{vertical-align:text-bottom}input,textarea,select{font-family:inherit;font-size:inherit;font-weight:inherit}input,textarea,select{*font-size:100%}legend{color:#000}#yui3-css-stamp.cssreset{display:none}


@font-face {
    font-family:"Futura";
    src:url("http://fulibalight.oss-cn-shanghai.aliyuncs.com/font/Futura%20LT%20Light.ttf");
}
body,
a,
p{
   font-family: "Futura","Avenir Next", "Segoe UI", "Helvetica Neue", Arial, "Hiragino Sans GB", "PingFang SC", 
   "Heiti SC", "Microsoft YaHei UI", "Microsoft YaHei", "Source Han Sans", sans-serif; 
   color: #161616;
}
body{
    font-size: 14px;
}
#wrap{
    overflow: inherit;
    width: 1000px;
}
#wrap,
#container,
#header,
body{
    background: #efefef;
    padding: 0;
}
#container{
    width: 1000px;
}
#header {
    background-image: none;
    box-shadow: none;
    height: 160px;
}
.logo,
#header::before{
    /*content: "¸£Àû°É";*/
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%);
    font-size: 50px;
    color: #D3504A;
}
.header-notice{
    display: none;
}
.logo{
    top: 0;
    background-image: url("http://fulibalight.oss-cn-shanghai.aliyuncs.com/img/fuliba_logo.png");
    width: 338px;
    height: 126px;
}
#menu-nav,
#menu-nav>li,
#menu-nav>li>a,
#menu-nav>li::before{
    margin: 0 auto;
}
#menu-nav {
    margin: 0;
}
#menu-nav>li {
    width: 90px;
    height: 38px;
    margin: 0;
    padding: 0;
    text-align: center;
}
#menu-nav>li>a {
    display: inline-block;
    width: 90px;
    height: 38px;
    color: #4c4c4c !important;
    margin: 0 auto;
    padding: 0;
    vertical-align: middle;
    line-height: 38px;
    transition: color 0.4s cubic-bezier(0.55, 0.09, 0.68, 0.53);
}
#menu-nav>li:hover {
    background: #d8d8d8;
    color: #D3504A;
}
#menu-nav>li:hover a {
    background: #fefefe;
    color: #4c4c4c;
    opacity: 1!important;
    height: 100%;
    transition: color 0.4s cubic-bezier(0.55, 0.09, 0.68, 0.53), height 0.4s cubic-bezier(0.55, 0.09, 0.68, 0.53);
}
#menu-nav li .sub-menu a {
    color: #4c4c4c;
    height: 100%;
}
#menu-nav>li:hover>a {
    background: #fefefe;
    color: #D3504A;
    opacity: 1!important;
}
#menu-nav li .sub-menu {
    opacity: 1;
}
#menu-nav>li:hover>a,
#menu-nav li.current-menu-item a,
#menu-nav li .sub-menu a:hover {
    background: #efefef;
    color: #D3504A !important;
}
.nav-right:hover a.show-search{
    opacity: 0.5;
    background: #f8f8f8;
}
#menu-nav li .sub-menu {
    position: relative;
    top: 0;
    display: block !important;
}
#menu-nav li .sub-menu li {
    width: 90px;
    height: 0;
    box-shadow: 0 0px 1px 1px #D7D7D7;
    position: relative;
}
#menu-nav li:hover .sub-menu li {
    height: 100%;
}
#menu-nav li.current-menu-item::before {
    content: '';
    display: block;
    height: 3px;
    width: 100%;
    opacity: 1;
    background: #D3504A;
}
#menu-nav>li::before {
    content: '';
    display: block;
    height: 3px;
    width: 0;
    background: #D3504A;
    opacity: 0;
    position: relative;
    margin: 0 auto;
    transition: width 0.3s cubic-bezier(0.55, 0.09, 0.68, 0.53), opacity 0.2s ease-in-out;
}
#menu-nav>li:hover::before {
    width: 100%;
    opacity: 1;
}
#navigation,
#container {
    box-shadow: 0 1px 1px 1px #D8D8D8;
    position: relative;
    left: 50%;
    transform: translate(-50%);
    z-index: 1000;
    background: #f8f8f8;
}
#navigation{
    width: 1040px;
    height: 41px;
}
#container{
    padding: 10px 0;
    z-index: 10;
}
#menu-nav li .has-submenu a:hover {
    color: #D3504A;
}
#container,
#menu-nav li.current-post-ancestor a {
    background: #f8f8f8;
}
#content{
    width: 660px;
    margin: 10px 0;
    padding: 10px 20px;
    padding-left: 25px;
    border: 0;
}
/*#content>div{
    display: none;
}*/
#content-page{
    display: block !important;
    background-color: #f8f8f8;
}
#sidebar{
    width: 250px;
    border: 1px dashed #7c7c7c;
    /*border-top: 0;*/
    /*border-bottom: 0;*/
    padding: 20px 10px;
    margin: 0 3px;
    margin-right: 20px;
}
#sidebar ul{
    border: 0 !important;
}
.entry-header{
    position: relative;
    width: 460px;
    left: 220px;
}
.entry-commentnumber{
    right: 20px;
    top: 0;
}
.entry-commentnumber .number{
    font-size: 12px;
    background-color: #D3504A;
    border-radius: 2px;
}
.entry-commentnumber .number{
    transition: background-color 0.4s ease-in-out;
}
.entry-commentnumber .corner{
    border-color: #D3504A rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) #D3504A;
    transition: border-color 0.4s ease-in-out;
}
.entry-commentnumber:hover .corner{
    border-color: #ea7379 rgba(255, 255, 255, 0) rgba(255, 255, 255, 0) #ea7379;
}
.entry-commentnumber
.entry-common{
    margin-bottom: 20px;
}
.entry-name{
    font-size: 13px;
    padding-right: 18px;
}
.entry-name a{
    position: relative;
    top: -2px;
    color: #484848;
    transition: color 0.4s ease-in-out;
}
.entry-name a:hover{
    color: #D3504A;
}
.entry-meta{
    margin: 0 auto;
    margin-top: 0;
    width: 180px;
    border-top: 1px solid #c4c4c4;
    width: 100%;
    text-align: center
}
.pre-cat{
    margin-right: 10px;
    font-size: 9px;
    width: 60px;
    background-color: #D3504A;
    z-index: 9;
    border-radius: 2px;
    transition: background-color 0.4s ease-in-out;
}
.pre-cat:hover,
.entry-commentnumber:hover .number
{
    background-color: #ea7379;
}
.pre-catarrow{
    border-left-color: #D3504A;
    z-index: 3;
    right: -7px;
    transition: border-left-color 0.4s ease-in-out;
}
.pre-cat:hover .pre-catarrow{
    border-left-color: #ea7379;
}
.pre-catinner a{
    padding: 0 2px;
    margin-top: 1px;
    text-align: center;
}
.post-thumb a.thumb{
    position: relative;
    top: -30px;
    overflow: hidden;
    width: 200px;
    height: 140px;
    margin-right: 20px;
    cursor: pointer;
}
.category-content .post-thumb{
    position: relative;
    top: -5px;
    font-size: 13px;
    height: 115px;
    z-index: 10;
    line-height: 23px;
    margin-bottom: 0;
}
.category-content a.thumb img{
    transition: transform 0.6s ease-in-out;
    position: absolute;
    top: -6px;
}
.category-content a.thumb:hover img{
    transform: scale(1.1);
}
.category-content a.thumb::before{
    content: '';
    display: block;
    width: 200px;
    height: 140px;
    position: absolute;
    opacity: 0;
    z-index: 9;
    cursor: pointer;
    background: #FFD6B6;
    transition: opacity 0.6s ease-in-out;
}
.category-content a.thumb:hover::before{
    opacity: 0.4;
}
.tool_news1{
    opacity: 0;
}
#ad_right,
#ad_left{
    opacity: 0;
}
.widget_new img{
    width: 250px;
    height: auto;
}
.widget h2{
    background-color: #f8f8f8;
    box-shadow: 0 1px 2px 1px #bdbdbd;
    color: #484848;
    padding-left: 10px;
}
.widget h2::before{
    content: '';
    display: block;
    width: 10px;
    height: 100%;
    float: left;
    background: #D3504A;
    position: relative;
    left: -10px;
}
.widget li{
    float: left;
    padding: 5px 0 0 5px;
}
.widget li a{
    line-height: 30px;
    height: 30px;
    padding: 0 10px;
    margin-right: 5px;
    display: block;
    text-align: center;
    font-size: 12px;
}
.tagcloud{
    padding: 0 5px;
}
.widget li a,
.tagcloud a,
#content-page>a
{
    background: #efefef;
    color: #6b6b6b; 
    transition: color 0.4s ease-in-out,background-color 0.4s ease-in-out;
}
.tagcloud a{
    font-size: 9px !important;
    width: 65px;
    height: 15px;
    line-height: 15px;
}
.tagcloud a:hover,
.widget li a:hover,
#content-page>a:hover{
    background-color: #D3504A;
    color: #f8f8f8;
}
.content-page>a.current{
    background-color: #D3504A !important;
    color: #f8f8f8 !important;
}
#footer{
    background-color: #efefef;
    background-image: none;
    color: #6b6b6b;
    border: 0;
    width: 630px;
    margin: 0 auto;
    margin-top: 40px;
    height: auto;
    padding-bottom: 35px;

}
footer .footer-navi{
    padding-left: 190px;
}
footer .footer-navi .nav .name{
    padding-left: 10px;
}
footer .footer-navi .nav{
    padding-left: 10px;
    height: 100%;
    width: 100%;
}
footer .footer-navi .nav .name{
    float: left;
    padding-right: 10px;
}
footer .footer-navi ul li{
    float: left;
}
#footer .menu li a{
    display: inline-block;
    width: 70px;
    text-align: center;
    margin-right: 10px;
    background: #f8f8f8;
    border: 1px dashed #484848;
    color: #484848;
}
#footer p:nth-of-type(1){
    position: absolute;
    top: 2px;
}
#footer p:nth-of-type(2){
    display: none;
}
}
