css:
@-moz-document url-prefix("https://www.xetown.com/") {
.xe_updated_ver .btn_item {
    background: #778899 !important;
    color: white;
    font-weight: bold;
}
div.xetownWidgetStyle .widgetTitle h2 {
    color: white !important;
    font-weight: bold;
}
.xetownContainer ul.contentNormal li a .title,
.today_login_list li,
.widgetContent a,
.xetownContainer ul.contentNormal li a .part .date,
.rPoint{
    color: white !important;
    font-weight: bold;
}
.rssContainer ul.contentRss li a .part .date {
    color: lightpink !important;
}
.xetownContainer ul.contentNormal li a .part .hour {
    color: red !important;
}
.widgetTitle,
.widgetContent,
div.xetownWidgetStyle {
    background: #778899 !important;
}
.bd_zine .cnt {
    color: white !important;
}
ul.cnb3.cnb3.clear a,
a.btn_img{
    color: black !Important;
}
div#reply a,
h3.feedbackHeader,
.desc_area li,
.desc_area li a {
    color: white;
}
.info_basic h2 {
    color: white;
}
.sub_desc,
.bd_faq_srch {
    background: #778899 !important;
    color: white !important;
}
.cmt_view_wrap .writebox .commentArea .xe_content {
    color: black !important;
}
.bd_tb_lst td,
.bd_tb_lst .hx:visited {
    color: lightcoral;
}
.cmt_editor {
    background: #1A2835;
}
.cmt_editor_tl strong {
    color: white;
}
.fdb_itm .meta a {
    color: white;
}
.bd_lst td.title a {
    color: white;
}
.bd_lst_wrp {
    border-style: solid;
}
.xe_content,
.xe_content > div,
.xe_content > span,
.xe_content > p,
.xe_content > ul,
.xe_content > ol,
.xe_content > h2,
.xe_content > h1,
.xe_content > h2,
.xe_content > h3,
.xe_content > h4,
.xe_content > h5,
.xe_content > h6,
.ngeb,
.rt_area.is_tmb,
.visual.sub .sub_title h1 {
    color: white !important;
}
.xe_content pre {
    background: #1A2835 !important;
}
.rd_hd .board .top_area {
    background: none;
}
.bd_tb_lst th {
    border-top: none;
    border-bottom: none;
}
.bd_tb_lst tbody tr:hover {
    background: black !important;
}
.bd a,
.regdateHour,
.bd_tb_lst th,
.bd_tb_lst td {
    color: white;
    font-weight: bold;
}
.xe_content a,
.xe_content a:hover,
.xe_content a:active,
.xe_content a:focus {
    color: #08c !important;
}
.header_wrap {
    background-color: #778899 !important;
}
thead.bg_f_f9 {
    background: #1A2835 !important;
}
.bd_tb_lst .notice td,
.bd_zine li.notice,
.bd_tb_lst .select,
.bd_tb_lst tbody tr:hover {
    background: #1A2835 !important;
}
.container,
.visual.sub {
    background-color: #1A2835 !important;
}
.fdb_tag.bg_f_f9.css3pie {
    background: #1A2835 !important;
}
.body .lnb > ul > li.on {
    background-color: #709099 !important;
}
.body .lnb > ul > li {
    background-color: #708090 !important;
}
.body .lnb > ul > li > a,
.body .lnb > ul > li > span {
    color: white;
}
.bd {
    background-color: #1A2835 !important;
}
.bd_tb_lst th,
.bd_tb_lst td {
    font-size: 14px !important;
}
.notice td {
    background: #708090 !important;
}
td.author,
td.title,
.bd_tb_lst .cate,
.nick {
    font-weight: bold !important;
}
.bd_tb_lst .cate span,
.bd_tb_lst .author span,
.bd_tb_lst .last_post small {
    max-width: none !important;
}
.bannerContent,
.rd_sign,
.show_document_reader,
.show_author_document,
.footer .f_info_area {
    display: none !important;
}
a.edit,
.bd .to_sns strong {
    color: black !important;
}
.cke_wysiwyg_frame, .cke_wysiwyg_div{
    background-color: lightslategrey;
}
}
