css:
@-moz-document domain("clientsfromhell.net") {
#sidebar,.myhead,.tmblr-full,.twitterbutton,.facebook,#header > div,iframe{
	display:none;
}

#container{
	width:100%;
}

.pagination{
    background:rgba(0,0,0,0.3);
	position:fixed;
    bottom:2px;
    left:0;
    right:0;
}

.post {
	width: 95%;
    background:#111;
    border:solid transparent!important;
    outline:2px solid lightgray;
    box-shadow: 0px 0px 10px 9px rgba(0,0,0,0.2);
    margin-bottom:25px;
    border-top:25px solid #395a9c!important;
}

.post:before{
	content:"C:/clients/locations/hell/posts";
    color:white;
    margin-top:-22px;
    left:30px;
    position:absolute;
}

.post:after{
	width:12px;
    background:white;
    height:3px;
    content:"";
    margin-top:-20px;
    margin-left:220px;
    position:absolute;
    font-weight:bold;
    animation: blink-animation 1s steps(5, start) infinite;
  -webkit-animation: blink-animation 1s steps(5, start) infinite;
}

@keyframes blink-animation {
  to {
    visibility: hidden;
  }
}
@-webkit-keyframes blink-animation {
  to {
    visibility: hidden;
  }
}

.post-content {
     border-bottom: 0px dotted transparent;
}

#main{
	background:transparent;
    border:solid transparent!important;
}

.date span {
    background: transparent;
}

.previous-page a, .next-page a{
	background-image:none;
}

.post-content{
 color:#cacaca!important;
}

a{
color:#D2673E!important;
    text-decoration:none!important;
    background:#0000ac;
}

body{
    background:#292630;
    font-family:"Courier New", Courier, monospace!important;
}
}
