css:
@-moz-document domain("shazoo.ru") {
.desktop .branding {
    display: none
}
.brandingClearfix {
    display: none
}
#homeFeaturesContainer {
    display: none
}
.adContainer {
    display: none
}
.homeCenter {
    display: none
}
#home .homeBottom {
    display: none
}
#home .homeMain .homeLeft .entry.popular {
    margin: 0 15px 5px 15px;
}
@media only screen and (min-width: 1160px) {
    #home .homeMain .homeLeft {
        -webkit-box-flex: 0;
        -ms-flex: none;
        flex: none;
        background: rgba(0, 0, 0, .02)
    }
}
.homeMain {
    -webkit-box-flex: 0;
    -ms-flex: none;
    flex: none
}
#home .homeMain .homeLeft .entry h2,
#home .homeStream h2 {
    font-size: 16px
}
#home .homeMain .homeLeft .entry.popular .desktop .entryImage:after {
    background: none
}
@media only screen and (min-width: 1160px) {
    #home .homeMain .homeLeft {
        margin: 0 auto
    }
}
.tac {
    display: none
}
@media only screen and (min-width: 801px) {
    #home .homeMain .homeLeft .entry.popular .desktop .entryImage {
        margin: 0;
        padding: 0 0 0 15px
    }
}
@media only screen and (min-width: 801px) {
    #home .homeMain .homeLeft .entry {
        padding: 3px 0px 3px 3px;
        margin-left: 15px;
        margin-right: 15px;
        margin-bottom: 5px
    }
}
.entryList .read {
    background: rgba(189, 189, 189, 0.24);
}
.entry {
    margin-bottom: 10px;
    border-radius: 3px
}
@media only screen and (min-width: 801px) {
    #home .homeMain .homeLeft .entry.popular .desktop .entryImage {
        margin: 0;
        padding: 0
    }
}
.entryDetails {
    padding-top: 16px;
}
@media only screen and (min-width: 801px) {
    #home .homeMain .homeLeft .entry.popular img {
        height: 50px;
        width: 80px;
        padding: 3px;
        object-fit: cover;                                    
    }
}
#home .homeMain .homeLeft .entry.popular .desktop .entryDetails {
    color: black;
}
#home .homeMain .homeLeft .entry.popular .entryByline,
#home .homeMain .homeLeft .entry.popular .entryTitle {
    color: black;
    padding-left: 70px;
    font-size: 1.3em;
}
#home .homeMain .homeLeft .entryByline.style-date,
#home .homeMain .homeLeft .entryByline.style-date-ago,
#home .homeMain .homeLeft .entryByline.style-date-ago-comments {
    display: none;
    float: left;
    font-size: 5px;
}
@media only screen and (min-width: 801px) {
    #home .homeMain .homeLeft .entry.popular .entryTitle {
        position: relative;
        bottom: 13px;
        left: -3px;
        right: -50px;
        font-size: 1.2em;
        font-weight: 700;
        margin: 0;
        top: 6px;
    }
}
.entryDetails {
    padding-top: 0;
}
.entryTitle {
    padding-top: 16px;
}
.entry.popular .entryTitle {
    padding-top: 0;
}
.popular {
    background: rgba(206, 206, 206, 0.65);
}
.entryList .entry {
    padding-right: 24px;
    padding-left: 24px;
}
.newsStream .entryList .read {
    background: transparent;
}
.asideBlock .ad {
    display: none;
}
.adBlock {
    display: none;
}
.entryList .entry {
    background-color: rgba(0, 0, 0, 0.04);
}

.entryComments {
    padding-right: 15px;
}
}
