css:
@-moz-document domain("flightrising.com") {



div[style="outline: 0px; z-index: 1002; height: auto; width: 470px; top: 100px; left: 473.5px; display: block;"]
{
        top: 10px !important;
}


div[style="width:420px; height:200px; position:relative;"]{
    width: 420px !important;
    height: 350px !important;
}

div[style="width:400px; height:200px; margin-left:auto; margin-right:auto; text-align:center;"]
{
    width: 400px !important;
    height: 350px !important;
}

img[style="vertical-align:bottom;"] {
    _width: 350px !important;
    _height: 350px !important;
}

div#spec_top img,
div#spec_top div img,
form#specform div img{
	width: 350px !important;
    height: 350px !important;
}

div[style="width:400px; height:275px; margin-left:auto; margin-right:auto; text-align:center;"]
{
        width: 400px;
    height: 350px !important;
}

div[style="width:420px; height:275px; position:relative; text-align:center; margin-left:auto; margin-right:auto;"]
{    width: 420px;
    height: 350px !important;
}

img[id="skin-preview-image"]{
    height: 350px !important;
    width: 350px !important;
}

#spec_top {
    width: 420px;
    height: 350px !important;
}

div#spec_top div img[src="/images/layout/loading.gif"]{
    width:16px !important; height:16px !important;
}

form#specform div span{
    
    top: -80px;
    background-color: white;
}
}
