css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("autohotkey.com") {
  body, .splash, .ribbon, #ipbwrapper { background-color: #000C17 !important; color: #777 !important; }
  *[style="color:#000000"] { color: #777 !important; }
  body * { border-color: #00ACAF !important; }
  .panel { color: #777 !important; }
  table.table1 td { color: #00ACAF !important; }
  
  img[src*="//autohotkey.com/board/ahk_logo_ipb2.png"] { padding-left: 221px !important; padding-top: 72px !important; background: transparent url("http://i.imgur.com/2EbeUQ7.png") no-repeat !important; width: 0px !important; height: 0px !important; background-size: contain !important; }

  img[src="https://www.autohotkey.com/docs/static/ahk_logo_no_text.png"], img[src="static/ahk_logo.png"], img[src="https://autohotkey.com/docs/static/ahk_logo_no_text.png"], img[src="./styles/simplicity/theme/images/logo.png"] { height: 0 !important; width: 0 !important; padding-left: 217px !important; padding-top: 70px !important; background: url("http://i.imgur.com/BKcgvk6.png") no-repeat !important; background-size: contain !important; } /* */
  
  /* header */
  .navbar .nav-tabs { background-color: #06253F !important; }
  .navbar .nav-tabs .tab:hover { background-color: #000C67 !important; color: #00ACAF !important; }
  .navbar .nav-tabs .tab.dropdown-visible { background-color: #000C67 !important; }
  .dropdown .dropdown-contents { background-color: #000C27 !important; }
  .navbar .nav-tabs .tab.selected { background-color: #000C67 !important; }
  
  a.time:hover { color: #00ACAF !important; }
  
  /* search */
  .search-header .inputbox, .search-header .button, .search-header .post-buttons > #expand > a { background-color: transparent !important; border-color: transparent !important; }
  .search-header .inputbox:hover { background-color: transparent !important; border-color: transparent !important; }
  .search-header .inputbox.search { background-color: #000C17 !important; color: #00ACAF !important; border-color: transparent !important; }
  .search-header .button:hover { background: #000C67 none !important; border-color: #000C17 !important; }
  .search-header .button:hover::before { background: transparent none !important; color: #00ACAF !important; }
  
  div#search-btn { background: #000C17 none !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  div#search-btn:hover { background-color: #000C67 !important; }
  
  /* input boxes and button */
  a.button2 { background: transparent none !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  a.button2:hover { background-color: #000C67 !important; }
  input { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  input.google-pure-input-1-2 { background: transparent url("http://i.imgur.com/aH3OnYp.png") no-repeat left !important; padding: 4px !important; }
  input.google-pure-input-1-2:focus { background: none !important; }
  .pure-button { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  .pure-button:hover { background-color: #000C67 !important; }
  .tools { border-color: #00ACAF !important; }
  input[type="reset"]:hover, input[type="submit"]:hover { background-color: #000C67 !important; }
  .inputbox:focus, select:focus, .inputbox, select { background-color: transparent !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  
  .hdr-table .hdr-language li { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  .hdr-table .hdr-language li:hover { background-color: #000C67 !important; }
  
  /* docs header */
  .header { background-color: #000C17 !important; }
  .navh1 { color: #00ACAF !important; }
  .nav { background-color: #000C27 !important; color: #00ACAF !important; }
  .nav li span { color: #00ACAF !important; }
  .nav .selected { background-color: #000C47 !important; }
  .nav li span:hover { background-color: #000C67 !important; color: #00ACAF !important; }
  .nav .selected span { color: #00ACAF !important; }
  #headerbar { background: #000C27 !important; border-bottom-color: #00ACAF !important; }
  
  .l-box b, .splash h1, h4 { color: #00ACAF}
  dd { border-left-color: #00ACAF !important; }
  
  h1, h2, h3 { border-bottom-color: #00ACAF !important; }
  strong { color: #00ACAF !important; }
  
  .right-col #main-content
  
  /* nav header */
  .navh1 { background-color: #000C27 !important; }
  
  /* examples */
  pre, code { border: 1px solid #00ACAF !important; background-color: #000C27 !important; }
  
  /* side menu */
  ul.jqtree-tree li > .jqtree-element:hover { background-color: #000C67 !important; }
  
  /* footer */
  .footer, .footer a { background-color: #000C27 !important; color: #00ACAF !important; }
  
  /**********/
  /* forums */
  /**********/
  
  #branding { background-color: #041d3f !important; }
  #community_app_menu > li > a:hover, #community_app_menu > li > a.menu_active { background-color: #000C67 !important; }
  
  .forabg ul.topiclist li.row, .forumbg ul.topiclist li.row, .navbar, .panel, .bg2 { background-color: #000C17 !important; } /* some bgs */
  .bg1 { background-color: #000C27 !important; }
  .bg1:hover, .bg2:hover { background-color: #000C37 !important; }
  ul.topiclist li, .content h2, .panel h2, label, .content { color: #00ACAF !important; }
  
  blockquote { background-color: #000C27 !important; border-color: #00ACAF !important; border-width: 1px 1px 1px 3px !important; }
  
  .postbody, .post_body { color: #00ACCF !important; }
  
  /* pagination - main forum */
  .pagination li.ellipsis span { color: #00ACAF !important; }
  .pagination li a { background-color: #000C17 !important; }
  .pagination li a:hover { background-color: #000C67 !important; }
  
  /* topic search ??? */
  .search-box .button, .search-box .button:hover { background-color: #000C17 !important; border: 1px solid transparent !important; }
  
  /* controls */
  .ipsBox { background: #000C47 !important; }
  #vnc_filter_popup_close, body#ipboard_body fieldset.submit, body#ipboard_body p.submit, .ipsForm_submit { background-color: #000C47 !important; }
  textarea:focus, .input_text:focus { background: transparent none !important; text-shadow: none !important; }
  textarea, .input_text { text-shadow: none !important; }
  .input_submit:hover { background-color: #000C67 !important; }
  
  .codebox { background-color: #000C27 !important; }
  .codebox_plus code, .codebox code { color: #777 !important; }
  .coMULTI, .co1 { color: #043E00 !important; }
  .edit { background-color: #000C57 !important; text-shadow: none !important; }
  
  .post_spoiler_show:hover { background-color: #000C67 !important; }
  
  /* search */
  #search_wrap { background-color: #000C47 !important; border-bottom: none !important; }
  #adv_search:hover { background-color: #000C67 !important; border-bottom: none !important; }
  #search_options { background-color: #000C17 !important; border: 1px solid #00ACAF !important; }
  #search_options_menucontent { background-color: #000C17 !important; }
  #search_options_menucontent label:hover { background-color: #000C47 !important; }
  
  /* msg error */
  .message.error { background-color: #000C47 !important; color: red !important;  }
  .message { background: none !important; text-shadow: none !important; }
  
  /* topic / login buttons */
  .topic_buttons li.disabled a, .topic_buttons li.disabled span { background-color: #000C37 !important; }
  
  /* topic reply talley */
  #community_app_menu > li.active > a, #secondary_navigation, .topic_buttons li a, .ipsButton, a.ipsButton, .pagination .pages li.active, .forumHover tr:hover td, img[src*="f_icon.png"], img[src*="f_icon_read.png"], img[src*="f_redirect.png"], .maintitle, .maintitle.linkBar, ul.post_controls a, ul.post_controls a.ipsButton_secondary, .post_block h3, .horizontalView .author_info, .col_f_icon img, .ipsLikeButton.ipsLikeButton_enabled, #footer { background-color: #000C87 !important; }
  
  /* page nav */
  .pagination a { color: #00ACAF !important; }
  .pagination .page a, .pagination .back a, .pagination .forward a { background: #000C27 !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  .pagination .page a:hover, .pagination .back a:hover, .pagination .forward a:hover { background-color: #000C67 !important; } 
  
  /* post date */
  .horizontalInfoList { color: #777 !important; }
  
  /* posts */
  .border, .statistics, .post_block, .ipsComment, .popupInner, .no_messages, .general_box, .ipsSideBlock, .poll_question ol, .ipsBox_container, .ipsFloatingAction, .column_view .post_body { background-color: #000C17 !important; }
  .postprofile { color: #005CC7 !important; }
  
  .ipsType_pagetitle { color: #00ACAF !important; }
  .post_body:hover { background-color: #000C37 !important; }
  .post_wrap .author_info { background-color: #000C37 !important; }
  pre.prettyprint, code.prettyprint { background-color: #000C27 !important; }
  .pln { color: #777 !important; }
  .typ { color: pink !important; }
  .kwd { color: cyan !important; }
  .lit { color: #00ACAF !important; }
  .ipbfs_titlebox { background-color: #000C37 !important; }
  .horizontalView .author_info { background: #000C47 none !important; }
  ul.post_controls { background-color: #000C67 !important; }
  
  /*********/
  /* other */
  /*********/
  
  table.info th { background-color: #000C27 !important; }

}
