@-moz-document domain("services.addons.mozilla.org"), domain("addons.cdn.mozilla.net") {
  body { text-shadow: none !important; color: #777 !important; }
  
  /* main page */
  .pane header { background: none !important; border: 1px solid #00ACAF !important; box-shadow: none !important; }
  section > section { background: transparent none !important; border: 1px solid #00ACAF !important; box-shadow: none !important; }
  section#featured-addons h2 { color: #00ACAF !important; }
  .addons li > a { background: transparent none !important; border: 1px solid #00ACAF !important; box-shadow: none !important; }
  .addons li > a > p { color: #777 !important; }
  .wrap { background: transparent none !important; border: 1px solid #00ACAF !important; box-shadow: none !important; }
  
  section#promos li.panel > div { background: transparent none !important; }
  section h2 { text-shadow: none !important; }
  
  section#sub > section#up-and-coming * { border-color: #00ACAF !important; background: transparent none !important; box-shadow: none !important; }
  section#sub > section#up-and-coming a.addon-title { color: #00ACAF !important; }
  section#sub > section h2 { color: #00ACAF !important; }
  
  section#sub > section#featured-themes h2 { background: transparent none !important; box-shadow: none !important; }
  section#sub > section#featured-themes span.addon-title { border-top-color: #00ACAF !important; background: rgba(0,32,192,.75) none !important; color: #00ACAF !important; }
  section#sub > section#featured-themes span.addon-title:hover { background-color: #000C67 !important; }
  
  .nav-prev a { background: url(https://addons.cdn.mozilla.net/static/img/zamboni/discovery_pane/arrow-left.png?7cfb4e8) no-repeat center center, linear-gradient(to right,rgba(0,32,255,.8) 0,rgba(32,0,255,.5) 80%) !important; }
  .nav-next a { background: url(https://addons.cdn.mozilla.net/static/img/zamboni/discovery_pane/arrow-right.png?50ae1bd) no-repeat center center, linear-gradient(to right,rgba(0,32,255,.8) 0,rgba(32,0,255,.5) 80%) !important; }
  
  /* items */
  .detail header { text-shadow: none !important; } /* */
  h1.addon, h1.addon .version { color: #00ACAF !important; }
