css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vistastylebuilder.com") {

body {
    background: #222 !important;
    color: #fff !important;
}
input, textarea, button, select {
    background: #111 !important;
    color: #fff !important;
    border: 1px solid #333 !important;
}
#logoheader, #header {
  -moz-border-bottom-colors: #333 #000;
  background: #111 none repeat scroll 0 0 !important;
  border-bottom: 2px solid #000;
  border-color: #000;
  padding-bottom: 1em;
}
#logo {
  background: rgba(0, 0, 0, 0) url("http://oi67.tinypic.com/zwi1y9.jpg") no-repeat scroll center center;
  height: 97px;
}
#menu ul li a {
    color: #fff !important;
}
#menu ul li #active {
    color: #a00 !important;
}
#menu ul li a:hover {
    color: #a00 !important;
}
a:link {
    color: #900 !important;
}
a:visited {
    color: #b00 !important;
}
a:hover {
    color: #fff !important;
}
a.nav:hover {
    color: #fff !important;
}
body {
    background-color: #111 !important;
}
.quote {
    background-color: #111;
    border: 1px solid #333;
    color: #ccc;
}
.code {
    background-color: #000 !important;
    border: 1px solid #333 !important;
    color: #fff !important;
	font-size: smaller !important;
}
.highlight {
    background-color: yellow;
    color: black;
}
.windowbg, #preview_body {
    background-color: #222 !important;
    padding: 10px;
}
.windowbg2 {
    background-color: #222 !important;
    padding-left: 10px;
}
.windowbg3 {
    background-color: #111 !important;
    padding-left: 10px;
    padding-right: 10px;
}
.calendar_today {
    background-color: #ffffff;
}
.titlebg, tr.titlebg th, tr.titlebg td, .titlebg2, tr.titlebg2 th, tr.titlebg2 td {
    background: #222 !important;
    border-bottom: 0 !important;
    border-top: 0 !important;
    color: #fff !important;
}
.catbg, tr.catbg td, .catbg3, tr.catbg3 td, .catbg2, tr.catbg2 td {
    background: #222 !important;
}
.catbg, .catbg2, .catbg3 {
    border-bottom: 0 !important;
    border-top: 1px solid #333 !important;
    border-left: 1px solid #333 !important;
    border-right: 1px solid #333 !important;
}
.catbg a:hover, .catbg2 a:hover, .catbg3 a:hover {
    color: #900;
}
.bordercolor {
    background-color: #333 !important;
}
.tborder {
  background-color: #000 !important;
  border-left: 0;
  border-right: 0;
  border-top: 1px solid #000;
  border-bottom: 1px solid #000;
  padding: 0 !important;
}
.maintab_back a:link {
    color: #900;
}
.maintab_active_back a:visited {
    color: #fff;
}
.maintab_back a:visited, .maintab_active_back a:link {
    color: #900;
}
.maintab_back a:hover, .maintab_active_back a:hover {
    color: #fff;
}
.mirrortab_back a:hover, .mirrortab_active_back a:hover {
    color: #900;
}
}
