css:
@-moz-document domain("onliner.by") {
.news-last__title {
    display: none;
}


.news-grid__part_2 {
    display: none;
}

.news-reference {
    display: none;
}

.news-header_extended .news-header__preview {
    max-height: 300px;
}

.news-popular {
    padding-top: 25px;
    padding-bottom: 25px;
    background-color: #f4f4f4;
    position: relative;
    display: none;
}


.news-grid_condensed .news-header__detail, .news-grid_condensed .news-header__title {
    width: 820px;
    margin-left: 0;
}

.news-header_extended .news-header__image {
    width: 65%;
    padding-bottom: 20%;
}

.news-header__bottom {
    padding-left: 20px;
    padding-right: 20px;
    bottom: 0;
    left: 0;
    right: 0;
    position: absolute;
    background-color: rgba(0,0,0,.5);
    width: 65%;
}


.news-header__bottom .news-header__flex {
    height: 40px;
}

.news-header_extended .news-header__image {
    width: 65%;
    padding-bottom: 50%;
}

.news-grid_condensed .news-text blockquote, .news-grid_condensed .news-text dd, .news-grid_condensed .news-text dl, .news-grid_condensed .news-text dt, .news-grid_condensed .news-text h1, .news-grid_condensed .news-text h2, .news-grid_condensed .news-text h3, .news-grid_condensed .news-text h4, .news-grid_condensed .news-text h5, .news-grid_condensed .news-text h6, .news-grid_condensed .news-text hr, .news-grid_condensed .news-text li, .news-grid_condensed .news-text ol, .news-grid_condensed .news-text p, .news-grid_condensed .news-text table, .news-grid_condensed .news-text ul {
    width: 820px!important;
    margin-left: 0!important;
}


.news-grid_condensed .news-apropos, .news-grid_condensed .news-assessment, .news-grid_condensed .news-attention, .news-grid_condensed .news-banner, .news-grid_condensed .news-comment, .news-grid_condensed .news-entry, .news-grid_condensed .news-last, .news-grid_condensed .news-opinion, .news-grid_condensed .news-overflow, .news-grid_condensed .news-reference, .news-grid_condensed .news-related, .news-grid_condensed .news-treatment, .news-grid_condensed .news-vote {
    width: 820px;
    margin-left: 0;
}


.news-text .instagram-media, .news-text .twitter-media {
    width: 820px!important;
    margin-left: 0!important;
}

}
