css:
@-moz-document domain(thewingedhussars.com) {

    
/*@import url(https://fonts.googleapis.com/css?family=Lato:400,400italic,700,700italic,300italic,300);*/
/*@import url(https://fonts.googleapis.com/css?family=Orbitron:400,500,700,900);*/

/* latin */
@font-face {
  font-family: 'Orbitron';
  font-style: normal;
  font-weight: 400;
  src: local('Orbitron-Light'), local('Orbitron-Regular'), url(https://fonts.gstatic.com/s/orbitron/v7/HmnHiRzvcnQr8CjBje6GQvesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
/* latin */
@font-face {
  font-family: 'Orbitron';
  font-style: normal;
  font-weight: 500;
  src: local('Orbitron-Medium'), url(https://fonts.gstatic.com/s/orbitron/v7/VK2oK8L4CJesxwY5dF3zxY4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
/* latin */
@font-face {
  font-family: 'Orbitron';
  font-style: normal;
  font-weight: 700;
  src: local('Orbitron-Bold'), url(https://fonts.gstatic.com/s/orbitron/v7/Y82YH_MJJWnsH2yUA5AuYY4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
/* latin */
@font-face {
  font-family: 'Orbitron';
  font-style: normal;
  font-weight: 900;
  src: local('Orbitron-Black'), url(https://fonts.gstatic.com/s/orbitron/v7/FyvTORElv6iWeP2Jf9yG3Y4P5ICox8Kq3LLUNMylGO4.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

a:link, a:visited {
    color: #E1090A;
    text-decoration: none;
}


.wrapper > a > img {
	display:none;   
}
#logo {
    background-color: #000;
    padding: 0px 0px;
    border-bottom: 1px solid #E1090A;
}
#logo .wrapper {
 	background-image: url("http://i.imgur.com/O8OPaKM.jpg");
	background-position: top center;
    background-repeat: no-repeat;
    min-height:297px;
    font-family: 'Lato', sans-serif;
    font-family: 'Orbitron', sans-serif;
    
}
#logo .wrapper a {
    font-weight:300;
    color:#fff;
}
#header ul.menu li a {
    background-image: none;
}
#logo ul.top_links {
    padding: 20px 0px;
}
#panel .upper {
    background-image: none;
    background-color: #AC1818;
    border-color: #AC1818;
    font-family: 'Orbitron', sans-serif;
}
.welcome strong {
 	padding: 0px 30px;
    font-weight: 300;
}
.welcome a {
	font-weight: 600;
}
.welcome a.logout {
    padding: 0px 30px;   
    background-image: none !important;
}
button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons > a, a.button {
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px; 
    border-radius: 0px; 
}
#content {
    background: #000;
    width: auto !important;
    padding: 20px 10px;
    overflow: hidden;
}
#search input.button, #search input.textbox {
    border-color: #E1090A;
}
#search input.button {
    background-image: none !important;
    color: #666666 !important;
    text-transform:uppercase;
    border-color: #666;
    
    /* Firefox v3.6+ */
    background-image:-moz-linear-gradient(50% 0% -90deg,rgb(255,255,255) 0%,rgb(204,204,204) 98%) !important; 
    /* safari v4.0+ and by Chrome v3.0+ */
    background-image:-webkit-gradient(linear,50% 0%,50% 100%,color-stop(0, rgb(255,255,255)),color-stop(0.98, rgb(204,204,204))) !important;
    /* Chrome v10.0+ and by safari nightly build*/
    background-image:-webkit-linear-gradient(-90deg,rgb(255,255,255) 0%,rgb(204,204,204) 98%) !important;
    /* Opera v11.10+ */
    background-image:-o-linear-gradient(-90deg,rgb(255,255,255) 0%,rgb(204,204,204) 98%) !important;
    /* IE v10+ */
    background-image:-ms-linear-gradient(-90deg,rgb(255,255,255) 0%,rgb(204,204,204) 98%) !important;
    background-image:linear-gradient(180deg,rgb(255,255,255) 0%,rgb(204,204,204) 98%) !important;
	min-height:24px;
 
    /* Firefox v3.5+ */
    -moz-box-shadow:0px 0px 3px rgba(0,0,0,0.65) !important;
    /* Safari v3.0+ and by Chrome v0.2+ */
    -webkit-box-shadow:0px 0px 3px rgba(0,0,0,0.65) !important;
    /* Firefox v4.0+ , Safari v5.1+ , Chrome v10.0+, IE v10+ and by Opera v10.5+ */
    box-shadow:0px 0px 3px rgba(0,0,0,0.65) !important;
 
}
.pm_alert {
    background: #AC1818;
 	 
    color:#fff;
    border: 1px solid #E1090A;
    text-align: center;
    padding: 15px 20px;
    margin-bottom: 15px;
    font-size: 13px;
}
.pm_alert strong {
 	 font-weight: 300;   
}
.pm_alert a {
    color:#fff;
    font-weight: 600; 
    font-size: 14px;
}

.navigation .active {
    color: #fff;
    font-size: small;
    font-weight: 300;
    font-family: 'Orbitron', sans-serif;
    margin-left: -17px;
}
.navigation a {
    font-family: 'Orbitron', sans-serif;
    font-size:14px;
    padding-right:20px;
}
#content .wrapper span.smalltext {
    font-size: 12px;
    color: #fff;
    font-family: 'Orbitron', sans-serif;
    margin-bottom: 10px;
    display: inline-block;
}
#content .wrapper .inline_row span.smalltext,
#content .wrapper .tborder span.smalltext{ 
    color: #333;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
}
#content .wrapper div.smalltext {
    font-size: 12px;
    color: #333;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
  
}
.smalltext a {
    font-family: 'Orbitron', sans-serif;
}


.tborder {
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    border-radius: 0px;
    background: #FBFBFB;
    border: 1px solid #ccc;
    padding: 1px;
}
 
.thead {
    -moz-border-radius-topleft: 0px;
    -moz-border-radius-topright: 0px;
    -webkit-border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    
    background: #AC1818 none top left repeat-x;
    color: #ffffff;
    border-bottom: 1px solid #E1090A;
    padding: 10px 25px;
    font-family: 'Orbitron', sans-serif;
}
.thread_legend dd {
    padding-bottom: 4px;
    margin-right: 15px;
    color: #ccc;
    font-size: 13px;
}



#footer .upper {
    background: #AC1818;
    color:#fff;
    border-top: 1px solid #E1090A;
    border-bottom: 1px solid #E1090A;
    padding: 10px 25px;
    font-size: 13px;
    overflow: hidden;
    font-family: 'Orbitron', sans-serif;
}
#footer .upper a {
    color:#fff;
    padding-right:20px;
}
#footer .lower {
    background-color: #000;
    color: #ccc;
     
    padding: 15px 6px 12px 6px;
    overflow: hidden;
    font-size: 13px;
}
#footer .lower a, #footer .lower a:hover {
	/*color: #AC1818 !important;*/
    font-weight: 300 !important;
}
#current_time, #current_time strong {
    font-weight: 300 !important;
    font-family: 'Orbitron', sans-serif;
}
    
    



}
