css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.overleaf.com") {
  
  /* Top Overleaf symbol */
  img[alt="Overleaf"] {
    filter: invert(20%);
  }
  
  /*.caret {
    border-right: 4px solid transparent;
    border-left: 4px solid transparent
  }*/
  
  /**** Login Page ****/
  /* Email, Password */
  form.form-inline .form-container input[type="password"], form.form-inline .form-container input[type="email"] {
    color: #ddd;
  }
  
  /**** Home Page ****/
  
  /* Page Bkgd */
  .main-content {
    background-color: #181818;
  }
  
  body {
    background-color: #181818;
  }
  
  /* 'My Projects' */
  .section_header > h3 {
    color: #ddd;
  }
  
  /* Search */
  .form-control {
    background-color: #202020;
    color: #ddd;
  }
  
  /* Boxes */
  .overbox {
    background-color: #202020;
    color: #ddd;
  }
  
  /* Project blocks */
  .doc-container {
    background-color: #202020;
  }
  
  .doc-title {
    color: #ddd;
  }
  
  /**** Document Page ****/
  
  /* Top Control Panel with 'Edit', 'Find', etc. */
  #wl_editor_menu {
    background-color: #343434;
  }
  
  /* Input field in 'Versions', 'Share',etc. */
  .form-control[readonly] {
    background-color: #202020;
  }
  
  /* 'Share' headings */
  #modal-share .modal-body h4, #modal-share .modal-body h4 a, #modal-share .modal-body h4 a:hover {
    color: #d1d1d1;
  }
  
  /* Username dropdown */
  .topbar .nav ul.dropdown-menu {
    background-color: #282828;
  }
  
  /*** Source View ***/
  
  /* Find field (on Ctrl-F) */
  .CodeMirror-dialog-top {
    background-color: #343434;
    color: #aaaaaa;
  }
  
  /* Source Bkgd */
  .CodeMirror.cm-s-wl-default {
    background-color: #282828;
  }
  
  /* Source Line No. */
  .CodeMirror-linenumber {
    background-color: #282828;
  }
  
  .CodeMirror-gutters {
    background-color: #282828;
  }
  
  /* Source default color */
  span {
    color: #ddd;
  }
  
/*   // This applies to selected text too !
  // Hence, do not use
  .CodeMirror pre {
    background-color: #282828;
  } */
  
  /* Selected Text */
  .CodeMirror-selected {
    background-color: #414141 !important;
  }
  
  /* Selected Braces */
  .cm-bracket.CodeMirror-matchingbracket {
    color: #d2d2d2 !important;
  }
  
  span.CodeMirror-selectedtext, span.cm-cm-overlay.cm-spell-error.CodeMirror-selectedtext { /* For error and non-error underlined */
    background-color: #414141;
  }
  
  /* Blinking cursor */
  .CodeMirror-cursor {
    border-left-color: #d2d2d2;
  }
  
  .switch-light {
    background-color: #282828;
  }
  
  /* LaTeX error */
  .CodeMirror-marker-error-background {
    background-color: #5e2626; /* derived from original #f2dede */
  }
  
  div.marker_float {
    background-color: #202020;
  }
  
  #wl_preview_status_popover_container.preview_status_error .popover, #wl_preview_status_popover_container.preview_status_timeout .popover {
    background-color: #202020;
  }
  
  /* File History */
  #modal-file-history .file-history-diff pre {
    background-color: #202020;
  }
  
  #modal-file-history ins {
    background-color: #003300;
    color: #d1d1d1;
  }
  
  #modal-file-history del {
    background-color: #440000;
    color: #d1d1d1;
  }
  
  /* Compare Versions */
  
  /*
  #wl_editor_content .CodeMirror-merge-r-chunk { / * Compared code * /
    background-color: #383838;
  }
  
  #wl_editor_content .CodeMirror-merge-r-chunk-start {
    border-top: 1px solid #d1d1d1;
  }
  
  #wl_editor_content .CodeMirror-merge-r-chunk-end {
    border-bottom: 1px solid #d1d1d1;
  } */
  
  .CodeMirror-merge-gap { /* Middle scroll separator */
    background-color: #282828;
  }
  
  /* 'Edit', 'Find', Right-click dropdown */
  .dropdown-menu:not(.dropdown-menu-bare) {
    background-color: #282828;
  }
  
  /* 'More' dropdown */
  .popover.bottom {
    background-color: #282828;
  }  
  .popover-content > li > a {
    background-color: #282828;
  }
  
  /* Autocomplete Pop-up */
  .CodeMirror-hint {
    background-color: #343434;
    color: #d1d1d1;
  }
  
  .CodeMirror-hint.CodeMirror-hint-active {
    background-color: #4f9c45;
    color: #d1d1d1;
  }
  
  /*** Rich Text View ***/
  
  /* Source Bkgd */
  .CodeMirror-scroll {
    background-color: #282828;
  }
  
  /* Line No.s */
  .CodeMirror-linenumbers {
    background-color: #282828;
  }
  
  /* Title */
  .preamble > h1 {
    color: #d1d1d1;
  }
  
  /*** Project Tab ***/
  #wl_pinned {
    background-color: #282828;
    color: #d1d1d1;
  }
  
  /* Word Count */
  .modal-dialog > .modal-content {
    background-color: #282828;
    color: #d1d1d1;
  }
  
  .texcount-content.message > pre {
    background-color: #282828;
    color: #d1d1d1;
  }
  
  /* Right-click Pop-up */
  .padded-item {
    background-color: #282828;
  }
  
  .dropdown-menu > li > a {
    color: #d1d1d1;
  }
  
  /* File dropdown - 'Rename', 'Delete', 'Add...', 'Upload from...' */
  .dropdown-menu.dropdown-add-files {
    background-color: #343434;
  }
  
  /* Upload from Computer Pop-up overlay */
  .wl-fileloader-dropzone {
    background-color: #282828;
  }
  
  .wl-fileloader-localDragHelp {
    text-shadow: none;
  }
  
  /* Download zip dropdown */
  .download-zip, .download-zip-with-outputs, .download-zip-with-intermediates {
    background-color: #343434;
  }
  
  /* 'Save to Dropbox' */
  img {
    filter: invert(80%);
  }
  
  /*** Right Real-time PDF ***/
  img.preview_page {
    filter: invert(80%);
  }
  
  /* Top Panel with 'Preview',etc. */
  #wl_preview_menu {
    background-color: #343434;
  }
  
  /**** Read-only Page ****/
  
  /*** Help dialog ***/
  
  /* Search input */
  /* input#gsc-i-id1.gsc-input {
    background-color: #343434;
  } */
  
  /* Headers */
  .modal-body > h4, .modal-body > div > div > h4 {
    color: #d1d1d1;
  }
  
  /**** Profile Page ****/
  body {
    color: #d1d1d1;
  }
  
  /* Header */
  #profile h3 {
    color: #d1d1d1;
  }
  
  /* Top inputs */
  label.select-wl select {
    background-color: #202020;
    color: #d1d1d1;
  }
  
  /* 'Email' */
  #university-selection #email-autocomplete {
    background-color: #202020;
  }
  
  /**** Templates ****/
  
  /* Page No.s */
  .pagination > li > a {
    background-color: #202020;
  }
  
  /* 'Open as Template' -> View Source' Pop-up */
  pre.cm-s-default {
    background-color: #282828;
    color: #d1d1d1;
  }
  
  /* Search
  input#gsc-i-id1[class=" gsc-input "]  {
    background-color: #343434;
  } */
  
  /**** Referral Sign Up ****/
  #sign_up .signin_box .box .form input[type="text"], #sign_up .signin_box .box .form input[type="email"], #sign_up .signin_box .box .form input[type="password"] {
    color: #d1d1d1;
  }
  
}
