css:
@-moz-document domain("dreamincode.net") {
body, div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, form, fieldset, input, textarea, p, blockquote, th, td {
    background-color: #3d3d3d;
    color: gray;
    margin: 0;
    padding: 0;
}
#header #navigation-login-register {
    height: 45px;
    padding-left: 10px;
    background: #3d3d3d;
}
#header .navigation .last {
    background: #3d3d3d;
    border: 0 none;
}
#login-register, #login-register a {
    color: #c46c000;
    text-decoration: none;
}
p.edit {
    background-color: #3d3d3d;
    color: #515151;
    font-size: 0.9em;
    line-height: 120%;
    padding: 10px;
    margin-top: 10px;
}
.post_block {
    background: #3d3d3d;
    border-bottom: 4px solid #3d3d3d!important;
}
ul.post_controls {
    background-color: #3d3d3d;
    padding-left: 2px;
}
a {
    color: #c46c00;
    cursor: pointer;
}
#header .navigation a {
    border-right: 1px solid #5f665f;
    padding: 0 0.6em;
    color: #c46c00;
    text-decoration: none !important;
}
.post_body a {
    font-weight: bolder;
    color: #c46c000!important;
}
a {
    color: #c46c00;
    cursor: pointer;
}
.column1 a, column2 a {
    color: gray;
    text-decoration: none !important;
}
.column1 .navigation li {
    list-style-type: none;
    border-bottom: 1px dotted #b2b2b2;
    padding-left: 18px;
    background: #3d3d3d;
    line-height: 22px;
    color: gray;
    font-size: 11px;
}
.column1 .navigation h2 {
    border-bottom: 1px dotted #b2b2b2;
    padding-left: 0.5em;
    background: darkgray;
    color: #c46c00;
    font-weight: bold;
    line-height: 29px;
    font-size: 12px;
}
.column1 a, column2 a {
    color: #c46c00;
    text-decoration: none !important;
}
.column2 li a, .column1 .lists li a, #related li a {
    color: #c46c00;
    font-size: 12px;
}
.author_info {
    text-align: left;
    font-size: 0.9em;
    border-right: 1px solid #3d3d3d;
    margin: 15px -5px 10px 0px;
    width: 150px;
    float: left;
    clear: both;
}
p.citation {
    background: #2e4d3b;
    color: #2b3730;
    font-size: 0.9em;
    font-weight: bold;
    border-width: 1px 1px 0 3px;
    border-style: solid;
    border-color: #2e4d3b;
    padding: 5px 8px;
}
.rep_bar {
    background: #3d3d3d;
    font-size: 1.1em!important;
    -webkit-border-top-left-radius: 5px;
    padding: 0px;
    right: 10px;
    bottom: 45px;
    position: absolute;
    width: 280px;
}
.post_block h3 {
    background: #3d3d3d;
}
.post_body a {
    font-weight: bolder;
    color: #c46c00;
}
element.style {
    color: #c46c00;
}
body .maintitle a {
    color: #c46c00;
}
}
