css:
@-moz-document url-prefix("http://www.tppcrpg.net/") {
/* General Tags */
#tppcESbody::-webkit-scrollbar { width: 5px; height: 5px; }
#tppcESbody::-webkit-scrollbar-track { background: transparent; }
#tppcESbody::-webkit-scrollbar-track-piece { background: transparent;  }
#tppcESbody::-webkit-scrollbar-thumb { background: #333; }
body { overflow-x: hidden; }
a { color: #D48A00 !important; text-decoration: none; }
a:hover { color: #996E1D !important; }
hr { border-color: #D48A00; }
h2 { color: #D48A00; }
h3 { color: #996E1D; }
input:focus, select:focus, textarea:focus, button:focus { outline: none; }

/* Battles */
.headerBar { background: #D48A00; color: #000; font-weight: bold; border: none; }
.PokeImage { background-color: #D48A00; margin-top: -1px; }
.innerContent { background: #222; }
.innerContent fieldset { border-color: #D48A00; }
.innerContent fieldset .hpBar { background: #D48A00; }
legend, .item { color: #D48A00; }
.rosterBox .headerBar { background: #222; }
.rosterContent .row0, .rosterContent .row0 a { background-color: #D48A00; color: #000 !important; }
.rosterContent .row1 { background-color: #222; color: #D48A00 !important; }
.rosterContent .hpBar .hpVal { background: #996E1D; }

/* Pokedex Entries */
.dex td table th { border: none; }
.dex td table td { background: #222; border-color: #D48A00; }
.dex .iBox div { background-color: #D48A00; border: none; }

/* Footer */
#footer { background-color: #222; color: #D48A00; padding: 2px; }

/* Change Banner (Orange #D48A00) */
#header { background-image: url('http://i.imgur.com/iF9SGdn.png'); background-color: #111; }
#header ul { background: #222; border-color: #D48A00; text-transform: capitalize; } 
#header ul li a { color: #D48A00 !important; }

/* Hide Ad Space */
#tAoDp { display: none; }

/* Sidebars */
#right { position: relative; z-index: 10 !important; }
#left ul { border: 1px solid #D48A00; border-left: none; border-top: none; }
#right ul { border: 1px solid #D48A00; border-right: none; border-top: none; }
li.h { background-color: #D48A00; color: #000; }
li.r1, li.r0 { border-top: none; border-bottom: none; }
li.r1 a, li.r0 a { border-left: 1px solid #D48A00; }
li.r0 { background-color: #222; }
li.r1 { background-color: #111; }
li.r1 a { background-color: #222; }
li.r0 a { background-color: #111; }
li.pb, li.rs { background-color: #222; }
li.rs div img { background-color: #333; border: 1px solid #D48A00; }
#switchSidebar { background-color: #222; color: #996E1D; border-bottom: 1px solid #D48A00; }
#switchSidebar div { background: #D48A00; }
li.hpSide { background: #996E1D; }
li.hpSide img { content: url('http://i.imgur.com/LjO7CIi.png'); }

/* Body */
body { background-color: #111; color: #996E1D; }
#body { border-top: 1px solid #D48A00 !important; }
#inner .r0 { background-color: #222; }
#inner .r1 { background-color: #333; }
#inner th, .notice, #cBox { background-color: #D48A00; color: #000; }
#cBox a { color: #555 !important; }
#cBox a:hover { color: #000 !important; }
.error, .success { background-color: #D48A00; border: 2px solid #999; color: #000; }
.profileBox, #profile li div.i { background-color: #222; border: 1px solid #D48A00; }
.ld { border: 1px solid #D48A00; }
.w50 div { background-color: #D48A00; color: #000; }
#towers td div.i { background: #333; border: 1px solid #D48A00; }
#configure li { background-color: #D48A00; color: #000; border: none; }
#configure li div.h { background: #222; border: 1px solid #D48A00; }
#configure li input { background: rgba(153,110,29,0.5); }
.maintenance { background-color: #D48A00; color: #000; border-color: #333; }
.maintenance div { background-color: #333; }
.tradeBox { background-color: #D48A00; color: #000; }
#leftR h3, #rightR h3 { background-color: #D48A00; color: #000; border-color: #333; }
#leftR ul li div, #rightR ul li div { background-color: #333; border-color: #D48A00; }
#leftR ul li, #rightR ul li { border-color: #D48A00; }
#right .date .date_time { background-color: #996E1D; }
#profile li div.h { color: #D48A00; }

/* Buy Page */
#buy td { background: #222; border: 1px solid #D48A00; }
#buy td div.np { background-color: #D48A00; color: #000; border-bottom: none; }
}

@-moz-document url("http://www.tppcrpg.net/rarity.html") {
    table { border-color: #D48A00; }
    th { background-color: #D48A00; color: #000; }
    .row0 { background-color: #222; color: #D48A00; }
    .row1 { background-color: #333; color: #D48A00; }
}
