css:
@-moz-document regexp(".*\\.slack.com\\/customize\\/emoji") {
div#page_contents {
    width: auto;
    margin-left: 200px;
    padding: 0;
}

table#custom_emoji tr:nth-child(1) {
    display: none;
}

table#custom_emoji, table#custom_emoji tr, table#custom_emoji td {
    display: inline-block;
    height: auto;
}

table#custom_emoji tr {
    position: relative;
    width: 110px;
    height: 150px;
    margin: 5px;
    border: 1px solid #DDD;
}
table#custom_emoji tr:hover {
    background-color: #EEE;
}

table#custom_emoji span.emoji-wrapper {
/*     the image itself */
    position: absolute;
    top: 5px;
    left: 5px;
    width: 100px;
    height: 100px;
}

table#custom_emoji tr td:nth-child(2) {
/*     emoji code/name */
    position: absolute;
    top: 110px;
    left: 0;
    right: 0;
    width: 100%;
    text-align: center;
    height: 150px;
    padding: 0;
    font-size: 12px;
    line-height:0.7;
}
table#custom_emoji tr td:nth-child(3) {
/*     Type of emoji (emoji or alias) */
    display: none;
}
table#custom_emoji tr td:nth-child(4) {
/*     Author column */
    position: absolute;
    bottom: -1px;
    left: 1px;
    right: 1px;
    width: 100%;
    height: auto;
    text-align: center;
    padding: 0;
    font-size: 12px;
    line-height: 1;
}
table#custom_emoji tr td:nth-child(5) {
    position: absolute;
    top: 0;
    left: auto;
    right: 0;
    width: 100%;
    height: auto;
    text-align: center;
    padding: 0;
    font-size: 12px;
    line-height: 1;
}
}
