css:
@-moz-document domain(thewingedhussars.com) {


body {
	background: #0f0f0f;
	color: #fff;
	margin: 0;
    
}     
    form #message {
    width: 200px;
}
    
.wrapper > a > img {
	display:none;   
}
#logo {
    background-color: #0f0f0f;
    padding: 0px 0px;
}
#logo .wrapper {
 	background-image: url("http://i.imgur.com/Zu5l8Wj.jpg");
	background-position: top center;
    background-repeat: no-repeat;
    min-height:480px;
    border-width: 0px;
    border-color: #000;
    padding: 0px 0px;
    margin-top: 0px;
}
#logo .wrapper a {
    font-weight:bold;
    color:#fff;
}

#menu {
list-style: none;
background: #4062b6;
height: 50px;
padding: 0px 0px;
}
    
#menu li {
float: left;
display: block;
position: relative;
z-index: 500;
margin: 0 1px;
line-height: 33px;
}
    
/* this is the parent menu */
#menu li a {
display: block;
padding: 8px 22px 0 22px;
font-weight: 700;
height: 42px;
text-decoration: none;
color: #4062b6;
text-align: center;
border-right: 1px solid #0f0f0f;
}
    
#menu ul li {
width: 170px;
float: left;
border-top: 1px solid #253a6f;
margin: 0px -1px;
background: #102a68;

}

#header ul.menu li {
display: inline;
padding: 13px;
border-right: 1px solid #2B2B2B;
}
    
    #header ul.menu li a {
    background-image: none;
}

#logo ul.top_links {
    padding: 0px 0px;
}

#panel .upper {
    background-image: none;
    background-color: #0f0f0f;
    border-color: #0f0f0f;
	margin-top: 0px;
    margin-bottom: 0px;
}

button, input.button, input.textbox, input.invalid_field, input.valid_field, select, textarea, .editor_control_bar, blockquote, .codeblock, fieldset, .pm_alert, .red_alert, .popup_menu, .postbit_buttons > a, a.button {
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px; 
    border-radius: 0px;
    padding: 2px;
    outline: 0;
}
#content {
    background: #0f0f0f;
    width: auto !important;
    overflow: hidden;
    padding: 0px 0px;
}
    
#search input.button, #search input.textbox {
    border-color: #617fd5;
    color: #fff;
}
#search input.button {
    background-image: none !important;
    color: #fff !important;
    text-transform:uppercase;
    border-color: #617fd5;
   color: #fff

}
textarea {
	background: #fff;
    background-color: #fff;
  	color: #000;
	border: 1px solid #3A3A3A;
	padding: 2px;
	line-height: 1.4;
	outline: 0;
	font-family: Tahoma, Verdana, Arial, Sans-Serif;
	font-size: 13px;
}

.tborder {
    border-radius: 0px;
    background: #26292c;
    border: 1px solid #4062b6;
    padding: 1px;
}
 
.thead {
    background: #4062b6 none top left repeat-x;
    color: #fff;
    border-top: 0px solid #102a68;
    border-bottom: 0px solid #102a68;
    padding: 10px 25px;
    font-family: 'Tahoma', sans-serif;
}
    
#footer .upper {
    background: #102a68;
    color:#000;
    border-top: 1px solid #E1090A;
    border-bottom: 1px solid #E1090A;
    padding: 10px 25px;
    font-size: 13px;
    overflow: hidden;
    font-family: 'Tahoma', sans-serif;
}
#footer .upper a {
    color:#fff;
    padding-right:20px;
}
#footer .lower {
    background-color: #000;
    color: #ccc;
     
    padding: 15px 6px 12px 6px;
    overflow: hidden;
    font-size: 13px;
}
#footer .lower a, #footer .lower a:hover {
	/*color: #AC1818 !important;*/
    font-weight: 300 !important;
}
#current_time, #current_time strong {
    font-weight: 300 !important;
    font-family: 'Tahoma', sans-serif;
}


}
