css:
@-moz-document domain("spacialgaze.psim.us"), domain("play.pokemonshowdown.com") {
body {
    background: url("http://img01.deviantart.net/c505/i/2013/302/3/6/sylveon_desktop_wallpaper_1080p_by_raludi-d6sahzj.png") !important;
    background-color: #344B6C;
    color: #FFF;
}
.highlighted em {
    background: #95daf8;
    color: #6e82b7;
}
.highlighted,
.message-announce {
    background: none;
}
.message-announce {
    background: #f492a5;
    color: #f7e8d8;
    padding: 1px 4px 2px;
}
.chat-log {
    background: #f7e8d8;
    background-size: cover;
    font-weight: bold;
    color: #6e82b7;
    border-width: 0;
}
.ps-room.ps-room-light {
    background: #f492a5;
    color: #6e82b7;
}
.challenge {
    background: #f492a5;
    border: 1px solid #2980b9;
    padding: 1px 5px;
    color: #f7e8d8;
}
.pm-log em {
    color: #6e82b7;
}
.userlist li button {
    font: 12px helvetica, arial, sans-serif;
    text-shadow: 1px 1px #000;
}
.userlist li button:hover {
    background: #f9cbd0;
}
.userlist {
    background: #f492a5;
    border-right: 1px solid #186384;
}
.menugroup {
    background: #f492a5;
}
.menugroup .button,
.pad button {
    background: -webkit-linear-gradient(#6e82b7, #6e82b7)!important;
    background: -o-linear-gradient(#95daf8, #95daf8)!important;
    background: -moz-linear-gradient(#6e82b7, #6e82b7)!important;
    background: linear-gradient(#95daf8, #95daf8)!important;
    border: 1px solid #111133;
    color: 	#6e82b7 !important;
}
.menugroup .button:hover,
.pad button:hover {
    background: -webkit-linear-gradient(#6e82b7, #6e82b7)!important;
    background: -o-linear-gradient(#6e82b7, #6e82b7)!important;
    background: -moz-linear-gradient(#6e82b7, #6e82b7)!important;
    background: linear-gradient(#6e82b7, #6e82b7)!important;
    border: 1px solid #222244;
    color: #95daf8 !important;
}
.menugroup .button:active,
.pad button:active {
    background: -webkit-linear-gradient(#FEEFA5, #FEEFA5);
    background: -o-linear-gradient(#FEEFA5, #FEEFA5);
    background: -moz-linear-gradient(#FEEFA5, #FEEFA5);
    background: linear-gradient(#FEEFA5, #FEEFA5);
    border: 1px solid #222244;
}
.roomlist {
    font-family: helvetica, arial, sans-serif;
    color: #f9cbd0;
}
.roomlist a.ilink {
    border: 1px solid #000;
    color: #186384;
    background: #f7e8d8;
}
.roomlist a.ilink:hover {
    border: 1px solid #000;
    color: #1D90FF;
    background: #f9cbd0;
}
.roomlist a.ilink small {
    color: #f492a5;
}
.ladder table {
    background-color: #f7e8d8;
    color: #6e82b7
}
.mainmenufooter a {
    text-decoration: none;
    color: #FFF;
    text-shadow: 1px 0px #000;
    font-family: helvetica, arial, sans-serif;
}
.mainmenufooter a:hover {
    color: #1D90FF;
    text-decoration: none;
}
#header {
    background: #f492a5;
}
.header .logo {
    opacity: 1;
    background: #f492a5;
}
.userbar .username {
    font: 15px helvetica, arial, sans-serif;
    font-weight: bold;
}
.userbar .username:hover {
    border-bottom: 1px solid #f492a5;
}
a {
    color: #f492a5;
    text-decoration: none;
    margin-left: 3px;
    margin-right: 3px;
}
a:hover {
    color: #6e82b7;
    text-decoration: underline;
}
.ps-popup {
    background: #6e82b7;
    font: 14px helvetica, arial, sans-serif;
    color: #FFF;
    border-color: #212121;
}
.userdetails .rooms {
    color: #f7e8d8;
}
.popupmenu button {
    box-shadow: none;
    color: #95daf8;
}
.popupmenu button:hover {
    box-shadow: none;
    color: #FFF;
    background: rgba(78, 78, 78, 1);
}
.popupmenu button.sel:hover {
    padding: 0px 3px;
    border: 1px solid #AAA;
    background: rgba(65, 65, 65, 1);
}
.ps-overlay {
    background: rgba(0, 0, 0, 0.3);
}
.pm-window h3 {
    border: 1px solid #212121;
    color: #FFF;
    background: rgba(0, 0, 0, 0.7);
    font-family: helvetica, arial, sans-serif;
}
.pm-window h3:hover,
.pm-window.focused h3,
.pm-window.focused h3:hover {
    border: 1px solid #212121;
    color: #1D90FF;
    background: rgba(0, 0, 0, 0.8);
    font-family: helvetica, arial, sans-serif;
}
.message-pm {
    color: #000;
    font-style: italic;
    background: #95daf8;
}
.revealed {
    color: #186384;
}
a.ilink {  
    text-decoration: none;
    color: 	#f7e8d8;
}
a.ilink:hover {
    color: #f7e8d8;
}
.maintabbarbottom {
    background: #95daf8;
    border: 1px solid #000;
    box-shadow: none;
}
.tabbar a.button.cur,
.tabbar a.button.cur:hover {
    color: #6e82b7;
    background: #95daf8;
    border: 1px solid #000;
    text-shadow: none;
}
.tabbar a.button {
    color: #95daf8;
    background: #6e82b7;
    border: 1px solid #000;
    text-shadow: none;
}
.tabbar a.button:hover {
    color: #95daf8;
    background: #6e82b7;
    border: 1px solid #000;
    text-shadow: none;
}
button {
    border: 1px solid #212121;
    background: none repeat scroll 0% 0% #6e82b7;
    color: #f7e8d8;
}
button:hover {
    color: #6e82b7;
    background: #95daf8;
}
.tournament-bracket {
    font-size: 8pt;
    background: url("http://i.imgur.com/Q8vHT0Y.png");
    opacity: .8;
}
.tournament-title {
    background: #f7e8d8;
    color: #6e82b7;
}
.tournament-status,
.tournament-toggle {
    color: 	#f7e8d8;
    background: #6e82b7;
}
.tournament-tools {
    padding: 10px;
    border-top: 1px solid #AAA;
    background: url("http://i.imgur.com/Q8vHT0Y.png");
}
.regular-button {
    box-shadow: 0px 1px 0px 0px #FFFFFF inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
    border: 1px solid #A8A8A8;
    background: #EEEEEE;
    color: #000;
}
.regular-button:hover {
    box-shadow: 0px 1px 0px 0px #FFFFFF inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
    border: 1px solid #A8A8A8;
    background: #DBDBDB;
    color: #000;
}
.blue-button {
    border: 1px solid #474747;
    color: #474747;
    background: #6F9BB5;
    box-shadow: 0px 1px 0px 0px #92B0C1 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
}
.blue-button:hover {
    border: 1px solid #474747;
    color: #474747;
    background: #7CACC7;
    box-shadow: 0px 1px 0px 0px #92B0C1 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
}
.LV-button {
    border: 1px solid #212121;
    color: #FFF;
    background: rgba(0, 0, 0, 0.8);
    box-shadow: 0px 1px 0px 0px #414141 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
}
.LV-button:hover {
    border: 1px solid #212121;
    color: #1D90FF;
    background: rgba(0, 0, 0, 0.8);
    box-shadow: 0px 1px 0px 0px #414141 inset, 0px 2px 3px rgba(0, 0, 0, 0.4);
}
.closebutton,
.minimizebutton {
    color: #000;
}
.minimizebutton:hover {
    color: #000;
    text-decoration: none;
}
.closebutton:hover {
    color: #000;
    text-decoration: none;
}
.setchart-nickname {
    background: #f7e8d8;
}
.setmenu button {
    background: #f7e8d8;
    color: 	#6e82b7;
}
.setmenu button:hover {
    background: #6e82b7;
    color: #f7e8d8;
}
.setchart {
    background: #f7e8d8;
}
.utilichart .movenamecol {
    color: 	#000;
    font: 12px helvetica, arial, sans-serif;
}
.teambuilder-results h3,
.teambuilder-results .utilichart h3 {
    background: #f7e8d8;
    color: #6e82b7;
    box-shadow: none;
}
.teambuilder-results .result {
    background: #f492a5;
}
.teambuilder-results .result a:hover {
    background: #f7e8d8;
}
.teambar button {
    background: #f492a5;
    color: #6e82b7;
}
.teambar button:hover {
    background: #f9cbd0;
    color: black;
}
.teambar button:disabled,
.teambar button:disabled:hover,
.teambar button:disabled:active {
    background: #f9cbd0;
    color: #f9cbd0;
}
.utilichart .namecol {
    color: #FFF;
}
.switchmenu button,
.movemenu button {
    border: 3px solid #222222;
    color: #000;
}
.switchmenu button:hover,
.movemenu button:hover {
    border: 3px solid #222222;
    color: #222222;
}
.switchselect button:hover {
    color: #458;
}
.moveselect button:hover {
    color: #842;
}
.ps-room .battle-log {
    background: #f492a5;
    color: 	#000;
}
.battle-log h2 {
    background: #95daf8;
    color: #6e82b7;
    border: 1px solid #212121;
}
.battle-log .chat strong {
    color: 	#000;
}
.dark .battle-log .chat strong {
    color: 	#000;
}
.battle-log-add {
    background: #f492a5;
}
.ps-room.ps-room-opaque {
	background: #f7e8d8;
}
.ps-room .battle-controls {
    background: #f7e8d8;
}
.dark .ps-room .battle-controls {
    background: #444;
}
.leftbar {
    background: #f492a5;
    color: #FFF;
}
.rightbar {
    background: #f492a5;
    color: #FFF;
}
.effect-volume em,
.music-volume em {
    display: block;
    margin-top: -6px;
    font-size: 9pt;
    padding-left: 5px;
    color: #f7e8d8;
}
.infobox,
.ladder,
.pm-window {
    position: relative;
    border: 1px solid #181818;
    border-radius: 2px;
    box-shadow: inset 0px 0px 0px 1px rgba(0, 0, 0, .2);
    margin-top: 5px;
    color: #000000;
    background-color: #95daf8;
}
}
