css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newz.dk"), domain("railgun.dk") {

#wrapper {
max-width: 100% !important;}

#center {
width: 100% !important;}

.site_navigation {
margin: auto !important;
left: 40% !important;} 

/* Fixer toppen + reklamer */
.ad_930x180,
.comment_form_info .ad_300x250,
.ad_300x250,
#nmTopBar,
#msbanner,
.top-interstitial,
#streamer
{display:none!important}

/* Fixer venstre siden undtagen poll */
#left_placeholder,
.breaking_news,
div.search,
/* div.login = Login boks */
div.login,
a.news_submit:link,
#sweeplist,
.indexcontainer,
.newsletter,
.uservoicediv,
.poll_extra .poll_new,
.interstitial,
.bookmarks,
.submit-news,
.create-abonnement,
iframe[id=mdgxWidgetiFrame],
div.left-navigation>div[style*="lightgrey"],
div.left-navigation>br
{display:none!important}
  
  #main-container {
    background: none !important;
  }

.secondary_column {
padding:0 0 0px !important;
}

/* Fixer Facebook + Twitter */
.social
{display:none!important}

/* Fixer bunden */
.partnersites,
.partnersitesContainer,
.copyright,
.shop-footer
{display:none!important}

body {
background: #FFF !important;
padding:0 0 0px !important;}

#wrapper {
padding:0 0 30px !important;}

#container {
background: none !important;}

/* Fixer lav en bruger */
.accessdenied .info,
.page_discussion .accessdenied,
.create-profile
{display:none!important}

  /* Removes "Hej - og tak for dit besøg!" - Adblock warning */
  .adblock-message-container,
  .adblock-message-overlay
  {display:none!important}
  
}
