css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ibood.com") {
	
/* IBOOD VAN DE DAG  ================ */
  
  a.sl > h2{
    margin: 0 0 30px 0!important;
  }

  @media(min-width:767px){
      .wantone{
          bottom: 50px!important;
       }

       .button-cta a{
          box-shadow: 0 0 0.2em rgba(0, 0, 0, 0.3);
          font: bold 2.2em/50px Verdana,sans-serif;
          width: 250px;
       }
  }

  .specslist > ul{
    font-weight: normal;
  }
  
  .short-desc.cf, .price > span.discount, .offer-title > strong:nth-child(1), .primary-offer-specs .widget.community .widget-wrap.cf, .subscribe-box {
      display: none;
  }
  .widget.community .button-link {
  float: left;
  }
  a.pe{
    float: right;
  }
  .section-inner .container{
    padding: 0 12px;
  }

  
/* HUNT ===================*/
  .huntbeacon.homepage{
    background: transparent url("http://ibood.com/img/hunt-ani-small.gif") no-repeat scroll 0% 0%;
    top: 248px;
    left: -25px;
  }
  .huntbeacon.right{
    left: 170px;
    
  }
  .offer--product-detail .image.hunt{
    margin-top: 30px;
    margin-bottom: 15px;
  }
  .passed-offers-slider  img.slider-img-logo{
    max-width: 20%;
  }
  
/* SPECs ==================== */
  @media(min-width:767px){
     #addToBasketForm .cta .button-cta #order-button{
       width: 200px;
       border-radius: 5px;
       box-shadow: 0.1em 0.1em 0.2em rgba(0, 0, 0, 0.3);
       font: bold 1.8em/37px Verdana,sans-serif;
     }
  }
  .takeone .widget-wrap{
    display: none;
  }
  
  
/* FLASH SALES ============== */
  aside.flash, .footer-shops, .flash-info > .logo-img{
    display:none;
  }
  
  .flashsale, .widget-box, .tile, .flash-offer, .flashsales-promo, .flashsales-header.cf{
    box-shadow: 1px 1px 1px 1px #DDD;
  }
  
  #flashsales-section .flashsales{
    width: 100%!important; 
    padding: 0px!important;
  }
  
  @media(min-width:767px){
     .flashsale{
        width: 49%;
        float: left;
        margin-right: 10px;
     }
  }
  
  .flash-img, .flash-info{
    width: 50%;
  }
   
  .flash-info{
    padding-top: 0px;
    padding-bottom: 0px;
  }

  .flash-info > h5{
     margin-top: 10px!important;
     margin-bottom: 10px!important;
     font-size: 1.5em;
  }

  .flash-info > .button-form > a{
     font: bold 1.1em/30px Verdana,sans-serif!important;
     margin-top: 10px!important;
  }

  .flash-img{
     padding: 2px;
     height: 125px;
  }
  .flash-img > .banier{
    background-image: url('http://ibood.com/img/banier/dropdown.png')!important;
    font: 1.1em/15px Museo500,Verdana,sans-serif;
    right: -7px;
    left: auto;
    width: 58px;
    height: 37px;
    padding: 4px 2px 0px 8px; 
  }
  
/* ALL DEALS  ======================= */ 
  /* === DAILY OFFERS ==================*/
  .all-offers .offer-product-detail h5{
    font: 1.5em/15px Museo700,Verdana,sans-serif;
  }
  .all-offers .onlytoday, .all-offers .counter .countdown, .all-offers .counter .until, .all-offers .counter .ficon-date{
    display: none;  
  }
  
  .all-offers .offer-product-detail .banier{
    font: 1.0em/14px Museo500,Verdana,sans-serif!important;
    background-image: url('http://ibood.com/img/banier/dropdown.png')!important;
    width: 58px;
    height: 37px;
    padding: 4px 2px 0px 8px; 
    top: 10px;
  }
  
 .all-offers .offer-product-detail .banier .percentage{
    font: 1.5em/14px Museo500,Verdana,sans-serif;
  }
  .all-product-button{
      font: 1.1em/14px Museo500,Verdana,sans-serif;
  }
  
  .all-offers .old-price span.strike{
    float: left;
  }
  .all-offers .counter{
    color: #FFF;
    min-height: 10px;
  }
  
  .all-offers .button-cta.small a, .all-offers .button-cta.small button{
     font: 1.7em/37px Museo500,Verdana,sans-serif;
  }
  
  .all-offers .callout-title{
    display: none;
  }
  
  
  /*ibood*/
  .primary-offers > div:nth-child(1) {
    border-top: 5px solid #d2e2f0;
    border-color: #d2e2f0;
  }
  
  .primary-offers > div:nth-child(1) > .offer-product-detail > .left-side-product-box > h5 > a{
    color: #d2e2f0;
    background: none;
  }

  /*HL*/
  .primary-offers > div:nth-child(2){
    border-top: 5px solid #BCA185;
    border-color: #BCA185;
  }
  .primary-offers > div:nth-child(2) > .offer-product-detail > .left-side-product-box > h5 > a{
    color: #BCA185;
    background: none;
  }
  
  /* HB */
  .primary-offers > div:nth-child(3){
    border-top: 5px solid #2BACB2;
    border-color: #2BACB2;
  }
  .primary-offers > div:nth-child(3) > .offer-product-detail > .left-side-product-box > h5 > a{
    color: #2BACB2;
    background: none;
  }

   /* extra */
  .primary-offers > div:nth-child(4){
    border-top: 5px solid #5e90b2;
    border-color: #5e90b2;
  }
  .primary-offers > div:nth-child(4) > .offer-product-detail > .left-side-product-box > h5 > a{
    color: #5e90b2!important;
    background: none;
  }

  /*=== FLASH ==========================*/
  .flashgroup .banier{
    top: 10px;
    font: 1.1em/15px Museo500,Verdana,sans-serif;
    background-image: url('http://ibood.com/img/banier/dropdown.png')!important;
    width: 58px;
    height: 37px;
    padding: 4px 2px 0px 8px; 
  }
  
  .flashgroup .title, .all-offers .offer-product-detail .banier{
    font: 1.3em/14px Museo500,Verdana,sans-serif;
    padding: 4px 5px;
    max-height: 45px;
    overflow: hidden;
  }
  
   .flashgroup .offer-detail .banier .percentage{
      font: 1.5em/14px Museo500,Verdana,sans-serif;
  } 
  .flashgroup .flash-price .strike{
    display: none;
  }
  
  .flash-offer .flash-offer-img .soldout-overlay{
	  background: rgba(255, 255, 255, 0.8) none repeat scroll 0% 0%;
  }
  
 /* SOCIAL MEDIA  =====================*/

  .ibood-community, .wantone > ul.social-icons, aside.flash.detail > .widget:nth-child(5), aside.widgets{
    display: none;
  }
 
  /* FLASH SALE PAGE ===================*/
  .flashsales.page.cf{
    width: 100%;
  }
  .flashsales-header{
    width: 97.2%;
  }
  
  .container > aside.flash.detail:nth-child(4){
    display: block;
    float: left;
    width: 98%;
    padding-right: 10px;
  }
  
  aside.flash.detail > .separator:nth-child(4), aside.flash.detail > .separator:nth-child(6), .flash.detail .widget.community .widget-title{
    display:none;
  }
   aside.flash.detail > .separator:nth-child(2){
     border: none;
   }
   .ficon-community:before{
    font-size: 16px;
    height: 16px;
    width: 16px;
  }
  .flash.detail .button-link{
    float: left;
  }
  
  .flash.detail .widget:nth-child(1){
    padding: 20px;
    margin-top: 10px;
    box-shadow: 0 0 0.2em rgba(0, 0, 0, 0.3)!important;
  }
  .flash-offer h5{
    min-height: 43px;
  }
  .widget.community{
    padding: 0;
  }
  
  /* FORUM =================*/
  .community{
    width: 100%!important;
  }
	
  /* DARKNESS ============== */
	.menu-top, .home-living .menu-top,.sports-fashion .menu-top , .extra .menu-top, .alldeals .menu-top, .myibood .menu-top{
	  background: transparent linear-gradient(#0b1621, #13273a) repeat scroll 0% 0%;
	}

	section .section-inner, header .header-inner, .specslist, body, .myibood .addressess, html{
		background: #0b1621 none repeat scroll 0% 0%;
	}

  .footer-ibood, .home-living .footer-ibood,.sports-fashion .footer-ibood , .extra .footer-ibood, .alldeals .footer-ibood, .myibood .footer-ibood, .widget-box .widget-wrap, .widget.specifications.hunt .widget-wrap {
    background: #0f1e2e none repeat scroll 0% 0%;
  } 
  
  header .ad.header, .widget.ad{
    background: none!important;
  }
  
  /* EXTRA */
  .extra .strip-tab, .extra .split .shipping-price, .extra .forum-column.details .mini-pagination a.page-numbers, .extra .account-stats i .split .shipping-price, .extra .what, .extra .faq-split, .extra .confirmation h1, .extra .breadcrumb a, .extra .top a, .extra .hunt-deals .title, .extra .stock-range span, .extra .expert-time, .extra .topic-content h4, .extra .basket-item-row .remove-coupon, .extra .faq-questions p.category, .extra .flash-offer h5 a, .extra aside.flash h5, .extra .ibood-community h1, .extra .flashsales h1, .extra .step-title, .extra .hint i, .extra .secure-connection i, .extra label.info i, .extra .coupon-explanation, .extra .basket-item-total .remove-item, .extra .offer-name, .extra .newhere, .extra .content-box h3 i, .extra .lost, .extra .change, .extra .tabs .content h2, .extra .offer h2, .extra .details .title a, .extra .takeone .button-links .expert, .extra .readmore, .extra .widget-title ul i, .extra .widget-title > a, .extra .primary-offer h2, .extra .mobile-nav.secondary ul li a, .extra .cart-icon a, .extra .cart-icon a i, .extra .footer-shops h1, .extra .subscribe-label, .extra .secondary-nav li a, .extra .social-icons li i, .extra .event-list-title .passed, .extra .widget.event-list .widget-title, .extra .active-topics th,.primary-nav li.extra:hover > a, .primary-nav li.extra.active > a, .primary-nav li.extra .dropdown h3{
    color: #0d84cd ;
  }
  
  .strip.sports-fashion{
	 background: rgba(0, 0, 0, 0) linear-gradient(#036771, #2ea1a6) repeat scroll 0 0
  }
  
	
  /* DROPDOWN */
	.dropdown-right{
		background: #13273a url("http://www.ibood.com/img/flash.png") no-repeat scroll center 80%;
	}

	.dropdown{
		background: #204061 none repeat scroll 0 0;
	}

	.primary-offer h2, .dropdown-primary h3,.primary-nav>ul>li:hover>a, .primary-nav>ul>li.active>a,body, .offer-row .offer-details .order-actions i,body.alldeals .primary-nav > ul > li.alldeals > a, body.alldeals .primary-nav > ul > li.active:hover > a{
  	  color: #d2e2f0;
  }

	section.second .section-inner, .footer-legal, .textbox, .dateofbirth select, .select{
		  background: #13273a none repeat scroll 0% 0%;
	  	color: #fff;
	}
  
	.copyrights li a, a, .myibood .row-header a, .clean .welcome a, p a{
			color: #d2e2f0;
	}
  
	.primary-offer .price, .faq-categories p.answer, .forum-column.details .img, .sports-fashion .header-pagination .pagination li span, .sports-fashion .header-pagination .pagination li a, .home-living .header-pagination .pagination li span, .home-living .header-pagination .pagination li a, .extra .header-pagination .pagination li span, .extra .header-pagination .pagination li a,  .pagination li span, .header-pagination .pagination li a, body.hunt .primary-offer .button-links {
			background: none;
	}
	
	.price .new-price, .flash-info h5, .flashsale .date, .specslist, .dropdown-right strong, .listprice .price, .offer-product-wrap.text,.strip-tab, .home-living .strip-tab, .extra .strip-tab, .sports-fashion .strip-tab, .content-box, .wrap-box, p, .myibood-nav li a, .offer-row,.clean .welcome, label.error span{
			color: #FFF;
	}
  
  .strip-tab, .primary-offer h2 span, .carty h4{
     background: rgba(0,0,0,0.2); 
  }
	
	.widget-title{
			color: #999;
	}

  img,.flashsales-header .header-img, .carty, .flex-active-slide .tile, .passed-offers-slider .tile, .passed-offers-slider .flex-direction-nav a {
			/*opacity: 0.5;*/
    -webkit-filter: brightness(50%); /* Chrome, Safari, Opera */
     filter: brightness(50%);
	}

	img:hover, .flashsales-header .header-img:hover, .carty:hover, .flex-active-slide .tile:hover, .flex-active-slide .tile img, .passed-offers-slider .tile img, .passed-offers-slider .flex-direction-nav a:hover, .passed-offers-slider .tile:hover,  .current-offer img, .current-offer:hover {
			/*opacity: 1;*/
    -webkit-filter: brightness(90%); /* Chrome, Safari, Opera */
     filter: brightness(90%);
	}
  
  .current-offer {
    	/*opacity: 70;*/
    -webkit-filter: brightness(70%); /* Chrome, Safari, Opera */
     filter: brightness(70%);
  }
  
  /* Lighter content box */
	.all-offers .offer-product-wrap, .all-flash .offer-wrap, .flashsale, .primary-nav>ul>li:hover>a, .primary-nav>ul>li.active>a, .content-box, .myibood .box, .content-box.full, .order-row, .flash-offer, body.alldeals .primary-nav > ul > li.alldeals > a,body.alldeals .primary-nav > ul > li.active:hover > a, .flashsales-promo, .flash.detail .widget:nth-child(1), .flash.detail .widget.community .widget-box{
			background: #204061 none repeat scroll 0% 0%;

	}
  .all-offers .old-price{
    color: #204061;
  }
	
  /* NO SHADOW */
	.widget-box, .offer-product-detail, .offer-detail, .flashsale, .content-box, .content-box.shadow, .order-row, .myibood .order-list .pagination,.flash-offer-wrap, .flash-offer, .flashsales-header, .flashsale, .widget-box, .tile, .flash-offer, .flashsales-promo, .flashsales-header.cf {
			box-shadow: none;
	}
  
  .flashsales-header.cf{
    border: #e0eaf5 solid 2px;
  }
  
  .strip{
    background: transparent linear-gradient(#244b6d, #0d84cd) repeat scroll 0% 0%;
  }
  
  .strip.home-living{
    background: transparent linear-gradient(#503b26, #8d6e4e) repeat scroll 0% 0%;
  }
  
  .offer-product-wrap.text .fade{
	  background-image: linear-gradient(rgba(0, 0, 0, 0), #204061, #204061);
  }
  .soldout-overlay, .flash-offer .flash-offer-img .soldout-overlay{
    background: rgba(32,64,97, 0.2) none repeat scroll 0 0;
  }
  
  /* MYIBOOD */
  .myibood .strip-tab, .myibood .split .shipping-price, .myibood .forum-column.details .mini-pagination a.page-numbers, .myibood .account-stats i .split .shipping-price, .myibood .what, .myibood .faq-split, .myibood .confirmation h1, .myibood .breadcrumb a, .myibood .top a, .myibood .hunt-deals .title, .myibood .stock-range span, .myibood .expert-time, .myibood .topic-content h4, .myibood .basket-item-row .remove-coupon, .myibood .faq-questions p.category, .myibood .flash-offer h5 a, .myibood aside.flash h5, .myibood .ibood-community h1, .myibood .flashsales h1, .myibood .step-title, .myibood .hint i, .myibood .secure-connection i, .myibood label.info i, .myibood .coupon-explanation, .myibood .basket-item-total .remove-item, .myibood .offer-name, .myibood .newhere, .myibood .content-box h3 i, .myibood .lost, .myibood .change, .myibood .tabs .content h2, .myibood .offer h2, .myibood .details .title a, .myibood .takeone .button-links .expert, .myibood .readmore, .myibood .widget-title ul i, .myibood .widget-title > a, .myibood .primary-offer h2, .myibood .mobile-nav.secondary ul li a, .myibood .cart-icon a, .myibood .cart-icon a i, .myibood .footer-shops h1, .myibood .subscribe-label, .myibood .secondary-nav li a, .myibood .social-icons li i, .myibood .event-list-title .passed, .myibood .widget.event-list .widget-title, .myibood .active-topics th{
    color: #356e9f;
  }
  
  .myibood-nav li a.active i, .myibood-nav li a:hover i, .myibood .my-content i{
    color: #ea842b;
  }
  
  .myibood .row-header, .myibood .order-list .pagination{
    background: #306594 none repeat scroll 0 0;
    color:  #FFF;
  }

/* FORUM */
  .forum-row:nth-child(2n), .comment-row:nth-child(2n+1) {
    background: #13283a none repeat scroll 0 0;
   }
  
  .home-living .widget.community i.ficon-community, .home-living .comment-header, .home-living .forum-header, .home-living .tab-header, .home-living .callout, .home-living .header-pagination .pagination, .home-living .forum-footer .pagination{
    background: #6d553c;
  }
  .comment-header, .forum-header, .tab-header, .callout{
    background: #244b6d;  
  }
  .sports-fashion .widget.community i.ficon-community, .sports-fashion .comment-header, .sports-fashion .forum-header, .sports-fashion .tab-header, .sports-fashion .callout{
    background: #175d60;
  }
  .comment-content .quote, .topic-content .quote{

    background-color: #1b3a54;
  }
  /* Hunt */
  .widget.event-list-widget.stripes, .container.stripes{
    background-color: #244b6d!important;
    background-image: linear-gradient(45deg, rgba(19, 39, 58, 0.5) 25%, rgba(19, 39, 58, 0.35) 25%, rgba(19, 39, 58, 0.35) 50%, rgba(19, 39, 58, 0.5) 50%, rgba(19, 39, 58, 0.5) 75%, rgba(19, 39, 58, 0.35) 75%, rgba(19, 39, 58, 0.35));
  }
  .widget.specifications.hunt::before, .widget.specifications.hunt::after, body.hunt .primary-offer .button-links::before, body.hunt .primary-offer .button-links::after{
    border: none;
  }
}
