css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("warframe.wikia.com") {
/*   Hide useless shit, ads and BS */
  #WikiaBar, #WikiaNotifications, .background-image-gradient, #mw-js-message, noscript, .skiplinkcontainer, #fb-root, #ad-skin, #WikiaTopAds, #InvisibleHighImpactWrapper, #GPT_FLUSH, #EVOLVE_FLUSH, #HITMEDIA_FLUSH, #TURTLE_FLUSH, #tfb, #TOP_LEADERBOARD_AB, .table-cell:not([class*="search-container"]), #DisplayClockJS, #WikiaRail, #WikiaFooter, .branding, .mainpage-header-news:nth-of-type(7), .mainpage-header-news:nth-child(9), .mainpage-header-news:nth-child(13), nowiki, .mainpage-box-featuredpoll, .mainpage-box-recentactivity, .rcs-container > .center, .alliance-module, .mainpage-box-socialmedia, .header-container > .header-column.header-tally:last-child, .WikiaArticleInterlang, .global-footer > nav > ul > li:not(.article-comments-pagination), #article-comments-counter-header > div, .mainpage-box-playfree{
    display:none !important;
    visibility:collapse !important;
  }
  .global-navigation-container > .search-container{
    max-width:750px !important;
    display:block;
    margin:0 auto !important;
  }
/*   Disable transitions */
  *, img.lzyTrns{
    transition:all 0s ease 0s !important;
    -o-transition:all 0s ease 0s !important;
    -moz-transition:all 0s ease 0s !important;
    -webkit-transition:all 0s ease 0s !important;
  }
/*   Start the dark stylying at the base */
  html, body, #WikiaSiteWrapper{
    background:none #313131 !important;
    opacity:1.0;
    color:#dddddd;
    background-image:none !important;
  }
  .WikiaPage .WikiaPageBackground{
    background:none #282828;
  }
  table.foundrytable th, .WikiaArticle ul.tabbernav, .WikiaArticle .tabbertab-borderless ul.tabbernav, .WikiaArticle .tabbertab-bordered ul.tabbernav, .navboxhead, .navboxgroup, .WikiaArticleComments .MiniEditorWrapper .editarea textarea{
    background:none #111111;
  }
  table[style*="border: 1px solid #CCCCCC;"] tr th, table.navbox.mw-collapsible.mw-made-collapsible tr td[style*="background-color:#aaa1e5"], [style*="background: linear-gradient(#404040, #151515);"]{
    background:none #111111 !important;
  }
  table.foundrytable td, .infobox, .navbox, .portable-infobox, .mainpage-box-news, .mainpage-box-forumactivity, .emodtable td, .bigmodtable td, .SpeechBubble .speech-bubble-message, .SpeechBubble.even .speech-bubble-message, .SpeechBubble.owner .speech-bubble-message{
    background:none #212121;
  }
  .plainlinks, .article-categories{
    background:none #212121 !important;
  }
/*   Remove redundant styling */
  .WikiaPage, table.foundrytable td, table.foundrytable td, table.foundrytable th, table.foundrytable td, .infobox, .mainpage-box-news, .mainpage-box-forumactivity, .article-categories, .WikiaArticleComments, .SpeechBubble .speech-bubble-message, a.wikia-button.secondary:hover, .wikia-single-button a.secondary:hover, .wikia-menu-button.secondary:hover, input.secondary[type="submit"]:hover, input.secondary[type="reset"]:hover, input.secondary[type="button"]:hover, .button.secondary:hover, button.secondary:hover, .WikiaArticleComments .upper-pagination, .WikiaArticleComments .article-comments, .avatar{
    box-shadow:none;
    border-color:transparent;
  }
  a.wikia-button.secondary, .wikia-single-button a.secondary, .wikia-menu-button.secondary, input.secondary[type="submit"], input.secondary[type="reset"], input.secondary[type="button"], .button.secondary, button.secondary, a.wikia-button.secondary:hover, .wikia-single-button a.secondary:hover, .wikia-menu-button.secondary:hover, input.secondary[type="submit"]:hover, input.secondary[type="reset"]:hover, input.secondary[type="button"]:hover, .button.secondary:hover, button.secondary:hover, a.wikia-button, .wikia-single-button a, .wikia-menu-button, input[type="submit"], input[type="reset"], input[type="button"], .button, button, a.wikia-button:hover, .wikia-single-button a:hover, .wikia-menu-button:hover, input[type="submit"]:hover, input[type="reset"]:hover, input[type="button"]:hover, .button:hover, button:hover{
    background-image:none;
    border-color:transparent;
  }
  .bigmodtable{
    margin-bottom:5px;
  }
  .bigmodtable td{
    border-color:#313131;
  }
  table[style*="border: 1px solid #CCCCCC;"]{
    border-color:transparent !important;
  }
  .WikiaArticleComments .MiniEditorWrapper .editarea{
    border:none;
  }
/*   Take care of the navigation */
  #globalNavigation{
    background:none #4c4c4c;
    box-shadow:inset 0px 1px 2px 0px #111111, 0px 0px 3px 1px #000000;
  }
  .WikiHeader > nav li.marked > a, .navbox, .portable-infobox, .WikiHeader > nav li > a{
    border-color:transparent;
  }
  .WikiaPage .WikiaArticle .infobox{
    font-size:12px;
  }
  .infobox, .navboxhead, .WikiHeader > nav li > a, #bodyContent{
    color:#dddddd;
  }
  span[style*="color:rgb(39,42,52);"]{
    color:#aaaaaa !important;
  }
  table[style*="border: 1px solid #CCCCCC;"] tr th a span, .navboxhead a, .article-comments-pagination, [style*="color:#3a3a3a;"]{
    color:#f18260 !important;
  }
  .infobox .left{
    width:50%;
  }
  .WikiaMainContent{
    width:100%;
  }
  a, table.foundrytable th a, .infobox .left a, .WikiaArticleComments .article-comments-pagination a{
    color:#f18260;
  }
  .accent, #article-comments-pagination-link-next, .ambox-blue{
    border-color:#f18260;
  }
  .mainpage-box-playfree span, .mainpage-box-playfree:hover{
    background:none #d40001 !important;
  }
  .WikiaArticleComments .article-comments-pagination a.accent, a.wikia-button.secondary:hover, .wikia-single-button a.secondary:hover, .wikia-menu-button.secondary:hover, input.secondary[type="submit"]:hover, input.secondary[type="reset"]:hover, input.secondary[type="button"]:hover, .button.secondary:hover, button.secondary:hover, .WikiaArticle ul.tabbernav li.tabberactive a, .WikiaArticle .tabbertab-borderless ul.tabbernav li.tabberactive a, .WikiaArticle .tabbertab-bordered ul.tabbernav li.tabberactive a{
    background:none #f18260;
    color:#111111;
  }
  .Wall ul.comments > li.message > .speech-bubble-message::after{
    border-color:#212121 #212121 transparent transparent;
  }
  .Wall ul.comments > li.message > .speech-bubble-message{
    background:none #212121;
    color:#f1f1f1;
  }
  .SpeechBubble .speech-bubble-message:after{
    border-color:#212121 #21211 transparent transparent !important;
  }
  .SpeechBubble .speech-bubble-message::before, .SpeechBubble .speech-bubble-message::after{
    border-color:transparent #212121 #212121 transparent !important;
  }
  .article-comm-text{
    font-size:14px !important;
  }
  .accent, .neutral{
    background:none transparent;
  }
  .SpeechBubble .speech-bubble-avatar a img{
    background-color:transparent !important;
  }
}
