css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("web.telegram.org") {

  .tg_head_split {
    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2),
                0 8px 10px 1px rgba(0, 0, 0, .14),
                0 3px 14px 2px rgba(0, 0, 0, .12);
  }

  .im_page_wrap {
    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2),
                0 8px 10px 1px rgba(0, 0, 0, .14),
                0 3px 14px 2px rgba(0, 0, 0, .12);
  }

  .im_page_split {
    background: #e7ebf0 url(http://i.imgur.com/WdGuNvY.jpg) fixed;
  }

  .im_dialogs_col_wrap {
    background: #f1f1f1;
  }

  li.im_dialog_wrap > a {
    will-change: background-color;
    transition: background-color 150ms ease-in;
  }

  li.im_dialog_wrap > a * {
    will-change: color;
    transition: color 200ms ease-in;
  }

  .im_history_wrap {
    background: linear-gradient(180deg, transparent, 70%, transparent, rgba(255, 255, 255, .6));
  }

  .im_bottom_panel_wrap {
    background: linear-gradient(180deg, rgba(255, 255, 255, .6), 60%, rgba(255, 255, 255, .8));
  }

  .im_history_messages {
    text-shadow: 0 1px white;
  }

  .im_history_messages [ng-switch-when="sticker"] {
    filter: drop-shadow(0 0 6px white) drop-shadow(0 0 7px white);
  }

  .im_record_bg {
    background-color: rgba(255, 255, 255, .4);
  }

  .im_record_bg:hover {
    background-color: rgba(255, 255, 255, .7);
  }
}
