css:
@-moz-document domain("radiotunes.com") {

body {
    background-color: #222;
}
#logo {
    -webkit-filter: brightness(40%);
}
#main-container {
    background-color: #333;
}
#toolbar-container {
    background-color: #222;
    border-bottom-color: #444;
}
#page>header {
    background-color: #035;
}
.nav-link, #header-nav a, #account-nav .account>li>a {
    color: #38A;
}
#account-nav li>a.arrow-button {
    background-color: #444;
    color: #999;
}
#account-nav li>a.arrow-button span.arrow {
    border-left-color: #777;
}
#channel-director {
    color: #999;
}
#channel-director span {
    color: #38A;
}
#channel-title {
    color: #693;
}
#channel-selector .arrow {
    color: #693;
}
#toolbar {
    border-left-color: #444;
}
#toolbar button {
    background-color: #150;
}
#toolbar button:before {
    color: #693;
}
#toolbar:before {
    background-color: #555;
}
#toolbar .group {
    border-right-color: #444;
}
#toolbar .group:before {
    background-color: #555;
}
#ctl-play .icon:before {
    text-shadow: 1px 1px 0 #888;
}
#hero {
    border-bottom-color: #555;                                            
}
#channel-selector {
    border-bottom-color: #030;
}
#channel-selector .backdrop {
    background: #150;
    background-image: -webkit-linear-gradient(top, #114a01 0%, #114a01 100%)
}
.flat-interact, #wp-track-vote-buttons .vote-btn, #comments-toggle {
    background-color: #363636;
    border-color: #444;
    color: #888;
}
#ui .panel {
    background-color: #444;
    border-color: #222;
}
#panel-ad .ribbon {
    background-color: #035;
}
#panel-ad .ribbon .ribbon-title {
    color: #ACE;
}
#panel-ad .ribbon .ribbon-btn {
    background-color: #046;
}
#track-history .item {
    background-color: #333;
    border-top-color: #444;
}
#track-history .item .info {
    color: #999;
}
#track-history .item .info a {
    color: #693;
}
.content-box {
    border-color: #555;
    background-color: transparent;
}
.content-box .box-title {
    background-color: #035;
}
.content-box h2.box-title, .content-box .box-title h2 {
    color: #38A;
}
#now-playing .title {
    color: #AAA;
}
#now-playing .status {
    color: #888;
}
#now-playing .timestamps {
    color: #777;
}
#now-playing .progress-container {
    background-color: #222;
}
#now-playing .progress-bar {
    background-color: #150;
}
.cssgradients #ctl-play {
    background-image: -webkit-linear-gradient(top, #444 0%, #444 50%, #444 100%)
}
ul.interact li {
    background-color: #333;
    border-top-color: #444;
}
ul.interact>li .action {
    color: #999;
}
ul.interact li.current-channel {
    background-color: #363636;
}
ul.interact>li.current-channel:hover {
    background-color: transparent;
}
ul.similar-channels li {
    background-color: #333;
    border-top-color: #444;
}
ul.similar-channels .name {
    color: #999;
}
li.current-channel .description {
    color: #999;
}
ul.interact>li [class^="icon-"], ul.interact>li [class*=" icon-"] {
    color: #AAA;
}
#foot-content {
    background-color: #035;
}
#page>footer {
    background-color: #046;
}
#footer-nav a, footer .copyright {
    color: #38A;
}
#header-nav .active>a, #header-nav a:hover {
    color: #AAA;
}
/*
#fullwidth-cta {
    -webkit-filter: brightness(13%);
}
*/
.filter-bar {
    background-color: #333;
}
#filter-bar-wrap label {
    color: #777;
}
.filter-button-wrap li {
    background-color: #444;
    background-image: -webkit-linear-gradient(top, #444 0%,#333 100%);
    border-color: #222;
    color: #999;
}
.channel-tile {
    background-color: #333;
    border-color: #222;
    box-shadow: 0 1px 2px #555;
}
.channel-tile .info {
    background-color: transparent;
    box-shadow: 0 1px 2px #555;
}
.channel-tile .info:after {
    background-color: #464646;
}
.channel-tile .info .title {
    color: #AAA;
}
.channel-tile .info .artist {
    color: #999;
}
.channel-tile .info hr {
    background-color: #777;
}
.channel-tile .info .now-playing {
    color: #BBB;
}
#channel-mosaic .channel-tile .overlay {
    background: rgba(255,255,255,0.26);
}
.channel-tile .image .overlay {
    border-color: #777;
}

}
