css:
@namespace url(http://www.w3.org/1999/xhtml);

@font-face {
    font-family: 'Vazir';
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir.eot?#iefix') format('embedded-opentype'),
		 url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir.woff') format('woff'),
		 url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir.ttf') format('truetype');
    font-weight: normal;
}
@font-face {
    font-family: 'Vazir';
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir-Bold.eot');
    src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
		 url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir-Bold.woff') format('woff'),
		 url('https://cdn.rawgit.com/rastikerdar/vazir-font/master/dist/Vazir-Bold.ttf') format('truetype');
    font-weight: bold;
}

@-moz-document domain("reader.aol.com") {

  .article-list-body, .article-body, .article-title, .article-header-sticky h2 {
    direction: rtl;
    text-align: right;
    
  }
  
  .title a, .content, .article-body, .article-body p, .article-meta, h1,h2,h3,h4,h5,h6,
  .feed-label {
    font-family: Vazir, tahoma;
  }
  
  .article-item-list .origin {
    margin: 0px;
    width: 80px
  }
  
  .article-item-list .aoli-star {
    margin: 40px;
  }
  
  .favicon {
    margin-left: 5px;
  }
  
  .section-header h2 {
    direction: rtl;
    text-align: left;
  }
  
  .section-header .count {
   margin-right: 10px; 
  }
  
  .article-body div {
    direction: ltr;
  }
  
  .article-item-view .article-content {
    max-width: 800px;
  }
 
  
}
