css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("weather.com") {

html, body, .heroBackground, .berlin, .wx-mid-wrap .panel-pane, .media-container, .description-and-source-outer
{color: white !important;
background: #111 !important;}

h1, h2, h3, h4, h5, h6, strong, .today_nowcard-sidecar caption, .today_nowcard-timestamp, .gm_sun_moon .moonphase p.text-right, .wx-dsxdate, .wxcard .lo-temp, .wxcard .hi-temp, .unified-search .menu ul li.results .name, .timestamp, .gm_alert_headline .wx-return > .wx-text, .breaking-now-inner h3 a, ::-moz-placeholder 
{color: white !important;
border: none !important;
text-shadow: none !important;}  
  
a, .text-alert-2, .wx-icon-arrow-left-2::before, .wx-icon-arrow-right-2::before, .wx-icon-search::before
{color: #00adee !important;} 
  
a:visited 
{color: #00668c !important;}   
  
#wx-local-bg, #wx-rail, .gm_location_title, .glomo_content_media.panel-pane, #wx-footer, .gm_delivery_units, .gm_geo_teaser, .berlin .video_player, .video-playlist-bottom, .today_nowcard-video, .local-suite .expand-view, .right-rail, .info-container, .wxnode_opencontent_well, .related-articles-header, .related-articles-container, .glomo_social_sharing, .taboola_module, .severe-news-content-bar
{display: none !important;}
  
li.default:hover
{background: none !important;}  
  
.panel, #umap-forecast .wx-module .map-header .wrapper, .white2 > .page-nav, .gm_header_savedlocations, .glomo-header .search-region, .header_drilldown .main-menu.list-group, .twc-typeahead-item-active, .panel-heading, .gm_sun_moon .moonphase, .gm_sun_moon .list-group.sun li, .gm_sun_moon .list-group.moon li, .ls-display-btn, .weather-table .legend, .glomo_forecast_weekend .weekend-labels, .wxcard, mapLegendTop h1, p-map-legend, .modal-content, .gm_enhanced_location_search, .gm_enhanced_location_search .heading--search, th, tr, td, .unified-search .menu ul li.results, .field-name-body  
{color: white !important;
background: #222 !important;
border: none !important;
box-shadow: none !important;}
  
li.default.active, .main-menu.list-group > li > a:hover, .nav-tabs > li.active > a, .dropdown-menu > li > a:hover, .white2 .page-nav .active > a, .white2 .page-nav a:hover, .unified-search .menu ul li.results .twc-typeahead-item-active, .unified-search .menu ul li.results .twc-typeahead-item-hover, .gm_alerts .bg-alert-3, .gm_alerts .bg-alert-3:hover, .gm_alerts .dropdown .priority .title, .breaking-now-container
{color: white !important;
background: #00adee !important;}

.unified-search   
{color: white !important;
background: #333 !important;
border: 1px solid #00adee !important;}   
  
.daily-forecast-map, .gm_sun_moon, .dropdown-menu, .menu, .local-suite
{background: #222 !important;
border: 1px solid #00adee !important;}  
  
.ls-24-hour-wrap, .ls-hourly-wrap
{background: #222 !important;
border-top: 1px solid #00adee !important;
border-left: 1px solid #00adee !important;
border-right: 1px solid #00adee !important;}
  
.ls-display-control
{background: #222 !important;
border-bottom: 1px solid #00adee !important;
border-left: 1px solid #00adee !important;
border-right: 1px solid #00adee !important;}  
  
.today_nowcard-sidecar tr:first-child  
{border: none !important;}  
  
.today_nowcard-sidecar tr, .list-group > li, .thumbnails
{border-top: 1px solid #00adee !important;}    
  
.today_map .panel-heading .nav > li, div.width-auto:nth-child(2), div.width-auto:nth-child(3)
{border-left: 1px solid #00adee !important;}  
  
.today_map .panel-heading .nav 
{border-right: 1px solid #00adee !important;} 
  
.twc-table th, .twc-table td, .gm_alert_details .alert-vicinity .alert-item, .separator, .wx-slideshow  
{border-bottom: 1px solid #00adee !important;}  
   
.gm_alert_details .detail-content .detail-header
{padding-top: 10px !important;}

#wx-main
{padding-right: 0 !important;}  
  
.gm_alert_details .time-affect .timestamp
{padding-left: 8px !important;}  
  
.forecast, .gm_alert_details .time-affect
{padding-bottom: 5px !important;}    
  
.text-alert-2, .gm_alert_headline .title 
{padding-left: 8px !important;
padding-bottom: 10px !important;}  

.weather-table .legend 
{margin-bottom: 0px !important;
padding-bottom: 0px !important;}
  
}


@-moz-document url("https://weather.com/weather/5day/l/48220:4:US") {
  
.ls-display-control
{display: none !important;}
  
}
