css:
@-moz-document url-prefix("https://irccloud.com"), url-prefix("https://www.irccloud.com"), url-prefix("https://alpha.irccloud.com") {
div#mainwrapper{
    padding-right: 6px;
}
div#sidebarwrapper{
    position: absolute;
    right: 0px;
    width: 6px;
    overflow: hidden;
}
div#sidebarwrapper:hover{
    width: 175px;
}
}
