css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ernstchan.com") {
body {
    background-color: #ffffff;
    color: #000000;
    font: normal 0.8em sans-serif;
}

.post {
    background-color: #aca7cc;
    border-right: 1px solid #afa7e0;
    border-bottom: 1px solid #9891c3;
    margin-bottom: 0.5em;
    overflow: auto;
}

.postername {
    color: #381fb8;
}

a:link, a:visited, a:hover {
    color: #392e93;
}

blockquote.unkfunc, span.quote {
    color: #237A7E;
    margin: 0;
}

.file {
    background-color: #847cb9;
    text-align: center;
    margin: 0 1.2em 1.2em 0;
    float: left;
    display: block;
}

.thread_head {
    clear: left;
    background-color: #847cb9;
    padding: 5px;
}

ul.menu li a, ul.menu_bottom li a {
    background-color: #847cb9;
    color: #000000;
    font-weight: bold;
    text-decoration: none;
    padding: 0.5em;
    margin-right: 0.5em;
    float: left;
}

ul.menu li a {
    border-right: none;
    border-bottom: none;
    border-left: none;
    border-bottom-right-radius: 0.5em;
    border-bottom-left-radius: 0.5em;
}

ul.pagelist {
    background-color: #847cb9;
    border-right: 1px solid #BFB5A1;
    border-bottom: 1px solid #BFB5A1;
    padding: 0.5em;
    margin: 0 1em 0 0;
    float: left;
}

.postblock {
    background-color: #847cb9;
    color: #FFFFFF;
    padding: 0.5em;
}

div#WzTtDiV {
    display: none;
}

.info {
    background-color: #847cb9;
    border-right: 1px solid #BFB5A1;
    border-bottom: 1px solid #BFB5A1;
    border-left: 1px solid #BFB5A1;
    padding: 1em;
    margin: 0 0 0.5em 0;
}

.title, .info th {
    background-color: #9891c3;
    color: #FFFFFF;
    font-weight: bold;
    padding: 5px;
}

}
