css:
@-moz-document domain("bttiantang.com") {
/* hidden */

.place strong,
.tlkt.cl,
.ad_hide,
a#cst,
.mrlhr,
.rel.cl,
.mrrdt.cl,
.tit > span,
.Tlogo,
center,
#topbar,
.newtip,
.fx,
.nobg {
    display: none !important;
}
/*////////*/

body {
    background: #fff;
}
.hd {
    background: #333 !important;
    margin-bottom: 0;
}
.Btitle {
    background: #444 right center no-repeat;
    font-weight: normal;
}
* {
    border-radius: 0
}
/* head */
input#keyword{
    padding-right: 0;
    outline: none;
}
.Ts {
    left: 0;
    border: none;
    border-radius: 0;
    padding: 0;
}
.Ts .si {
    float: left;
    font: 16px Arial;
    line-height: 25px;
    height: 25px;
    width: 302px;
}
.Ts .sbt {
    cursor: pointer;
    float: left;
    height: 33px;
    width: 69px;
    color: #333;
    font-size: 16px;
    background: none;
}
.posrel {
    float: right;
    background: #fff;
}
/* content */

.mb {
    padding: 0;
    background: none;
}
.mtlbt.cl > font {
    display: none !important;
}
.mtlbt.cl {
    display: none;
}
em.e_db:after {
    content: '豆';
    color: #00ac50;
	 font-style: normal;                                            
}
em.e_db {
    background: none;
}

.mrrdt .lst{
	border: none;
    margin-top: 0;
} 
.litpic img {
    width: 30px;
    height: 45px;
}

/* 强调色 */
.title a b,
em.e_db:after, 
.minfo h2 a b,
.minfo h2 a b i,
.rt em ,
font[color="#FFF300"],
b > font {
    color: #00ac50
}

.sum, 
.rt strong {
    color: #333;
}

em { font-style: normal;}

/* pagination */
span.pageinfo {
    letter-spacing: 1px;
}

.pagelist li a {
    font-weight: normal;
    color: #4CAF50;
}
/* 详情页 */
.viewbox .title {
    display: none;
}

.place{
    background: none;
    border: none;
    text-indent: 0;
}

/* 检索页 */
.minbar dl dd a.this {
    background: #444;
}

/* torrent download page */
table {
    border: none !important;
    background: none;
}

td  form:after {
    content: 'download';
    display: block;
    width: 140px;
    height: 55px;
    line-height: 55px;
    background: #333;
    color: #fff;
    margin-top: -55px;
    border-radius: 50%;
}
input[type="image"] {
    opacity: 0;
}
}
