css:
@-moz-document url("https://www.zhihu.com/") {
  body{
    color: rgb(247, 250, 252)
  }
  /* hide feeds */
  .zg-wrap.zu-main.clearfix{display: none !important;}
  /* fix layout */
  .zu-top{
  background: none;
  box-shadow: none;
  border: none;
    position: inherit;
    margin: 0 auto;
    padding: 50px 0;
}
#zh-top-link-logo{
  margin-left: auto;
  margin-right: auto;
  background-image: url("https://static.zhihu.com/static/revved/img/index/logo.6837e927.png");
  background-position: inherit;
  position: inherit;
  float: inherit;
  height: 74px;
  width: 160px;
  display: block;
  margin-bottom:30px;
}
#zg-top-nav{
  display: none;
}
  /* eye candy */
  *{
    border-radius: 0 !important;
  }
  /* bar and button */
  .zu-top-search-input{
    background: inherit;
    box-shadow: inherit;
  }
.zu-top-search-input,#zu-top-add-question,.zu-top-search-button{
  border: 1px solid #2d78f4 !important;
}
.zu-top-search-button,#zu-top-add-question{
  background: #3385ff !important;
}
  #zu-top-add-question:hover,.zu-top-search-button:hover{
    box-shadow: 1px 1px 2px rgba(0,0,0,0.2);
    /*background:#007eff !important;*/
  }
  .zu-top-search-input:focus{
    box-shadow: inset 0 1px 1px rgba(0,0,0,.2),0 1px 1px rgba(255,255,255,.3);
  }
  .zu-top-search-form{
    width: 720px;
  }
  /* user profile panel */
  .top-nav-profile .zu-top-nav-userinfo{
    color: #3385ff;
  }
  .zu-top-nav-userinfo.selected,.top-nav-profile:hover,.zu-top-nav-userinfor,.top-nav-profile a{
    background: #3385ff;
  }
  .top-nav-profile:hover .zu-top-nav-userinfo{
    background: #3385ff !important;
    color:white;
  }
  .zu-top-nav-useinfo{
    background: white;
  }
  html.no-touchevents .top-nav-dropdown a:hover{
    background: #036fdd;
  }
  /* search tips */
  .zu-autocomplete-row-label{
   background: #d6e9f8;
   height: 1px;
    padding: 0;
    color:rgba(0,0,0,0);
  }
  .ac-renderer{
   /* box-shadow: 1px 1px 3px rgba(0,0,0,0.2);*/
    box-shadow: none;
    border: 1px solid #2d78f4 !important;
    margin-left: -1px;
    border-top: none !important;
    width: 679px;
  }
  .zu-autocomplete-row-search-link{
    background: #d6e9f8 !important;
    color: #259 !important;
  }
}
