/* Posts */
.block--messages .message-cell.message-cell--user {
	border-radius:0;
	background-color: #e1e4f2;
	border-color: #d1d1e1;
}
.message-userDetails .message-name {
	font-size: 14pt;
    font-weight: normal;
}
.message.message .message-userDetails .userBanner {
    padding: 0;
    /* text-align: left; */
    border: none;
	margin: 0;
    color: #000;
    background-color: transparent;
}
.message.message .message-userDetails .userBanner span {
    display: none;
}
.message.message .reactionsBar { padding: 8px; }
.block--messages .message.message--post, .block--messages .message.message--conversationMessage {
	margin-top: 6px;
	border: 1px solid #0B198C;
	border-radius: 0;
	box-shadow: none;
}
.message-main {
	background-color: #F5F5FF;
}
.bbCodeBlock.bbCodeBlock--quote {
	border: 1px inset;
	margin: 15px 5px;
	position: relative;
	overflow: visible;
}
.bbCodeBlock.bbCodeBlock--quote:before {
	content: "Quote:";
	font: 11px sans-serif;
	position: absolute;
	left: 0;
	top: -14px;
}
	
.bbCodeBlock--quote > .bbCodeBlock-title, .bbCodeBlock--quote > .bbCodeBlock-content {
	color: #000;
	background-color: #e1e4f2;
	padding: 6px;
	overflow: hidden;
}
.bbCodeBlock--quote > .bbCodeBlock-content {
	font-style: italic;
}
.bbCodeBlock--quote > .bbCodeBlock-title:before {
	content: "Originally ";
	display: inline;
}
.bbCodeBlock-sourceJump { font-weight: bold; }
.bbCodeBlock--expandable .bbCodeBlock-content .bbCodeBlock-expandContent {
	max-height: none;
}
.bbCodeBlock--expandable .bbCodeBlock-content .bbCodeBlock-expandLink {
	display: none;
}
.message-cell--main {
	padding: 0;
}
.message-cell--main .message-content {
	padding: 0 8px;
}
.message-cell--main .message-body {
	margin-top: 8px;
}
.message-attribution .message-newIndicator {
	background-color: #E80;
}
.message-userExtras {
	margin-top: 1em;
}
.message-userExtras .pairs {
	line-height: normal;
}
.message-cell.message-cell--user, .message-userExtras.message-userExtras, .message-userTitle.message-userTitle, .message-userExtras .uix_icon.uix_icon {
	font: 11px verdana, geneva, lucida, 'lucida grande', arial, helvetica, sans-serif;
	color: #000;
}
.message-userExtras .pairs.pairs--justified>dt {
	margin-right: .3em;
	white-space: nowrap;
}
.message-userExtras .pairs.pairs--justified {
	justify-content: unset;
}
.message-userExtras .pairs.pairs--justified dd {
	overflow-x: hidden;
	text-overflow: clip;
	float: none;
}
.message-userExtras .uix_icon--user:before { content: "Joined:"; }
.message-userExtras .uix_icon--messages:before { content: "Posts:"; }
.message-userExtras .uix_icon--like:before { content: "Likes:"; }
.message-userExtras .uix_icon--trophy:before { content: "Trophies:"; }
.message-userExtras .uix_icon--web { display: none; }
.message-userExtras .react_total_bar {
	margin-top: .5em;
}
.actionBar.actionBar {
	margin: -6px;
	border-top: none;
}
.actionBar a.actionBar-action, .reactions-right .th_reactions__trigger {
	padding: 2px 6px;
    border: 1px solid #909EB8;
    border-radius: 9px;
    color: #fff;
    background: linear-gradient(-30deg, #465C81, #8EACE0);
    text-decoration: none;
	font-family: sans-serif;
	font-weight: bold;
}
.actionBar a.actionBar-action:hover {
	color: #fff;
}
@media (max-width: 650px) {
	.block--messages .message-cell.message-cell--user {
		padding: 4px 30% 4px 4px;
	}
	.message--post, .message--conversationMessage { position: relative; }
	.message-cell--main .message-attribution {
		color: #000;
		white-space: nowrap;
		padding: 0;
	}
	.message-attribution:not(.message-attribution--plain) .u-dt {
		position: absolute;
		right: 4px;
		top: 2em;
	}
    .message-attribution-opposite {
		position: absolute;
		right: 4px;
		top: 4px;
	}
	.react_total_bar.react_total_bar {
		height: 4px;
		margin: 0;
	}
	.message-userExtras.message-userExtras {
		margin: 0;
	}
}

@media (min-width: 651px) {
	.block--messages .message-cell.message-cell--user {
		margin-top: 25px;
		flex-basis: 170px;
	}
	.block--messages .message--quickReply .message-cell.message-cell--user {
		margin-top: 0;
	}
	.message-userDetails .message-name {
		margin: 0 -8px;
	}
	.message-cell--main .message-attribution:not(.message-attribution--plain) {
		padding: 4px 4px 3px;
		border: 1px solid #d1d1e1;
		border-radius: 0;
		background-color: #6295C8;
		color: #fff;
		margin-left: -170px;
		width: calc(100% + 170px);
		font: bold 12px/16px sans-serif;
	}
	.message--conversationMessage .message-cell--main .message-attribution:not(.message-attribution--plain) {
		margin-left: -178px;
		width: calc(100% + 170px + 16px);
	}
	.message-cell--main .message-attribution:not(.message-attribution--plain) a {
		color: #fff;
	}
	.message-signature.message-signature {
		border-top: 0;
		padding: 0;
		margin-top: -8px;
	}
	.message-signature:before {
		content: "__________________";
		display: block;
	}
	.message-lastEdit {
		position: absolute;
    	top: -26px;
    	color: #fff;
    	right: 9.3em;
		font: bold 12px/16px sans-serif;
	}
	/* QA controls */
	.block--messages .message.message--post, .block--messages .message.message--conversationMessage { position: relative; }
	.message-cell--qaControls {
		position: absolute;
		left: 0;
		bottom: 0;
		width: 170px;
		z-index: 10;
		padding: 4px 12px 8px;
		background-color: #F5F5FF;
	}
	.message-cell--qaControls a {
		float: left;
	}
	.message-cell--qaControls a + a {
		padding-left: 15px;
	}
	.message-cell--qaControls a:first-child + a {
		padding-left: 25px;
	}
	.message-cell--qaControls + .message-cell--user {
		padding-bottom: 48px;
	}
}
}
