#webtop_favorites_inner_container > .favorite > span.caption > span {
    color: cyan!important;
    font-weight: bold;
}
#webtop_favorites_inner_container > .favorite > span.caption {
    color: white!important;
    font-weight: bold;
}
#webtop_favorites_inner_container > span.favorite {
    background-color: hsla(200, 100%, 69%, 0.4);
    border-radius: 24px;
    border: 2px #000077 solid;
    margin: 3px;
}

#webtop_favorites_inner_container span.favorite:hover{
     /* background: white; */
     
    background: linear-gradient(to bottom, rgba(115, 187, 223, 1) 0%, rgba(23, 86, 127, 1) 100%);
     
	border-radius: 24px;
    border: 2px solid orange;
    cursor:pointer;   
}

#webtop_favorites_inner_container > .favorite > .image > img {
    content: url('http://4.bp.blogspot.com/-3M8LUykUF9U/UjWDCwBhpBI/AAAAAAAAAY4/eNzKnR1BbA8/s1600/Chrome-Worn.png');
}
body {
    background-color: navy;
    background-image: url(http://www.boozallen.com/content/dam/boozallen/boozallen-immerse/Images/impact-2016/impact-2016-header.jpg) !important;
}
#webtop_favorites_inner_container > span:nth-child(29),
#webtop_favorites_inner_container > span:nth-child(27),
#webtop_favorites_inner_container > span:nth-child(17) {
    border: 2px red solid;
    background-color: hsla(200, 100%, 69%, 0.6);
}
#webtop_favorites_inner_container > span:nth-child(29)> .image > img {
    content: url('http://findicons.com/files/icons/2198/dark_glass/128/mail_appt.png');
}
#webtop_favorites_inner_container > span:nth-child(27)> .image > img {
    content: url('http://a1.mzstatic.com/us/r30/Purple49/v4/c5/48/fc/c548fc77-1af8-04f7-aa76-138aac3e2bea/icon256.png');
}
#webtop_favorites_inner_container > span:nth-child(17)> .image > img {
    content: url('http://findicons.com/files/icons/2016/vista_style_objects/128/hotel.png');
}
#page_footer {
    padding: 0 5px 10px 5px;
    background-color: hsla(200, 100%, 69%, 0.4);
    border-radius: 14px;
    border: outset;
    width: 97%!important;
}
}
