css:
@-moz-document url-prefix("http://seasonvar.ru/serial") {
noindex,
iframe[style="position: fixed; display: block; top: 0px; left: 0px; width: 100%; height: 100%;"],
.td-for-left-block,
#___plusone_1td,
#player_msoc,
.sidebar.lside,
div [align="center"] {
    display: none !important;
    position: absolute;
    left: -9999px;
}

.pgs-player-inside,
.pgs-player-inside object {
    height: 754px;
}
.middle.left {
    border-left-width: 20px !important;
    padding-left: 20px !important;
}
#pjsfrrshtmlPlayer {
    display: block !important;
}
}

@-moz-document domain("seasonvar.ru") {
html body {
    font-family: arial, sans-serif;
    padding-top: 1px !important;
    background-color: #080808;
}
noindex,
.lside-player div div {
    display: none !important;
    position: absolute;
    left: -9999px;
}
html body div iframe {
    display: none !important;
}
.wrapper {
    margin: 0px auto 0!important;
}
.content-wrap,
.pgs-sinfo-actor [data-tabt=actorSpoiler],
.pgs-seaslist .tabs-result,
.pgs-seaslist a,
.news-blog,
#svc_new,
#svc_new:focus,
.b-addform textarea,
.b-addform textarea.focus,
.pgs-seaslist span,
form input:focus,
form textarea:focus,
.headblock-search-txt:focus,
.news.img a:nth-child(odd) {
    background-color: #0f0f0f;
    color: #e5d3b9;
}
href,
a,
.pgs-sinfo-actor-title,
.b-taglist-form-lnk,
.pgs-sinfo-actor a,
.pgs-sinfo_list.rating span,
.tabs-close-btn,
.b-addform-rules-link,
.pgs-afterplay [data-tabr=epinfo] b,
.svc_form-rules-link,
.href,
a:hover,
.news-blog-title,
.news a:hover,
.news_s:hover,
.profile-menu a:hover .lside-serial a:hover,
.lside-btn-addon {
    color: #c1b9bc;
    text-decoration: none;
}
.rside .svico-mnotwatch .rside .svico-mblock,
.rside .svico-mwant,
.rside .svico-mwatch,
.rside .svico-mwatched {
    background-color: #ef010185;
}
.middle,
.tabs-result-comments,
.pgs-review,
.news,
.news-head:after,
.news_s,
select,
.lside-blockg .btn_grp span,
.pgs-marks-el a,
.pgs-marks-remove {
    background-color: #434646;
    color: #e5d3b9;
    text-shadow: 0px 0px 0px #fff0;
}


*,
.pgs-marks-name,
.pgs-marks-mess span,
.doptxt a {
    color: bisque;
}
.pgs-marks-bn .pgs-marks-el a {
    background-color: #501a38;
    color: bisque;
}

.svc_info,
.b-pagelist .btn,
.b-pagelist button,
.pgs-seaslist .tabs-title li,
.pgs-seaslist .tabs-title li:hover,
.tabs-title,
.header_txt,
.btn,
.btn_grp,
.pgs-back,
.pgs-back:before,
.poster-top:after,
.b-taglist.dl a,
.pgs-msg.error,
.pgs-head,
.tabs-title li.act,
.tabs-title li:hover,
.news-head,
.news-head:before,
.rside-title li.act,
.rside-title li:hover,
.rside-title li,
.news-blog .b-removebnews,
.lside-in .btn,
.profile-menu-head,
.profile-menu a,
.lside-serial a {
    background-color: #5a5c62;
    color: #e5d3b9;
    background-image: linear-gradient(0deg, #5a5c62 0, #5a5c62);
    text-shadow: 0px 0px 0px #fff0;
}
.tabs-result,
.tabs-title,
.rside-title {
    border: 0px solid #2f2b2c;
}
.svc_info:after,
.svc_info:before {
    border-right-color: #5a5c62;
}
.pgs-back:after,
.pgs-head:after,
.pgs-head:before,
.pgs-afterplay,
.news a:nth-child(odd) {
    background: #000;
    border-right-color: #5a5c62;
}
.header_icon a:last-child,
.header_icon span:last-child {
    background: #5a5c62;
}
.pgs-sinfo-actor-title:after,
.pgs-sinfo-actor-title:before {
    border-top: 2px solid #ac989f;
}
.lside-in.letter,
.rside-result,
.sidebar.rside,
.lside-block {
    background: black;
    border: 0px solid black;
}
.news [class*=" svico-"],
.news [class^=svico-] {
    color: #fff;
}
.lside-serial a:nth-child(2n) {
    background-image: linear-gradient(0deg, #5a5c62 0, #5a5c62);
}
.lside-blockg .btn_grp input[type=checkbox]:checked + span,
.lside-blockg .btn_grp input[type=radio]:checked + span {
    color: #fff;
}
.lside-blockg .btn_grp input[type=checkbox] + span,
.lside-blockg .btn_grp input[type=radio] + span {
    opacity: 1;
}
}
