css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("leaderboard.battleroyalegames.com") {

html{
    background: url(http://www.btcdb.net/img/back-black.jpg) no-repeat center center fixed; 
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}

body{
     background: rgba(0,0,0,0);
     color: #ccc;
     text-shadow: 1px 1px 1px #000;
}

body .navbar{
     text-shadow: none;
}

a, a:link, a:hover, a:active, a:visited{
	color: #08d6f8;
}
     
.panel, .list-group-item, .panel-success > .panel-heading {
    background-color: rgba(0,0,0,0);
}
     
.panel-success {
    border: none;
}    
   
.nav-tabs > li > a, .nav > li > a:hover {
     border: none;
     background-color: rgba(0,0,0,0);
}
     .nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus{border-style: none;}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover,.nav-tabs > li.active > a {
     border-bottom: 3px solid rgba(255,255,255,.75);
     background-color: rgba(0,0,0,0);
}
.panel-default {
    border: none;
}
.panel-default > .panel-heading {
    background-color: rgba(0,0,0,0);
    color: #fff;
}
#rankgraph, #wingraph, #killgraph, #kdrgraph{
     color: #222;
     background: rgba(255,255,255,.1);
     text-shadow: none;
}

.block-title {
    font-weight: bold;
    color: #08d6f8;
}
.btn.btn-default.btn-sm, input.form-control.btn.btn-success{
     color: rgba(0,0,0,1);
     text-shadow: rgba(255,255,255,.5);
     border: 1px solid rgba(0,0,0,.5);
     box-shadow: 2px 2px 2px rgba(0,0,0,.5);
     border-radius: 0px;
     background: rgb(181,189,200); 
     background: -moz-linear-gradient(top,  rgba(181,189,200,1) 0%, rgba(130,140,149,1) 36%, rgba(40,52,59,1) 100%);
     background: -webkit-linear-gradient(top,  rgba(181,189,200,1) 0%,rgba(130,140,149,1) 36%,rgba(40,52,59,1) 100%);
     background: linear-gradient(to bottom,  rgba(181,189,200,1) 0%,rgba(130,140,149,1) 36%,rgba(40,52,59,1) 100%); 
}

.container.banner-margin > .row:first-child > .col-lg-12 > .block{
	min-height: 115px !important;
     border-right: 3px solid rgba(255,255,255,.25);
}
.container.banner-margin > .row:first-child > .col-lg-12 > .col-md-2.block{
     padding-top: 30px;
     border-right: 0;
}

.container.banner-margin > .row + .row > .col-lg-12{
     margin-top: 25px!important;
}

.container.banner-margin > .row + .row > .col-lg-12 > .col-lg-3 > .col-lg-12.block{
         min-height: 253px;
}
.container.banner-margin > .row + .row > .col-lg-12 > .col-lg-3 > .col-lg-12.block > p{
     background: transparent!important;
}

.container.banner-margin > .row + .row + .row > .col-lg-12 > .col-md-5 > .col-lg-12.block-header + .col-lg-12.block,
.container.banner-margin > .row + .row + .row > .col-lg-12 > .col-md-6 > .col-lg-12.block-header + .col-lg-12.block{
     min-height:290px;
}


.col-md-1.modelabel{
	text-align: center;
     background: transparent;
     width: 100%
}

.col-md-1.modelabel > span{
	padding:0 1rem;
     display: inline-block;
     width: auto;
     background: rgba(255,255,255,.5);
     color: #000;
     text-shadow: 1px 1px 0 rgba(255,255,255,.5);
}

.block{
     background: rgba(255,255,255,0);
         border-bottom: 3px solid rgba(255,255,255,.25);
}

.table-striped > tbody > tr:nth-child(odd) {
    background-color: rgba(255,255,255,.05);
}

.table > tbody > tr > td, .table > tfoot > tr > td, .table > tbody > tr > th {
    border-top: 1px solid rgba(255,255,255,.25);
}
.table > tbody > tr.success > td{
	background-color: rgba(255,255,255,.05);
}
.table > tbody > tr.success > td:first-child{
	border-left: 5px solid #f8088a;
}
.container.banner-margin {
    margin-top: 240px;
}
.banner {
    height: 175px;
    background: url("http://www.btcdb.net/img/brBanner.jpg") repeat scroll center center grey;
}

.block-header {
     background: rgba(0,0,0,.25);
	text-shadow: none;
     border-bottom: 2px solid rgba(255,255,255,.25);
}

.stat-nav > li.active > a {
    color: #fff;
}

.stat-nav > li, .stat-nav > li.active {
    border-bottom: 2px solid #bbb;
}
.stat-nav > li.active {
    border-bottom: 2px solid #fff;
}
.stat-nav > li > a:hover,  .stat-nav > li > a{
	color: #fff;
}
.stat-nav > li.active > a:hover, .stat-nav > li.active > a{
	color: #f8088a;
}
.table > thead > tr > th {
    border-bottom: 2px solid rgba(255,255,255,.25);
}

.c3-legend-item-event{
     color: #fff;
}

.label-primary{ background:#08d6f8; }
.text-success{ color: #a6e22e; }
.text-danger { color: #f8088a; }
.lossbox { background: #f8088a; }
.winbox{ background: #a6e22e; } 
.missingbox{ background: #aaa; } 
}
