css:
@-moz-document regexp("http://*.*.*.*/current") {
html {
    height: 100%
}

body {
	font-family: "BlinkMacSystemFont", "Roboto", "Myriad Pro", "Segoe UI", "Verdana";
    background-image: linear-gradient(black, rgba(0,0,255,0.5));/* 2001 era gradient */
}
body:after {
    content: '';    
    display: block;
    height: 100%;
    position: absolute;
    top: 0; left: 0;
    opacity: 0;
    width: 100%;
    z-index: -100;
    background-image: linear-gradient(black, rgba(0,256,128,0.5));/* 2001 era gradient */
    animation: bg2 5s infinite;
    animation-timing-function: linear;
}

.smallerText {    
	margin: 1em auto 1em auto;
}

table {
    border-collapse: collapse; 
    background:rgba(0,0,0,0.25);
}

tr:nth-child(even) {
    background:rgba(128,128,128,.25);    
}

td {
  padding: 2pt 4pt;
}  

@keyframes bg2 {
  0%   { opacity: 1; }
  50%  { opacity: 0; animation-timing-function: ease; }
  100% { opacity: 1; }
}
}
