css:
@-moz-document url("chrome://browser/content/browser.xul") {

[tabsontop="false"] #TabsToolbar {
border-bottom: 1px solid rgba(0,0,0,0.05) !important;
}

#main-window {
-moz-appearance: none !important;
background-color: /*[[mainuicolor]]*/ !important;
}

#navigator-toolbox {
-moz-appearance: none !important;
}

#TabsToolbar {
-moz-appearance: none !important;
background: -moz-linear-gradient(rgba(0,0,0,0.5), transparent) !important;
}

.tabbrowser-tab:-moz-lwtheme {
background-position: -1px, 0 !important;
}

.tabbrowser-tab {
border: 1px solid transparent !important;
-moz-border-image: none !important;
border-radius: 8px !important;
padding: 0 !important;
background: transparent !important;
}

.tabbrowser-tab:not([pinned="true"]) {
margin: 1px 1px 0 1px !important;
}

.tabbrowser-tab:not([selected="true"]) {
color: white !important;
text-shadow: 1px 1px 1px black !important;
}

.tabbrowser-tab:not([pinned="true"]):not([selected="true"]) .tab-text {
color: black !important;
text-shadow: none !important;
background: rgba(255,255,255,0.5) !important;
border-radius: 8px !important;
padding: 2px 4px !important;
}

.tabbrowser-tab[selected="true"] {
color: white !important;
text-shadow: 1px 1px 1px black !important;
}

.tabbrowser-tab[selected="true"] .tab-text {
background: rgba(0,0,0,0.5) !important;
border-radius: 8px !important;
padding: 2px 4px !important;
}

.tabbrowser-tab[selected="true"]:hover {
background: rgba(255,255,255,0.5) !important;
}

#toolbar-menubar {
border-top: 1px solid rgba(0,0,0,0.5) !important;
-moz-appearance: none !important;
background: -moz-linear-gradient(top, rgba(255, 255, 215, 0.5), rgba(255, 255, 215, 0.25), transparent) !important;
}

#main-menubar {
background: rgba(0,0,0,0.5) !important;
margin: 0 0 0 4px !important;
border-radius: 8px !important;
}

#main-menubar > menu:first-child {
padding-left: 8px !important;
margin-left: -4px !important;
}

#main-menubar > menu:not([open="true"]) {
color: white !important;
text-shadow: 1px 1px 1px black !important;
}

#nav-bar,
#PersonalToolbar {
background-color: transparent !important;
}

#nav-bar:-moz-lwtheme,
[tabsontop="false"] #nav-bar {
border-top: 1px solid rgba(0,0,0,0.25) !important;
}

.tabbrowser-tab .tab-background-start,
.tabbrowser-tab .tab-background-middle,
.tabbrowser-tab .tab-background-end {
opacity: 0 !important;
}

}
