} 
/* ------ inline alert --------- */
.gmelius_tracking_alert {
	border-color: transparent !important;
    background-color: #E5EFF5 !important;
}
.gmelius_tracking_alert span[gmelius-act] {
	border-color: transparent !important;
    background-color: #E5EFF5 !important;
    transition: all 350ms ease;
}
.gmelius_tracking_alert span[gmelius-act]:hover {
    background-color: #C6D8EF !important;
}
.gmelius_tracking_detection {
    border-color: transparent !important;
	background-color: #C6D8EF !important;
}

