/* General */
button {
        border-radius: 2px;
    cursor: default;
    font-size: 11px;
    font-weight: bold;
    text-align: center;
    white-space: nowrap;
    margin-right: 16px;
    height: 27px;
    line-height: 27px;
    min-width: 54px;
    outline: 0;
    padding: 0 8px;
    -webkit-box-shadow: none;
    box-shadow: none;
    background-color: #ea4335;
    color: #ffffff;
    border: 1px solid #dcdcdc;
    border: 1px solid rgba(0,0,0,0.1);
}
