css:
@-moz-document domain(nos.nl) {
.os-streams__lists-wrapper {
   padding-top: 0;
}
.os-streams__header {
 position:relative;
    top:auto;
    left: auto;
    right: auto;
	display:none;
}
.os-stream__content {
    padding: 0.3rem 2rem 0.5rem 3.6rem;
    position: relative;
}
.liveblog__update{
 margin:0;   
    padding:20px 0;
}
#content>.article-item-group.js-article-item-group{
    display:none;
}
main article{
    padding-top:0;
}
#content{
    margin-top:0;
}
.os-header.js-header.os-header--init-live{
    padding:0;
    margin-top:58px;
}
.os-navigation.js-os-nav{
        position: absolute;
    top: 64px;
    left: 0;
    right: 0;
}
.os-header__live .os-live__title{
    display:none;
}
.os-live__wrapper.js-wrapper{   
    margin-bottom:0;
}
h1{
    font-size:2em;
}
p{
    max-width:100%;
}
.liveblog__update__time, .liveblog__update__title, .liveblog__elements{
    margin:0;
}
.liveblog__update__time{
 	float:right;   
}
.liveblog-page .article_block{
    margin-left:0px;
    max-width:100%;
}
.liveblog-page .col-10.col-centered.padded-small{
    width: auto;
    margin-left: -40px;
    margin-right: -10px;
}
.liveblog-wrap{
    padding:0;
}
.liveblog__update::before{
    top:22px;
}
.liveblog-page header.article.row.liveblog-header{
 	display:none;   
}
.js-liveblog-update-count{
 	float:right;
    margin-top:20px;
}
.os-header.js-header{
	margin-top:58px;
}
}
