css:
@-moz-document domain("youtube.com") {
.options-renderer-submit-button {
  	border-color: #167ac6 !important;
    background: #167ac6 !important;
    border: solid 1px transparent !important;
    border-radius: 2px !important;
    box-shadow: 0 1px 0 rgba(0,0,0,0.05) !important;
}
body {
	background:0 0
}

#page {
    background: #FFF

}

.yt-card {
    box-shadow: none
}
#footer-main .yt-uix-button,
.comment-section-sort-menu .yt-uix-button,
.yt-uix-button-expander,
.yt-uix-load-more,
.paginator.load-comments {
    background: #d9d9d9;
    border-radius: 0!important;
    border: transparent;
    border-bottom: 1px solid #FFF!important;
    color: #000
}
#footer-main .yt-uix-button:hover,
.yt-uix-load-more:hover {
    background: #bfbfbf
}
.watched-badge {
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    text-align: center;
    line-height: 110px;
    font-size: 24px;
    background: #000;
    opacity: .7;
    font-weight: 200
}
.watched .video-thumb {
    opacity: 1
}
.yt-lockup-meta-info {
    opacity: 0
}
.pl-video-thumb .yt-uix-button,
.thumb-wrapper .yt-uix-button,
.yt-lockup-thumbnail .yt-uix-button,
.yt-lockup:hover .yt-lockup-meta-info {
    opacity: .4
}
.pl-video-thumb .yt-uix-button:hover,
.thumb-wrapper .yt-uix-button:hover,
.yt-lockup-thumbnail .yt-uix-button:hover {
    opacity: 1
}
.video-time {
    opacity: 0;
    bottom: 0;
    right: 0;
    padding: 4px 10px
}
.yt-lockup:hover .video-time {
    opacity: 1
}
.branded-page-module-title {
    font-size: 20px;
    padding-bottom: 24px
}
.branded-page-module-title-text,
.shelf-annotation {
    color: #888;
    font-weight: 200
}
.yt-subscriber-count,
.yt-uix-subscription-button {
    border-radius: 0;
    background: #F2F2F2
}
.branded-page-related-channels .yt-uix-subscription-button {
    background: #B8B8B8!important
}
.branded-page-related-channels .yt-uix-subscription-button:hover {
    background: #CC181E !important;
}
.yt-subscription-button-subscriber-count-branded-horizontal {
    border-color: #B8B8B8 !important;
    background: #B8B8B8;
    border-radius: 0;
    color: #FFF
}
.yt-subscriber-count,
.yt-uix-button-subscribed-branded {
    border-color: #F2F2F2
}
.yt-uix-button-subscribe-branded {
    background: #CC181E
}
.yt-uix-subscription-preferences-button {
    border: 0;
    background: #FFF;
    padding: 0 5px!important
}
#results .branded-page-box,
.branded-page-v2-primary-col .branded-page-box,
.section-list li .item-section .branded-page-box {
    border: 0!important
}
#results .branded-page-box {
    margin: 25px 10px 30px!important;
    padding: 15px;
    background-color: #F2F2F2
}
#results .yt-lockup-video .yt-lockup-description,
.expanded-shelf-content-item .yt-lockup-description {
    opacity: 0;
    color: #777
}
#results .yt-lockup-video:hover .yt-lockup-description,
.expanded-shelf-content-item:hover .yt-lockup-description {
    opacity: 1
}
#comment-section-renderer .comment-renderer-reply {
    opacity: .3
}
#comment-section-renderer .comment-renderer-footer .sprite-dislike,
#comment-section-renderer .comment-renderer-footer .sprite-like {
    opacity: .1
}
.comment-thread-renderer .yt-uix-button {
    background: 0 0
}
#autoplay-checkbox-label,
#autoplay-checkbox-label .toggle {
    border-radius: 0
}
.pl-video,
.search-header {
    border: 0
}
#autoplay-checkbox-label .checked::before {
    margin-top: -2px
}
.yt-uix-checkbox-on-off label {
    background: #CC181E !important;
}
.autoplay-info-icon {
    display: block
}
.yt-lockup-channel {
    background: #F2F2F2;
    margin: 10px 0 5px
}
.branded-page-related-channels-list .yt-lockup-channel,
.yt-ui-ellipsis {
    background: 0 0
}
.yt-lockup-channel .yt-uix-button {
    background: #777;
    color: #FFF;
    padding-left: 5px
}
.yt-lockup-channel .yt-uix-button:hover {
    background: #CC181E;
}
.yt-lockup-channel .yt-subscriber-count {
    background-color: #FFF
}
.yt-badge {
    height: 20px;
    border: 0;
    background-color: #B8B8B8;
    color: #FFF;
    line-height: 20px;
    padding: 0 6px
}
.yt-lockup-channel .yt-badge {
    background-color: #FFF;
    color: #777
}
.pl-video:nth-child(2n+1) {
    background-color: #FAFAFA
}
.channel-header-profile-image-container {
    top: 15px!important
}
.yt-lockup-title a {
    color: #000!important;
    font-weight: 200
}
.yt-lockup-byline a,
.yt-lockup:hover .yt-lockup-byline a {
    color: #999!important
}
#yt-picker-language-footer .yt-uix-button:hover,
.yt-picker-section .yt-picker-item:hover {
    background: 0 0
}
#watch7-sidebar .video-list-item .title {
    color: #000!important
}
.video-list-item .view-count {
    opacity: 0;
    color: #B8B8B8!important
}
.video-list-item:hover .view-count {
    opacity: 1
}
#page {
    margin-top: -1px
}
.pl-video:hover .pl-video-owner a {
    color: #767676
}
.lohp-video-metadata .yt-user-name {
    color: #767676!important
}
.lohp-video-link {
    color: #000;
    text-decoration: none!important
}
.lohp-large-shelf-container:hover .lohp-video-link,
.lohp-medium-shelf:hover .lohp-video-link {
    text-decoration: underline
}
.lohp-video-link:hover {
    text-decoration: underline!important
}
li.guide-section h3 a {
	color: #CC181E
}
.guide-item.guide-item-selected:hover {
	background: #CC181E
}
#action-panel-details:hover a,
#footer-links-primary a:hover,
#footer-links-secondary a:hover,
#yt-picker-language-footer .yt-uix-button,
#watch-discussion .user-name,
#watch-discussion .yt-uix-sessionlink,
#watch-discussion .like-count,
.lohp-large-shelf-container:hover .lohp-video-link,
.lohp-medium-shelf:hover .lohp-video-link,
.pl-video:hover .pl-video-title-link,
.upload-informational-footer a,
.watch-card:hover a,
.yt-picker-section .yt-picker-item,
.yt-uix-expander .load-more-button,
.yt-uix-expander-head,
.video-player-view-component .title a {
    background: 0 0;
    color: #167AC6
}
#results .item-section li:hover .yt-uix-tile-link,
#watch7-sidebar .video-list-item:hover .title,
.pl-video-owner a:hover,
.yt-lockup-video:hover .yt-lockup-title a,
.yt-shelf-grid-item:hover .yt-lockup-title a,
.yt-uix-redirect-link,
.comment-replies-header .load-comments a {
    color: #167AC6!important
}
  
#comment-section-renderer-items .yt-thumb,
#guide-subscriptions-section .yt-thumb,
#yt-masthead-account-picker .yt-uix-button,
#yt-masthead-user .yt-masthead-user-icon,
.branded-page-module-title .yt-thumb,
.channel-header-profile-image,
.channel-header-profile-image-container,
.circled-user-profile .video-thumb,
.watch-card-lockup-container .yt-thumb,
.yt-lockup-channel .yt-thumb,
.user-photo,
#watch-header .yt-thumb {
  /*[[rounded_true]]*/
  border-radius: 500px !important
}
}
