css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("redditlater.com") {
  
  body,
  footer,
  .admin-layout > .container,
  .admin-layout > .sidebar,
  #content
  {
    background-color: black !important;
  }
  
  .admin-layout > .container,
  .admin-layout > .sidebar > ul
  {
    border: grey solid 1px !important;
  }
  
  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  p,
  li,
  nav.sidebar li a,
  .heading,
  .subreddit,
  .date,
  analysis-form > .submit-field > input,
  .button:hover,
  button:hover,
  .button:focus,
  button:focus,
  label,
  .helptext,
  .welcome,
  footer > .container,
  .advertisement-info,
  .stripe-button-el,
  #my-plan > h4,
  #update-payment-info > h4
  {
    color: lightgrey !important;
  }
  
    nav.sidebar li a:hover,
  body.posted li.posted a,
  body.upcoming li.upcoming a,
  body.post-form li.post-form a,
  body.analysis li.analysis a
  {
    background: #4989AF !important;
    color: black !important;
  }
  
  .post:hover
  {
    background: #4989AF !important;
    color: black !important;
  }
  
  .post:hover a
  {
    color: black !important;
  }

    input,
  .admin-layout > .container > .form > form > .field.text-field > textarea,
  form > .plan,
  .pika-title select
  {
    color: #303030 !important;
    background: lightgrey !important;
  }
  
  a
  {
    color: #4989AF !important;
  }
  
  .pika-single,
  .pika-label,
  .pika-title,
  .pika-table,
  .pika-button,
  .pika-table th
  {
    background: black !important;
    color: lightgrey !important;
  }
  
    .pika-prev .pika-button,
  .pika-next .pika-button
  {
    color: lightgrey !important;
    opacity: 1 !important;
    overflow: visible !important;
  }
  
    .pika-prev
  {
  float: left !important;
  background-image:   url('data:image/png;base64,') !important;
    opacity: 0.7 !important;
  }
  
    .pika-next
  {
    float: right !important;
    background-image:  url('data:image/png;base64,') !important;
    opacity: 0.7 !important;
  }
  
    .pika-prev:hover,
  .pika-next:hover
  {
    opacity: 1 !important;
  }
  
  .is-today .pika-button
  {
    background: #4989AF !important;
    color: black !important; 
  }
  
  .is-selected .pika-button
  {
    background: lightgrey !important;
    color: #303030 !important;
    box-shadow: none !important;
    border-radius: 0px !important;
  }
  
  .pika-button:hover
  {
    background: #4989AF !important;
    color: black !important;
    border-radius: 15px !important;
  }
  
    #content > ul > li > code,
  .top-bar > .menu > ul > .authenticated > .button,
  .top-bar > .authenticated > .welcome > .button,
  #content > .splash > p > .button,
  .top-bar > .menu > ul > .unauthenticated > .button
  {
    color: black !important;
  }

}
