css:
/* 
Name:    Dark Fofo A-U
Author:  Elkisab
Version: 0.8 (22/09/16)
Site: http://forum.anime-ultime.net/
*/

@-moz-document domain("forum.anime-ultime.net") {

body {
    color : #dcdcdc;
    background-image : url("/*[[background]]*/");
}
dd {
    color : darkgreen
}
.author{
    color : darkred
}
.forabg {
    background-color : black
}
.inner {
    color : darkred;
}
a {
    color : black;
    
}
pre {
    color : white
}
#site-description {
    float : right
}
fieldset {
    display : block
}
.navbar {
    background-color : black
}
#logo {
  display: block;
  box-sizing: border-box;
  background: url("/*[[logo-img]]*/") no-repeat center;
  overflow: hidden;
  padding-top: 147px;
  width: 450px; 
  height: 150px;
  padding-left: 450px; 
}
#search-box #keywords{
    width: 120px;
    height: 20px;
    background-color: black;
}
input.search{
    background-image: url("https://lh6.ggpht.com/HX2sssK8l-Za0DiDMVNQL0zzVMpv9kuIyQwdTNBdU72v0BoSPmAyPtzzJUtxV-1JA8ZeimQ=w20");
    padding-left: 25px;
}
.inputbox:focus{
    color: white;
    border-color: darkred;
}
.inputbox:hover{
    color: gray;
    border-color: darkred;
}
.reply-icon span{
    background-image: url("http://images4.hiboox.com/images/3108/65898d45ba7fb3457fe4c12577a6467b.gif");
}
.post-icon span{
    background-image: url("http://i85.servimg.com/u/f85/13/15/08/49/button11.png");
}
.forabg{
    background-color: #030e19;
    background-image: url("/*[[background]]*/");
}
.linklist {
    background-image : url("/*[[background]]*/")
}
.panel {
    background-image : url("/*[[background]]*/")
}
.headerbar {
    background-image : url("/*[[background]]*/")
}

html{
    background-image : url("/*[[background]]*/")
}

.pagination span a:active{
    border-color: darkred;
}
.pagination span a,
.pagination span a:link,
.pagination span a:visited{
    border-color: darkred
}
a.button1,
input.button1,
input.button3,
a.button2,
input.button2{
    color: white;
    background-color: darkred;
    background-image: url("https://puu.sh/qFQHD.gif");
}
.navbar,
.navbar a:link,
.navbar a:visited{
    background-color: rgba(0, 0, 0, 0.1);
    color: #c9dfeb;
}
table.table1 td{
    color: darkgreen;
}
dl.details dd{
    color: darkgreen;
}
dl.details dt{
    color: #dcdcdc;
}
.newpm-icon span{
    background-image: url("http://i85.servimg.com/u/f85/13/15/08/49/button11.png");
}
.pmreply-icon span{
    background-image: url("http://puu.sh/rk4FI.gif");
}
.forwardpm-icon span{
    background-image: url("http://puu.sh/rk5cA.gif");
}
.locked-icon span{
    background-image: url("http://puu.sh/rk5MC.gif");
}
}
