css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("iptorrents.com"), domain("iptorrents.ru"), domain("iptorrents.me"), domain("iptorrents.eu"), domain("iptorrents.us") {

a[href$="/donate.php"] img { display: none; } a[href*="/donate.php"]{display: none;}
a[href$="/donateBTC.php"] img { display: none; } a[href*="/donateBTC.php"]{display: none;}
html > body > center > p[align="center"] { display: none !important; }
html > body > center > table[width*="1000"] { display: none !important; }
html > body > center > table.t0 tbody tr:nth-child(6) { display: none !important; }

/* remove shit banner */
#iptStart  .banner {
    background-image: none !important;
    height: 70px;
    border-top-left-radius: 24px;
    border-top-right-radius: 24px;
-webkit-box-shadow: inset 0px 0px 8px 4px rgba(95,95,95,1), 0px 0px 4px 0px rgba(255,191,191,1);
-moz-box-shadow: inset 0px 0px 8px 4px rgba(95,95,95,1), 0px 0px 4px 0px rgba(255,191,191,1);
box-shadow: inset 0px 0px 8px 4px rgba(95,95,95,1), 0px 0px 4px 0px rgba(255,191,191,1);
    border: 1px solid rgb(127, 127, 127);
  }
html body table#body tbody tr td h2 {
    display: none !important;
    width: 0; height: 0;
}
html body div#iptStart div.butRow a span {
    box-shadow: 0 3px 3px rgba(255,191,191,1);
    border: 1px solid rgba(63,63,63,1);
}
#blockblockA, .adsbygoogle {
    display: none !important;
}
}
