css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("square-enix.com") {
  html, body { background-color: #000C17 !important; color: #777 !important; }
  .title, h1, h2, h3, h4, h5, h6 { color: #00ACAF !important; }
  a:link { color: #00ACAF !important; }
  a:hover { color: cyan !important; }
  /* --------- misc images -------- */
  hr { background-color: #00ACAF !important; }
  .wrap-title { background-image: url("http://i.imgur.com/dIdTqD6.png") !important; }
  #block-user-avatar .button-settings .icon { background-image: url("http://i.imgur.com/Dz57RG9.png") !important; }
  #games-filters div.filter .label .drop, #games-filters div.filter .label:hover .drop  { background-image: url("http://i.imgur.com/s3I6VMX.png") !important; background-color: transparent !important; }
  
  
  /* ======== main page ======== */

  /* top bar */
  #membership-bar-header { color: #00ACAF !important; } /* text color */
  #membership-bar-header-bg { background-color: #000C27 !important; border-bottom-color: #00ACAF !important; } /* bg color */
  /* main page logo - bad css designers!!!! */
  #membership-bar-header-bg #membership-bar-header #se-logo img[src="https://eu.square-enix.com/sites/all/themes/custom/bleach/images/square_logo.png"] {
    padding-left: 175px !important;
    padding-top: 18px !important;
    background: transparent none no-repeat left center !important;
    width: 0px !important;
    height: 0px !important;
    background-size: contain !important;
  } /* */
  #membership-bar-header-bg #membership-bar-header #se-logo { background: transparent none !important; }
  #membership-bar-header-bg #membership-bar-header #se-logo { background: transparent url("http://i.imgur.com/UVLHJQ4.png") no-repeat left !important; }
  
  /* user menu */
  #user-area .dropdown, #user-area .user-pieces:hover .dropdown { background-image: url("http://i.imgur.com/dPtfDs7.png") !important; } /* user menu icon */
  #user-area .dropdown-box::before { border-color: transparent transparent rgb(0,12,103) transparent !important; } /* user menu arrow */
  #user-area .dropdown-box { background-color: rgb(0,12,103) !important; border-color: rgb(0,12,103) !important; } /* user menu bg color */
  #membership-bar-header a { color: #00ACAF !important; } /* user menu text color */
  #user-area .dropdown-box a:hover { background-color: #000C17 !important; } /* user menu hover color */
  /* search bar */
  .search-form .container-inline { border-color: #00ACAF !important; background-color: #000C17 !important; } /* search bar bg/border */
  .search-form input[type="text"] { color: #00ACAF !important; } /* search text color */
  .search-form input[type="submit"] { /* search icon */
    background-image: url("http://i.imgur.com/Dz57RG9.png") !important;
    background-color: #000C17 !important;
  }
  .search-form input[type="submit"]:hover { background-color: #000C67 !important; }
  
  /* main tabs */
  #page { background-color: #000C27 !important; box-shadow: #00ACAF 0 0 2px !important; } /* page color */
  #secondary-menu ul.main-menu li.menu-home a { background-image: url("http://i.imgur.com/Dz57RG9.png") !important; background-color: #000C17 !important; } /* home icon */
  #secondary-menu ul.main-menu li.menu-home a:hover { background-color: #000C67 !important; } /* home icon hover bg */
  #secondary-menu ul.main-menu li a:hover { color: #00ACAF !important; background-color: #000C67 !important; } /* tab text hover color */
  #secondary-menu ul.main-menu li.active a { background-color: #000C67 !important; } /* main tab selected bg */
  #secondary-menu ul.main-menu li a { color: #00ACAF !important; } /* tab text color */
  
  /* content */
  .content .home-block-container:hover { background-color: #000C67 !important; }
  #block-blogcarousel .content:hover { background-color: #000C37 !important; }
  .es-carousel li:hover { background-color: #000C67 !important; }
  .home-block-container .block-text-title { border-bottom-color: #00ACAF !important; }
  
  /* footer stuff */
  .bottom-blocks { background-color: #000C37 !important; }
  .footer-links { background-color: #000C27 !important; }
  .footer-text { background-color: #000C37 !important; color: #00ACAF !important; }
  
  /* ======== profile page ======== */
  #block-user-avatar .button-settings { background-color: #000C17 !important; border-color: #00ACAF !important; } /* settings btn */
  #block-user-avatar .button-settings:hover { background-color: #000C47 !important; } /* settings btn hover */
  #block-user-avatar { background-color: #000C47 !important; } /* top user info bg */
  #addgame { background-color: #000C17 !important; border-color: #00ACAF !important; } /* add game btn */
  #addgame:hover { background-color: #000C67 !important; } /* add game btn hover */
  #addgame .icon { background-image: url("http://i.imgur.com/Dz57RG9.png") !important; } /* add gam btn icon */
  #block-followed-games { background-color: #000C47 !important; } /* stuff? */
  
  /* ======== games page ======== */
  #games-filters div.filter .label { border-color: #00ACAF !important; } /* filter border */
  #games-filters div.filter .label:hover { background-color: #000C67 !important; } /* filter on hover */
  #games-filters div.filter .label ul.links { background-color: #000C17 !important; border-color: #00ACAF !important; } /* filter popup menu */
  #games-filters div.filter .label ul.links li:hover { background-color: #000C67 !important; } /* filter popup hover item */
  #games-index li .more { background-image: url("http://i.imgur.com/s3I6VMX.png") !important; background-color: transparent !important; } /* content arrow icon */
  #games-index li:hover { background-color: #000C67 !important; } /* content bg on hover */
  .title:hover { color: cyan !important; } /* content title on hover */
  #games-more { border-color: #00ACAF !important; color: #00ACAF !important; } /* more games btn */
  #games-more:hover { background-color: #000C67 !important; } /* more games btn hover */
  
  /* ======== blog page ======== */
  .content #blog-filters:hover { background-color: #000C27 !important; } /* filter hover fix because of above css */
  #blog-filters div.filter .label .drop { background-image: url("http://i.imgur.com/s3I6VMX.png") !important; background-color: transparent !important; } /* filter arrow */
  #blog-filters .label { border-color: #00ACAF !important; } /* filter border */
  #blog-filters .label:hover { background-color: #000C67 !important; } /* filter hover */
  .node-blogs .sub-content, .node-news .sub-content { border-color: #00ACAF !important; } /* content header */
  .content p { color: #777 !important; } /* content text color */
  .node-blogs .meta .vocabulary a, .node-news .meta .vocabulary a { background-color: #000C47 !important; } /* related products bg */
  
  /* ======== Forum ======== */
  #membership-bar-header-bg #membership-bar-header #se-logo { background: transparent url("http://i.imgur.com/UVLHJQ4.png") no-repeat !important; } /* logo again */
  .forumrow.table:hover { background-color: #000C47 !important; }
 
  /* search box again */
  input[src="images/buttons/search.png"] { padding-left: 22px !important; padding-top: 22px !important; background: transparent url("http://i.imgur.com/5osTpxh.png") no-repeat !important; width: 0px !important; height: 0px !important; background-size: contain !important; } /* search icon */
  #navbar_search input { background-color: transparent !important; color: #00ACAF !important; } /* stupid text box bg ... should be trans */
  div.above_body div.globalsearch span.textboxcontainer { border-color: #00ACAF transparent #00ACAF #00ACAF !important; } /* search box - text */
  div.above_body div.globalsearch span.buttoncontainer { border-color: #00ACAF #00ACAF #00ACAF #00ACAF !important; background-color: transparent !important; } /* searh btn borders */
  form#navbar_search .buttoncontainer input { border-color: transparent !important; } /* fucking inconsistent CSS for search stuff */
  /* ==== main tabs / nav bar ==== */
  .menu-home a { background: transparent url("http://i.imgur.com/Dz57RG9.png") 0 -179px no-repeat !important; height: 27px !important; width: 35px !important; text-indent: -119988px !important; padding-right: 10px !important; }
  div.above_body ul.main-menu li a:hover { background-color: #000C67 !important; } /* main tabs hover bg */
  /* stupid CSS design */
  div.above_body div.navbar ul.navtabs li ul li a.popupctrl.active { background-color: #000C67 !important; border: none !important; } /* unnecessary border on ACTIVE */
  div.above_body div.navbar ul.navtabs li ul li a.popupctrl:hover { border: none !important; background-color: transparent !important; } /* unnecessary borders on HOVER */
  /* real css remaining */
  div.above_body { background-color: #000C27 !important; border-color: transparent !important; } /* main img + tabs -- top */
  div.above_body div.navbar { background-color: #000C27 !important; } /* nav bar */
  div.above_body div.navbar ul.navtabs li ul li:hover { background-color: #000C67 !important; } /* all tabs hover color */
  div.above_body div.navbar ul.navtabs li ul li ul.popupbody { background-color: #000C17 !important; border-color: #00ACAF !important; } /* tab popup menu */
  div.above_body div.navbar ul.navtabs li ul li ul.popupbody li a { background-color: transparent !important; } /* popup menu item bg color */
  
  /* ==== forum body ==== */
  /* splash */
  div.above_body div.splash {
    background-image: url("http://i.imgur.com/rYalXx6.png") !important;
    background-size: contain !important;
    background-repeat: no-repeat !important;
    background-position: center !important; 
    height: 100px !important;
  }
  
  /* notices */
  div.body_wrapper { background-color: #000C27 !important; border-color: transparent !important; } /* main body/pg bg */
  .notices li { background-color: #000C47 !important; color: cyan !important; border-color: #00ACAF !important; } /* notice colors */
  .notices input[alt="Dismiss"] { border-color: #00ACAF !important; } /* notice "X" border */
  /* notice close "X" */
  input[src="images/misc/cross.png"] {
    padding-left: 16px !important;
    padding-top: 16px !important;
    background: transparent url("http://i.imgur.com/yPJa954.png") no-repeat !important;
    width: 0px !important; height: 0px !important;
    background-size: contain !important;
  } /* close "X" */

  /* breadcrumbs */
  div.body_wrapper div.breadcrumb { background-color: #000C37 !important; } /* bg */
  div.body_wrapper div.breadcrumb ul li { color: #00ACAF !important; } /* breadcrumb color */
  
  /* BAD CSS designers! */
  ol#forums li.forumbit_nopost ol li.forumbit_post div.forumrow div.foruminfo, ol#forums div.forumbits ol li.forumbit_post div.forumrow div.foruminfo { height: 100% !important; }
  
  /* main forum content / headers */
  ol#forums li.forumbit_nopost, ol#forums div.forumbits { background-color: #000C47 !important; } /* sub forum bg color */
  ol#forums li.forumbit_nopost ol, ol#forums div.forumbits ol { background-color: #000C17 !important; } /* sub form content bg color */
  .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow { color: #777 !important; } /* sub forum topic preview text color */
  ol#forums li.forumbit_nopost ol li.forumbit_post div.forumrow div.forumlastpost, ol#forums div.forumbits ol li.forumbit_post div.forumrow div.forumlastpost { background-color: #000C47 !important; } /* odd/last post */
  .time { color: white !important; } /* post time */
  ol#forums li.forumbit_nopost ol li.forumbit_post div.forumrow div.forumpostcount, ol#forums div.forumbits ol li.forumbit_post div.forumrow div.forumpostcount { background-color: #000C47 !important; } /* post count */
  div.wgo_block div.blockbody div.wgo_subblock div { background-color: #000C47 !important; } /* silly filters and forum info */
  .wgo_block .section { color: #777 !important; } /* silly filter/forum info text color */
  /* forum actions - mark read - view leaders */
  div.navlinks a { background-color: #000C47 !important; border-color: transparent !important; color: #00ACAF !important; } /* forum actions */
  
  /* forum collapse icons */
  img[src="images/buttons/collapse_40b.png"] { padding-left: 13px !important; padding-top: 13px !important; background: transparent url("http://i.imgur.com/dIHnSJZ.png") no-repeat !important; width: 0px !important; height: 0px !important; background-size: contain !important; }
  img[src="http://forums.eu.square-enix.com/images/buttons/collapse_40b_collapsed.png"] { padding-left: 13px !important; padding-top: 13px !important; background: transparent url("http://i.imgur.com/cbEpF9u.png") no-repeat !important; width: 0px !important; height: 0px !important; background-size: contain !important; }

  /* forum footer */
  div.body_wrapper div.footer { background-color: #000C47 !important; }
  form select { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  
  /* pagination */
  div.above_threadlist div.threadpagenav form.pagination, div.below_threadlist div.threadpagenav form.pagination { border-color: #00ACAF !important; }
  
  /* thread list controls */
  div.above_threadlist_controls { background-color: #000C47 !important; } /* bg */
  div.body_wrapper .popupbody { background-color: #000C17 !important; border: 1px solid #00ACAF !important; } /* popup bg */
  .popupbody li { border-top-color: #00ACAF !important; }
  .popupbody li a, .popupbody li label { background-color: transparent !important; }
  .popupbody li input.searchbox { color: #00ACAF !important; }
  form input { background-color: #000C17 !important; border-color: #00ACAF !important; }
  .popupbody li.formsubmit .submitoptions label { color: #777 !important; }
  div.body_wrapper .button { background-color: #000C17 !important; color: #00ACAF !important; border-color: #00ACAF !important; }
  div.body_wrapper .button:hover { background-color: #000C67 !important; }
  /* BAD CSS designers!!! */
  .toolsmenu .popupgroup a.popupctrl, .toolsmenu .popupgroup .popupmenu a.popupctrl, .toolsmenu .popupgroup .popupmenu:hover a.popupctrl { padding: 5px !important; }
  
  /* ==== sub forum content ==== */
  div.threadlist form#thread_inlinemod_form, div.threadlist { background-color: #000C27 !important; }
  div.threadlist form#thread_inlinemod_form div.threadlisthead { background-color: #000C47 !important; } /* thread column headers */
  div.threadlist form#thread_inlinemod_form ol.announcements li.announcerow div.announceicon { background-color: #000C47 !important; } /* info icon bg */
  div.threadlist form#thread_inlinemod_form ol.announcements li.announcerow div.announceinfo { background-color: #000C47 !important; } /* sub forum view count */
  /* all threads */
  .threadbit, .postbit .postdate .time, .date { color: #00ACAF !important; }
  #thread_inlinemod_form li:hover { background-color: #000C67 !important; }
  /* sticky */
  div.threadlist form#thread_inlinemod_form ol.stickies li.threadbit div.sticky div.threadinfo a.threadstatus { background-color: #000C47 !important; } /* thread icon bg */
  div.threadlist form#thread_inlinemod_form ol.stickies li.threadbit div.sticky dl.threadlastpost { background-color: #000C47 !important; } /* thread last post info */
  div.threadlist form#thread_inlinemod_form ol.stickies li.threadbit div.sticky div.threadviews { background-color: #000C47 !important; } /* thread views */
  /* not sticky */
  div.threadlist form#thread_inlinemod_form ol.threads li.threadbit div.nonsticky div.threadinfo a.threadstatus { background-color: #000C37 !important; } /* non sticky icon */
  div.threadlist form#thread_inlinemod_form ol.threads li.threadbit div.nonsticky dl.threadlastpost { background-color: #000C37 !important; } /* last post info */
  div.threadlist form#thread_inlinemod_form ol.threads li.threadbit div.nonsticky div.threadviews { background-color: #000C37 !important; } /* view count */
  /* forum footer stuff */
  div.threadlist form#thread_inlinemod_form div.forumfoot { background-color: #000C47 !important; }
  div.body_wrapper .blockhead { background-color: #000C47 !important }
  div.body_wrapper .blockbody { border-color: #000C47 !important; }
  div.forum_info form.blockbody { border-color: #000C47 !important; }
  /* lots of button crap */
  div.body_wrapper .navpopupmenu.popupmenu.nohovermenu a.textcontrol, div.body_wrapper .navpopupmenu a.textcontrol, div.body_wrapper .navpopupmenu a.popupctrl, div.body_wrapper .navpopupmenu.popupmenu.nohovermenu a.textcontrol, div.body_wrapper .navpopupmenu.popupmenu.nohovermenu a.popupctrl, div.body_wrapper .cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_ok, div.body_wrapper .cke_skin_kama .cke_dialog_contents a.cke_dialog_ui_button_cancel, div.body_wrapper .textcontrols a, div.body_wrapper a.textcontrol, div.body_wrapper #inlinemod_formctrls a.popupctrl, div.body_wrapper .actionbuttons .group .button, div.body_wrapper .button {
    background-color: #000C17 !important;
    border-color: #00ACAF !important;
    color: #00ACAF !important;
  }
  .forum_info { color: #777 !important; }
  /* thread view */
  div.thread_controls { background-color: #000C47 !important; }
  div.postlist ol.posts li.postbit { background-color: #000C27 !important; }
  div.postlist ol.posts li.postbit:hover { background-color: #000C57 !important; }
  div.postlist ol.posts li.postbit div.postbody { background-color: transparent !important; } /* post body */
  .postbit .userinfo, .postbit .userinfo_noavatar { background-color: transparent !important; }
  div.postlist ol.posts li.postbit div.postuser { background-color: transparent !important; border-color: #000C37 !important; }
  .signature { border-top-color: #00ACAF !important; }
  .postbit .posttitle, .postbitlegacy .title { border-bottom-color: #00ACAF !important; }
  /* missed text bits */
  div.postlist ol.posts li.postbit div.postuser div.userinfo div.userinfo_extra dl, div.postlist ol.posts li.postbit div.postuser div.userinfo_noavatar div.userinfo_extra dl { color: #777 !important; }
  .postbit, .postbitlegacy, .eventbit { color: #777 !important; }
  div.postlist ol.posts li.postbit div.postbody div.postrow blockquote.postcontent { color: #777 !important; }
  .postbit .postbody, legend { color: #777 !important; }
  /* next prev thread btns */
  div.navlinks.postnl a { background-color: #000C17 !important; border-color: #00ACAF !important; color: #00ACAF !important; }
  div.navlinks.postnl a:hover { background-color: #000C47 !important; }
  
  /* ==== forum profile ==== */
  .textbox, textarea, select { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #00ACAF !important; }
  dl.stats dt, #sidebar_container dl.stats, #sidebar_container .blockbody.userprof_content, #sidebar_container .userprof_content .time, .userprof_content .time { color: #777 !important; }
  /* edit box */
  .userprof_moduleinactive { background-color: #000C17 !important; }
  .cke_skin_kama .cke_wrapper.cke_ltr, .cke_skin_kama .cke_wrapper.cke_rtl { background-color: #000C27 !important; }
  .texteditor.content .cke_skin_kama textarea.cke_source, .texteditor textarea { background-color: #000C17 !important; color: #777 !important; border-color: #00ACAF !important; }
  span.cke_skin_kama { border-color: #00ACAF !important; }
  .profile_editor_border, .profile_content .actionbuttons { border-color: #00ACAF !important; }
  .activitystream_block dd.selected, dd.userprof_module { background-color: #000C47 !important; }
  .memberprofiletabunder { background: #000C47 none !important; }
  .formcontrols .blockrow { border-color: transparent !important; }
  .activitystream_block dd, dd.userprof_moduleinactive { border-color: #00ACAF !important; }
  /* inbox */
  #usercp_nav .blockbody { background-color: #000C27 !important; }
  #usercp_nav .blockrow * a:hover { background-color: #000C47 !important; }
  div.body_wrapper .blockbody ul.blockrow li.active { background-color: #000C67 !important; }
  #usercp_nav hr { color: #00ACAF !important; }
  div.body_wrapper #forum_icon_legend h5 { background-color: #000C47 !important; }
  .blockfoot, .blocksubfoot { background-color: transparent !important; }
  div.body_wrapper .blockbody .blocksubhead { background-color: #000C47 !important; }
  .blocksubhead { border-color: #00ACAF !important; }
  
  /* sub sub forums? */
  .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow, .forumhead + .L2 .forumrow, .forumhead + .childforum .L2 .L2:first-child .forumrow, .forumhead + .childforum .L2:first-child .L2 .forumrow, .forumhead + .childforum .L2:first-child .forumrow, .forumrow + .childsubforum .forumrow { background: #000C17 none !important; }
  .forumrow:hover, .foruminfo:hover { background-color: #000C47 !important; }
  .threadbit .sticky, .threadbit .nonsticky { background-image: none !important; background-color: #000C27 !important; }
  .threadbit .sticky:hover, .threadbit .nonsticky:hover { background-color: #000C47 !important; }
  a#newthreadlink_top.newcontent_textcontrol { color: transparent !important; }
  
  /* more sub forums */
  div.body_wrapper li.forumbit_nopost, div.body_wrapper div.forumbits { background-color: #000C47 !important; }
  div.body_wrapper li.forumbit_nopost ol, div.body_wrapper div.forumbits ol { background-color: #000C17 !important; }
  div.body_wrapper li.forumbit_nopost ol li.forumbit_post div.forumrow div.forumlastpost, div.body_wrapper div.forumbits ol li.forumbit_post div.forumrow div.forumlastpost { background-color: #000C27 !important; }
  div.body_wrapper li.forumbit_nopost ol li.forumbit_post div.forumrow div.forumpostcount, div.body_wrapper div.forumbits ol li.forumbit_post div.forumrow div.forumpostcount { background-color: #000C27 !important; }
  
}
