css:
@-moz-document domain("boards.4chan.org"), domain(boards.4channel.org){
/* Name:  NeonBlackChan
 * Author:  bbang
 * Version: 1.0
 */  

/* works best with tomorrow theme */



/* home page */


div.box-outer {
    background-color: #00080f !important ;
    color: #b6ffa6 !important ;
    border-style: solid !important;
    border-color: #2a262b !important ;
}

div.boxbar h2 {
    background-color: #000000 !important ;
}

html body {
    background-color: #000010 !important ;
    height: 0  !important;
}

html {
    background-color: #08080a!important ;
    color: #000000 !important;
}

#logo-fp a {
    background-color: #08080a !important;
    color: #08080a !important;
}

#bd {
    background-color: #08080a !important ;
    border-color: #a1a1a1 !important;
}

#ft ul li {
    background-color: #08080a ;
}

div.boxcontent {
    border-style: none ;
}



/* unvisited link */
a:link {
    color: #70c24d !important;
}

/* visited link */
a:visited {
    color: #44d24c !important;
}

/* mouse over link */
a:hover {
    color: hotpink !important;
    border: none !important;
}


/* board background for tomorrow theme */

body.is_thread.board_v.tomorrow.ws {
    background-color: #08080a;
}    
div.teaser b {
    color: #a3cc99;
}

div.thread {
    background-color: #080808;
    
 /* miscellaneous */
    
}
blockquote.postMessage {
    color: #878567 !important;
}

span.boardList {
    color: #5e5e5e;
}
div.desktop {
    color: #8e918e;
}

div.desktop {
    color: #8e918e;
}

a.quotelink {
    color: #70c24d;
}

div.post.reply {
    background-color: #000;
}

div.post.op.preview.inlined {
    background-color: #000;
}

div.postInfo.desktop {
    color: #878567 !important;
}

span.postNum.desktop a {
    color: #878567 !important;
}

span.name {
    color: #878567 !important;
}

span.absBotDisclaimer {
    color: #878567;
}

#footer-links a {
    color: #70c24d !important;
}

div.post div.postInfo span.postNum>a {
    color: #b3b08c !important;
                                  
}
div.fileText {
    color: #878567;
}

td.blotter-content {
    color: #878567;
}

div.boardTitle {
    color: #878567;
}

div.globalMessage.hideMobile b {
    color: #878567 !important;
}

td.blotter-date {
    color: #878567 !important;
}

td.blotter-content a {
    color: #70c24d !important;

}    

a.quotelink:hover {
    color: #6e0000 !important;
}

div.post.reply {
    background-color: #000 !important;
}

a.quotelink {
    color: #44c24d !important;
}

span.quote {
    color: #76e3a0 !important;
     font-style: italic !important;
}

/* Scrollbar */

*::-webkit-scrollbar {
    background-color: rgba(10,10,11, .2) !important;
    width: 4px !important;
    height: 10px ! important;
    border: 0px solid #0a0a0a ! important;
}

*::-webkit-scrollbar-button {
display: none ! important;
}

*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
border: 0 ! important;
box-shadow: none ! important;
}

*::-webkit-scrollbar-thumb {
    max-height: 12px ! important;
    background-color: #4b7550 !important;
}

*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
background: rgba(0,0,0, 0) ! important;
 
}    
   

body.ws.is_catalog.board_v.tomorrow {
    color: #878567;
    background-color: #08080a;
}

span.button {
    color: #70c24d;
}

span.deadlink {
    color: #570000 !important;
}
 div#footer-links a{
   color: #70c24d !important;
 }              
span.quote {
    color: #76e3a0;
}

  html {
    background-color: #08080a;
}

div.boardBanner {
    background-color: #08080a;
}

div.desktop {
    background-color: #08080a;
}

div.topad.center.ad-cnt {
    background-color: #08080a;
}

body.is_thread.board_v.yotsuba_new.ws {
    background-color: #08080a;
}

/* persistant central board list */

span.customBoardList {
   
    color: #6b6b6b;
    position: fixed;
    left: 13.5%;
    background-color: rgba(0,0,0, .77);
}


body.ws.is_catalog.board_v.yotsuba_b_new {
    background-color: #08080a !important;
    color: #878567 !important;
}

body {
    background-color: #08080a !important;
	color: #878567 !important;
}
 
/* image hover */

 form a img {
        opacity: .8 !important;
              
}
   
form a img:hover {
        opacity: 1 !important;
}       

img.thumb {
        opacity: .5 !important;
}

img:hover {
        opacity: 1 !important;
}
    
img {
	opacity: .899 !important;
}  

/*inline quotes*/


div.post.reply.preview.inlined {
    border-style: solid !important;
    border-color: #6e0000 !important;
}

div.post.reply {
    border-style: solid !important ;
    border-width: 1px !important;
    color: #6e0000 !important;
}



#order-ctrl {
    background-color: #000;
    color: #878567;
}

#size-ctrl {
    background-color: #000;
    color: #878567;
}

#teaser-ctrl {
    background-color: #000;
    color: #878567;
}

#styleSelector {
    background-color: #000;
    color: #878567;
}

/* external links */

a.linkified {
    background-color: #3b007a;
    color: #9ee8f2;
    border-style: solid;
    border-width: 1px;
    border-top-width: 0;
    border-right-width: 0;
    border-color: #006c70;
}
div.post.reply.highlight {
    border-color: #6e0000 !important;
   
}   

/* spoilers */

div.teaser s {
    border-width: 1px;
    border-style: dotted;
    border-color: #6e0000;
    border-top-width: 0;
    border-left-width: 0;
    border-right-width: 0;
    background-color: #000;
}

blockquote.postMessage s {
    border-style: dotted;
    border-color: #6e0000;
    border-width: 1px;
    border-top-width: 0;
    border-left-width: 00;
    border-right-width: 0;
}



}
