css:
@-moz-document url-prefix("https://students.open.ac.uk/dashboard/"), url-prefix("https://students.open.ac.uk/studydashboard/") {

 /* light pink background on forums with unread messages */
.status-unread-unread {
    background-color: #FFF0FF;
}

/* hide ugly icons */
.box-contents li .icon img,
.box-contents li .modified {
    display: none;
}

/* move intrusive customise button to bottom */
.topstrip {
    position: absolute;
    top: 96.5%;
    width: 100%;
}

/* move intrusive help link to bottom */
.viewhelplink {
    position: absolute;
    bottom: -6%;
    left: 19%;
}

/* move key over to accommodate customise buton */
.key {
    float: right;
}

/* replace envelope icon with a more noticeable yellow one */
.status-unread-unread img.status-unread-unread,
.key li.k2 img {
    content: url(https://msds.open.ac.uk/tutorhome/css/icon-contact.png);
    width: 16px;
    height: 12px;
    padding-top: 1px;
    padding-left: 2px;
    padding-right: 4px;
}

/* smaller list text */
.box-contents li {
    font-size: 0.9em;
    margin: 0 0 3px 0px;
}

/* smaller icons in headers */
.box-header .box-header-inner img.box-icon,
.box-header .expandcollapse {
    vertical-align: middle;
    width: 15px;
    height: 15px;
}

/* smaller header text */
.box-header h2 {
    font-size: 0.9em;
    padding-right: 20px;
}

/* less padding in header boxes */
.box-header .box-header-inner {
    padding: 5px 5px 5px 10px;
    margin-top: 10px;
}

/* smaller header boxes */
.box-header {
    min-height: 20px;
}

/* light blue box backgrounds */
.box-contents {
    background-color: #e5e5f3;
}

/* nudge updating indicator to avoid newly moved elements */
#updating
{
	margin-top: 0px;
}
  
}
