css:
@-moz-document url-prefix("https://www.youtube.com/watch") {
    #watch7-container {
        display: none;
    }
    #footer-container {
        display: none;
    }
    #content-container,
    #body-container {
        padding-bottom: 0 !important;
    }
    #placeholder-player {
        margin-bottom: 0 !important;
    }
    .appbar-hidden #masthead-positioner-height-offset {
        display: none;
    }
    #masthead-positioner {
        display: none;
    }
    body {
        overflow: hidden;
        background: black;
    }
    #watch-appbar-playlist {
        display: none;
    }
}
