css:
@-moz-document domain("fiestastoc.com") {
    body {
        background: #505050;
        color: #cacaca;
    }
    a {
        color: #cacaca;
        font-weight: bold;
    }
    a:hover {
        color: #b9b9b9;
        font-weight: bold;
    }
    #branding {
        background-color: #505050;
    }
    #user_navigation {
        background-color: #727272;
        border: 1px solid #616161;
    }
    #user_navigation .user_photo {
        border: 1px solid #616161;
    }
    #user_link_menucontent,
    #user_navigation a#user_link.menu_active,
    #user_navigation a#notify_link.menu_active,
    #user_navigation a#inbox_link.menu_active {
        background-color: #727272;
        color: #fff;
    }
    #user_link_menucontent #links a {
        color: #b9b9b9;
    }
    .ipsBox,
    .ipsPad {
        padding: 0px;
    }
    .border,
    .statistics,
    .post_block,
    .ipsComment,
    .popupInner,
    .no_messages,
    .poll_question ol,
    .ipsBox_container,
    .ipsFloatingAction,
    .column_view .post_body {
        background-color: #848484;
        color: #cacaca;
    }
    .desc,
    .desc.blend_links a,
    p.posted_info {
        color: #cacaca;
    }
    #recentajaxcontent li,
    #idm_categories a,
    #index_stats .status_list li,
    #panel_files .file_listing li,
    #panel_screenshots #ss_linked li,
    .file_listing,
    #cart_totals td,
    div#member_filters li,
    #files li,
    .ipsType_sectiontitle,
    #order_review td,
    #package_details .package_info,
    .block_list li,
    .package_view_top,
    .member_entry,
    #help_topics li,
    .ipsBox_container .ipsType_pagetitle,
    .userpopup dl,
    #announcements td,
    .preview_info,
    .sideVerticalList li,
    fieldset.with_subhead ul,
    .ipsList_data li,
    .ipsList_withminiphoto li,
    table.ipb_table td,
    .store_categories li,
    #mini_cart li,
    #index_stats div[id*="statusReply"],
    #ipg_category .ipg_category_row,
    .block_inner .ipb_table td,
    .gallery_pane h2,
    .status_feedback li[id*="statusReply"],
    .ipsSideMenu ul li,
    #usercp_content .ipsType_subtitle,
    .ipbfs_login_row,
    .articles .block-1,
    .articles .type-1x2x2 .article_row,
    #idm_category .idm_category_row,
    #category_list li a,
    .ipsComment {
        border-bottom: 1px solid #969696;
    }
    .row1,
    .post_block.row1 {
        background-color: #848484;
    }
    .row2,
    .post_block.row2 {
        background-color: #969696;
    }
    #shouts-global-resizer,
    #shouts-resizer {
        background-color: #848484;
    }
    #shoutbox-shouts-table {
        border: 0px;
    }
    .input_text,
    .ipsTagBox_wrapper,
    textarea {
        background: #b9b9b9;
        color: #cacaca;
    }
    .ipsSideBlock,
    .general_box {
        background: #848484;
    }
    .desc.lighter,
    .desc.lighter.blend_links a {
        color: #cacaca;
    }
    .statistics_head {
        background: #727272;
        color: #cacaca;
        text-shadow: none;
    }
    .ipbfs_titlebox {
        background: #616161;
    }
    .ipbfs_titlebox .ipsType_pagedesc {
        color: #cacaca;
    }
    ul.post_controls,
    .column_view .post_wrap {
        background: #848484;
        border-top: 1px solid #616161;
    }
    .ipsLikeBar_info {
        background: none;
    }
    blockquote.ipsBlockquote {
        background: #a7a7a7;
        border: 1px solid #616161;
    }
    p.citation {
        background: #969696;
        border: 1px solid #616161;
        text-shadow: none;
    }
    .column_view .post_body {
        border-left: 1px solid #616161;
    }
    ul.post_controls a,
    ul.post_controls a.ipsButton_secondary {
        background: #b9b9b9;
    }
}
