css:
@-moz-document url-prefix("http://www.wsgrpg.net/") {
select, div.lol_content_mid_text input { background: #D48A00; color: #000; border-color: #000; }
body { background: #111; color: #D48A00 !important; }
a.link, div.header_bar_text a { color: #555 !important; }
a.link:hover, div.header_bar_text a:hover { color: #222 !important; }

/* Sign In */
div.login_top { 
    width: 410px;
    border: 2px solid #A76107;
    border-top-left-radius: 8px; 
    border-top-right-radius: 8px; 
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
}
div.login_mid {
    width: 410px;
    background: #222;
    border-left: 2px solid #A76107;
    border-right: 2px solid #A76107;
}
div.login_bot {
    background: #222;
    width: 410px;
    border: 2px solid #A76107;
    border-bottom-left-radius: 8px; 
    border-bottom-right-radius: 8px; 
}
div.login_cont { color: #D48A00; }
div.login_cont hr { background: #D48A00; color: #D48A00; }
div.login_cont input[type=text], div.login_cont input[type=password] { border: solid #D48A00 1px; }
div.login_cont input[type=submit] { background: #D48A00; color: #111; border: 1px solid #333; }
div.login_cont a { color: #A76107; }
div.login_cont a:hover { color: #D48A00; }
div.main_content_mid { background: #222; border-left: 2px solid #A76107; border-right: 2px solid #A76107; padding-bottom: 5px; width: 830px; }
div.main_content_top_text { color: #111; }
div.main_content_mid_text { color: #D48A00; }

/* Tooltips */
.tooltips { background: #222; color: #D48A00; border-color: #A76107; }

/* Header */
div.header { background-image: url('http://www.wsgrpg.net/images/halloween/header.png'); }

/* Notices/Announcements */
div.imp_ex { background: #D48A00; border: 2px solid #444; border-radius: 50%; }
div.imp_msg { background: #333; border: 1px solid #A76107; margin-left: 7px; color: #D48A00; }

/* Tables */
td.topleft, td.topmid, td.topright, td.midleft, td.midmid, td.midright, td.botleft, td.botmid, td.botright, td.botsingle
{ border-color: #A76107; }

/* Body */
div.content_bottom { background: #161616; border-left: 2px solid #A76107; border-right: 2px solid #A76107; width: 846px; }
div.content_bg { background: #161616; border-left: 2px solid #A76107; border-right: 2px solid #A76107; width: 846px; }
div.time_bar { 
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    width: 846px;
}
div.header_bar { 
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
    border-top: none;
    width: 846px;
}
div.lol_content_top { 
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
    width: 518px;
}
div.lol_content_top_text { color: #111; }
td.topsingle { background-image: url('http://i.imgur.com/9vT4D7T.png'); border-color: #A76107; }
div.nav_mid_roster_bar div { background-image: url('http://i.imgur.com/l0EgYUA.png') !important; }
div.nav_top { 
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    width: 146px;
}
div.nav_bar_mid { 
    background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
    border-top: none;
    width: 146px;
}
div.nav_bar_mid_text { color: #111; }
div.nav_bar_mid_text a, td.topsingle, div.time_bar_text, div.nav_top_text, td.topleft, td.topright, td.topmid
{ color: #333 !important; }
div.nav_mid_text { color: #777; }
div.nav_mid_text a { color: #996E1D; }
div.nav_mid_text a:hover { color: #D48A00; }
div.nav_bot { 
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    border-top: none;
    width: 146px;
}
td.topleft, td.topmid, td.topright { 
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
}
td.topmid { border-left-width: 1px; border-right-width: 1px; }
div.lol_content_mid { background: #222; border-left: 1px solid #A76107; border-right: 1px solid #A76107; width: 520px; }
div.lol_content_mid_text { color: #D48A00; }
div.lol_content_mid_text a { color: #996E1D; }
div.lol_content_mid_text a:hover { color: #D48A00; }
div.lol_content_bot { background-image: url('http://i.imgur.com/4bBK42g.png'); }
td.midsingle { border-color: #A76107; }
td.botleft, td.botright, td.midsingle, td.midleft, td.midmid, td.midright,td.botsingle, td.botmid
{ background: #333; }

/* Sidebars */
div.nav_mid_promo, div.nav_mid, div.nav_mid_bot
{ background: #222; border-bottom: 1px solid #D48A00; border-left: 1px solid #A76107; border-right: 1px solid #A76107; width: 148px; 
}

/* Roster */
div.nav_mid_roster { background: #222; border-color: #D48A00; border-left: 1px solid #A76107; border-right: 1px solid #A76107; width: 148px; }
div.nav_mid_roster_bar { border-color: #D48A00; }
div.nav_mid_roster_text, div.nav_mid_roster_text a { color: #D48A00; }
div.roster_tip_top { background-image: url('http://i.imgur.com/fApi6c4.png'); }
div.roster_tip_mid { background: url('http://i.imgur.com/CYDo3pd.png') top left; }
div.roster_tip_mid_text { color: #D48A00; }
div.roster_tip_bot { background-image: url('http://i.imgur.com/yFQ683g.png'); }

/* Footer */
div.footer_bar {
	background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    width: 846px;
}
div.footer_bar_text { color: #111; }
div.footer_bar_text a { color: #555; }
div.footer_bar_text a:hover { color: #333; }
div.content_bottom { background-image: none; }

/* Toolbar */
div.toolbar_arrow { background-image: url('http://i.imgur.com/v8hTHrl.png'); }
div.toolbar_main { background-image: url('http://i.imgur.com/XtwRvon.png'); }
div.toolbar_main_text { color: #666; }
div.toolbar_main_text a { color: #996E1D; }
div.toolbar_main_text a:hover { color: #D48A00; }
div.toolbar_box_x { background-image: url('http://i.imgur.com/ObumhsL.png'); }
div.toolbar_box_mid { background: #333; }
div.toolbar_box_top { background-image: url('http://i.imgur.com/E0ndPUx.png'); }
div.toolbar_box_top_text { color: #D48A00; }
div.toolbar_frame_text { color: #D48A00; }
div.toolbar_frame_text a { color: #666; }
div.toolbar_frame_text a:hover { color: #996E1D; }

/* Forums */
.link1 { color: #111 !important; }
.link1:hover { color: #555 !important; }
div.main_content a, div.main_content_mid_sections_text a { color: #D48A00 !important; }
div.main_content a:hover, div.main_content_mid_sections_text a:hover { color: #A3650F !important; }
div.main_content_top { 
    width: 830px;
    background: #EDAB4E; /* Old browsers */
    background: -moz-linear-gradient(top, #EDAB4E 0%, #DC9634 50%, #C87F1A 51%, #BF7611 100%); /* FF3.6-15 */
    background: -webkit-linear-gradient(top, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* Chrome10-25,Safari5.1-6 */
    background: linear-gradient(to bottom, #EDAB4E 0%,#DC9634 50%,#C87F1A 51%,#BF7611 100%); /* W3C, IE10+, FF16+, Chrome26+, Opera12+, Safari7+ */
    border: 2px solid #A76107;
}
div.main_content_bot {
    background: #222;
    border: 2px solid #A76107;
    border-bottom-left-radius: 8px;
    border-bottom-right-radius: 8px;
    width: 830px;
}
td.forum_section_left, td.forum_tsection_left, td.forum_tsection_mid, td.forum_tsection_right, td.forum_section_mid, td.forum_section_right, td.forum_section_single_noheight, td.profile_main, td.profile_main_v2, td.forum_asection_left, td.forum_asection_right,td.forum_section_left_noheight, td.forum_section_right_noheight
{
    background: #222; 
    border-color: #A76107;
    color: #996E1D; 
}
td.forum_tsection_single { background: #222; border-color: #A76107; color: #A3650F; }
td.forum_asection_single { background: #333; border-color: #A76107; color: #A3650F }
/* posts */
td.forum_asection_left, td.forum_asection_right,td.forum_section_left_noheight, td.forum_section_right_noheight { 
    color: #A3650F; 
}
}

@-moz-document url-prefix("http://www.wsgrpg.net/forum/index.php?act=profile&p=") {
  div.main_content_mid_sections_text a { color: #555 !important; }
	div.main_content_mid_sections_text a:hover { color: #111 !important; }
}
