css:
@-moz-document url("http://mypi.ruliweb.com/"), url-prefix("http://bbs.ruliweb.com/"), url-prefix("http://www.ruliweb.com/") {
body {
    background-color: black;
    color: #aaa;
}
p {
    color: #999;
}
a {
    color: #999;
}
h4.title {
    color: white;
}
h5 {
    color: #ccc;
}
hr{
    border-top : 1px solid #444;
}

/* Main */
#main_top .section_style_box{
    border: solid 1px #444;
}
#main_top .section_style_box .widget_header{
    background-color: #111;
    border-bottom: solid 1px #444;
}
#main_top .section_style_box .widget_header .btn_wrapper button,
#main_top .section_style_box .widget_header .btn_wrapper a .btn_more{
    background-color: #222;
    border: solid 1px #333;
}

#main_top .section_style_box .widget_header .add_link a{
    color: #aaa;
}

#main_top .main_aside hr{
    border-top: 1px solid #444;
}
#main_top .section .section_title{
    color: #fff;
}

#board_tab .sub_title_wrapper{
    background-color: #222;
}

#main .main_content_area.default .main_aside{
    background-color: #222;
}

.widget .widget_top .top_list_item .title{
    color: #ddd;
}

.num_reply{
    color: #82a8d1;
}

.reading.widget.widget_page,
.bottom_list.col.col_6,
.widget_bottom.row{
    background-color: #222;
}

/* Header */

#header .gnb_wrapper .logo .ruliweb_icon {
    display: none;
}
#header .gnb_wrapper .logo a{
    display: block;
}
#header .gnb_wrapper .logo a::after {
    content: "";
    display: block;
    width: 48px;
    height: 54px;
    background-image: url("http://img.ruliweb.com/img/2016/icon/ruliweb_icon_144_144.png");
    background-size: 100%;
    background-position: 0 5px;
    background-repeat: no-repeat;
    margin-right: 10px;
    margin-left: 10px;
}
#header {
    background-color: black;
}
#header .gnb_wrapper .menu_wrapper .menu_background {
    background-color: #333;
}
#header .gnb_wrapper .menu_wrapper .menu .family .family_text{
    background-color: #444;
}
#header .gnb_wrapper .menu_wrapper .menu .family {
    background-color: #111f38;
}
#header .gnb_wrapper .menu_wrapper .menu>ul li a.active {
    color: #98ffff;
    background-color: #1f1e4a;
}
#header .ruliweb_text_icon_wrapper .ruliweb_text_icon{
    display:none;
}
#header > div:nth-child(3) > div.mini.max_width_outer > ul > li.ruliweb_text_icon_wrapper > a::before{
    content:"RULIWEB - Dark Version";
}
#header > div:nth-child(1) > div.mini.max_width_outer > ul > li.ruliweb_text_icon_wrapper > a::before{
    content:"RULIWEB - Dark Version";
}
#header .btn_search{
    background-color : #fff;
}
/* #header .header_search_wrapper{
    margin-left: 840px;
} */
.btn_ruli_dark,
.btn_ruli_light {
    border: solid 1px #444;
}
.btn_light {
    border: solid 1px #444;
}
.btn_light,
.btn_ruli_light {
    background-color: #222;
    color: white;
}
#ad_main_ti,
#footer {
    background-color: #222;
    border-top: 1px solid #222;
}
#footer .aticle_background {
    background-color: #222;
}
#footer > div.family_wrapper.max_width_outer {
    background-color: black;
}
#footer .family_wrapper .title {
    color: #666;
}
#footer .family_background {
    background-color: black;
    border-bottom: 1px solid #222;
}
#footer .aticle_wrapper .aticle li{
    color: #aaa;
}
#main .main_content_area{
    background-color: #111;
}
#main .main_content_area.gnb_main_nav_include .nav .nav_background {
    border: dashed 1px #333;
}
#nav .nav_wrapper .sub.active {
    background-color: #333;
    color: black;
}
#nav .nav_wrapper .sub.active a h5 {
    color: white;
    font-weight: bold;
}
/*  Aside Style */

#mAside .right_border {
    border-right: none;
}
.top_best.best_list {
    background-color: #222;
    border: solid 1px #444;
}
#board_top .board_title {
    background-color: #082743;
}
#board_top .board_title.visited {
    display: none;
}
#board_top .best_list .list_title .title_text {
    font-family: "Malgun Gothic", "맑은 고딕", sans-serif;
    text-shadow: 0 0 .5px #aaa;
    color: white;
}
/* Board list */
.board_main.theme_defalut{
    border: none;
}
.board_top .btn_wrapper .category_menu .list_detail li a {
    color: white;
}
.board_top .btn_wrapper .category_menu .category_select{
    background-color: black;
    color: white;
}
.board_main.theme_defalut th {
    border-bottom: 1px solid #5c6c90;
    color: #fff;
    background-color: #333;
}
.board_main.theme_defalut .table_body.notice td {
    background-color: #222;
    color: white;
}
#board_read .board_main{
    background-color : #161616;
}
.theme_defalut .comment_view_wrapper .comment_view.best .comment_table{
    border: solid 1px #333;
    border-bottom: none;
}
.theme_defalut .comment_view_wrapper .comment_view.normal .comment_table {
    border: solid 1px #333;
}
.theme_defalut .comment_view_wrapper .comment_view.normal .comment_table td {
    background-color: #222;
    color: white;
}
.theme_defalut .comment_view_wrapper .comment_view .comment_element.parent .user {
    color: white;
    background-color: #111;
}
.theme_defalut .comment_view .comment_element.parent .control_box button {
    background-color: #222;
}
.theme_defalut .comment_view_wrapper .comment_view .comment_element.best{
    background-color: #333 !important;
}
.theme_defalut .comment_view_wrapper .comment_view .comment_element.best .user, .theme_defalut .comment_view_wrapper .comment_view .comment_element.best .user_rated{
    background-color: #222 !important;
}
.comment_wrapper .comment_view .comment_element {}
.comment_wrapper .comment_view .comment_element.parent .control_box button {
    border: 1px solid #000;
}
.comment_wrapper .comment_view .comment_element .comment .text_wrapper .text{
    color: #999;
}
.board_main.theme_defalut td a {
    color: #999;
}
.table_body.current_article a.deco{
    color: #ccc;
    font-weight: bold;
}
.board_main.theme_defalut td {
    color: white;
    border-top: 1px solid #444;
    border-bottom: 1px solid #444;
    background-color: #111;
}
.icon-picture,
.icon-youtube-play {
    color: #acccff;
}
.paging_wrapper .btn_num.active,
.paging_wrapper .btn_num:hover {
    background-color: #222;
    border: 1px solid #333;
    color: white;
}
.ad {
    display: none;
}
#board_read .board_main {
    border: 1px solid #222;
}
#board_read .board_main .board_main_top .user_view .user_info .nick {
    color: white;
}
#board_read .board_main .board_main_top .user_view .user_info .btn_wrapper a {
    color: white;
}
#board_read .board_main .board_main_top .user_view {
    margin-right: 0;
    width: 100%;
}
#board_read .board_main .board_main_top .user_view .user_info p .exp_bar .exp_bar_state {
    background-color: #aaa;
}
#board_read .board_main .board_main_top .right_wrapper {
    display: none;
}
#board_read .board_main .board_main_top .user_view .share_wrapper .btn_report .btn_report_text {
    color: white;
}
#board_read .board_main .board_main_top .user_view .subject {
    background-color: #333;
    color: white;
}
#board_read .board_main .board_main_view .view_bottom .dislike,
#board_read .board_main .board_main_view .view_bottom .like {
    border: solid 1px #222;
    background-color: #111;
}
#board_read .board_main .board_main_top{
    border-bottom: none;
}

#board_read .board_main .board_main_top .user_view .share_wrapper{
    border-top : solid 1px #222;   
    border-bottom : solid 1px #222;   
}

#board_read .board_main .board_main_top .user_view .user_achievement .exp_bar_state{
    background-color: #191919;
    
}

#board_read .board_main .board_main_top .user_view .user_achievement .exp_bar{
    border: solid 1px #262626;
}

#board_read .board_main_bottom {
    border-top: 1px solid #222;
}
#board_read .board_bottom_bottom{
    border-top: 1px solid #222;
}
.board_main.theme_defalut .table_body.current_article td {
    background-color: #444;
}
.comment_wrapper .comment_view .comment_element .control_box .btn_report,
.theme_defalut .comment_view {
    background-color: #111;
    color: white;
}
.common_write .common_write_wrapper .common_input_wrapper .common_input {
    background-color: #333;
    color: white;
    border-bottom: 1px solid #444;
}
.common_write .common_write_wrapper .common_input_wrapper {
    border-top: 1px solid #444;
    border-left: 1px solid #444;
}
.common_write .common_write_wrapper .common_input_wrapper .common_input_btn {
    background-color: #555;
}
.theme_defalut .comment_view .comment_element.parent .control_box button {
    background-color: #111;
}
.theme_defalut .comment_view.best .user {
    background-color: #1b1f2b;
}
.theme_defalut .comment_view.best {
    background-color: #162743;
}
.theme_defalut .comment_view .user {
    background-color: #2b2b2b;
}
.theme_defalut .comment_view.normal .comment_table {
    border: 1px solid #444;
}
.comment_wrapper .comment_view .comment_element {
    border-top: 1px solid #444;
}
.theme_defalut .comment_view.best .comment_table {
    border-top: 1px solid #444;
    border-left: 1px solid #444;
    border-right: 1px solid #444;
}


/*  Write */

#write_form  h4 strong, #write_form label{
    color: white;
}

#board_write .subject_wrapper .subject, #board_write .link{
    background-color: #222;
    color: white;
    border: solid 1px #333;
}


/* Widget */
.widget.widget_rank .rank_item.top{
    background-color: black;
}
.widget.widget_rank .rank_item{
    background-color: black;
}


.widget.tab_box.hover .sub_title_wrapper .sub_title{
    background-color: #222;
    border: solid 1px #444;
    color: #aaa;
}

.widget .inner_box .list .list_item{
    list-style: none;
    padding-left : 10px;
}

.widget .inner_box .list .list_item a{
    color: white;
}

/* Best */

.list.tab_box_page.dot.active{
    background-color: #222;
}

.contents_main #best_article_list_tab .list_item{
    list-style: none;
    padding-left : 10px;
    color: white;
}

.contents_main #best_article_list_tab .list_item a{
    color: white;
}

.contents_main .center_aside_wrapper{
    background-color: #111;
}

/* Article page */
.articleBody{
    color: white;
}


/* Family */

.snb_family{
    background-color: #222;
    border: 1px solid #444;
}
.snb_family .list_menu .menu{
    border-top: 1px solid #444;
}
.snb_family .list_snb .link_txt{
    color: #aaa;
}

/* Game */

.games_attack .btn_search{
        border: 3px solid #0d3c77;
    background-color: #111;
}
}
