css:
@-moz-document domain("userstyles.org") {
/* userstyle.org
 *-------------------------------------------------*/

/*  
 * force hidden 
 */

#social
{
    display: none !important;
}

a {
    color: #333;
    display: inline-block;
    padding-bottom: 1px;
    border-bottom: solid #000 1px;
    text-decoration: none;
}

a:hover{
    color: #666;
}

a:visited:hover {
    color: #000;
}

a:visited {
    color: #bbb;
    text-decoration: none;
}

#main-header {
	 background-color: #fff;    
    margin: 70px auto;
}

header#main-header a {
    display: inline-block;
    border-bottom: #000 solid 2px;
    color: #000;
    padding-bottom: 6px;
}

#content-wrapper {
    float: left;
    width: 70%;
}

#main-article {
    margin: 0;
}

#left-sidebar{
    margin: 0;
    width: 100%;
}

#left-sidebar>* {
    background: none;
}

#left-sidebar nav {
    margin: 50px 0;
}

#left-sidebar li {
    display: inline-block;
}

#sidebar-search {
    position: fixed;
    top: 75px;
    right: 10px;
}

#search-terms {
    border: none;
    outline: none;
    border-bottom: solid 1px #000;
}

nav li:not(:first-child) {
    border-bottom: 2px solid #000;
    padding: 1px 5px;
}


nav li a,
nav li a:visited {
    color: #000;
    border: none;
}

#search-submit {
    background: none;
    border: 8px solid #000;
    border-radius: 50%;
    outline: none;
}


#search-submit:hover ,
#advanced-search-submit input[type="submit"],
div#advanced-search-submit button
{
    cursor: pointer;
}

div#advanced-search {
    background: #fff;
    margin: 2px 0;
    color: #000;
}

.advanced-search-active #search-terms{
    width: 100%;
}

#advanced-search select {
    border: none;
    outline: none;
}

div#advanced-search-submit button {
    border: 1px solid #dedede;
    width: 80px;
    font-size: 8px;
    line-height: 8px;
    height:15px;
    color: #dedede;
    background: #fff;
    outline: none;
}
div#advanced-search-submit button:hover {
    color: #000;
    border: 1px solid #000;
}

#advanced-search-submit input[type="submit"] {
    border: 1px solid #dddddd;
    border-radius: 50%;
    height: 70px;
    width: 70px;
    color: #dddddd;
    background: #fefefe;
    outline: none;
}

#advanced-search-submit input[type="submit"]:hover {
    color: #000;
    background: #fff;
}

/* userstyle.org/users
 *-------------------------------------------------*/
.author-styles td, 
.author-styles th {
    border-top: 1px solid #000;
    width: 60em;
    padding: 10px 0;
}

.author-styles  tr:last-child td {
    border-bottom: 1px solid #000;
}

table.author-styles {
    border: none;
    border-spacing: 0;
}

.author-styles  td {
    vertical-align: middle;
}

.author-styles  tr td:last-child{
    width: 200px;
}

.author-styles td a {
    border: none;
    color: #000;
}

.author-styles td a:visited{
    color: #000;
}



/* userstyle.org/styles/browse/
 *-------------------------------------------------*/
.style-brief {
    margin-bottom: 5em;
}

.style-brief-text {
    height: 195px;
    overflow: hidden;
}
}
