css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix("https://www.tumblr.com") {
/* main background and bg color */
body, #footer, div#footer_container.clearfix, #auto_pagination_loader, #search_actions_search,  .l-container.l-container--flex .l-content {
    background: #000020 url(http://i79.fastpic.ru/big/2016/0816/91/24a3254816640e5c855a1f6c620bcd91.jpg) left fixed !important;
    background-size: cover !important;
    }
/* new header bar */
.l-header-container {
    background: rgba(20,20,60,1) !important;
    }
/* fix hover color on sidebar controls and recommended blogs */
.controls_section_item:hover, .recommended_tumblelogs li:hover, .discover .controls_section.follow_list .item:hover {
    background-color: rgba(0,0,20,1) !important;
    }   
#auto_pagination_loader {
    border: none !important;
    }
/* search tweaks */
#search_actions_search .tumblelog_mask_item {
    background: none !important;
    }
#search_actions_search .blogs_wrapper_carousel:after, #search_actions_search .blogs_wrapper_carousel:before {   
    background: none !important;
    }
.discover-controls-wrapper {
    background: rgb(20,20,60) !important;
    }
/* in-dash and popup notifications */
.notification_inner, #toaster .toast .toast_border, #toaster .toast:hover {
    background: rgba(20,20,60,0.6) !important;
    }
/* blue content fill and gradient at the top of page */
#dashboard_index .l-content, #dashboard_posts_editing .l-content, #dashboard_useraction_followers .l-content, #activity_actions_index .l-content, #dashboard_drafts .l-content, #dashboard_group_members .l-content, #dashboard_post_queue .l-content, #dashboard_posts_likes .l-content, #dashboard_messages_inbox .l-content, #dashboard_messages_submissions .l-content, #tagged_actions_display .l-content {
    background: url(data:image/png;base64,) top repeat-x !important;
    padding-top: 20px !important;
    }
/* fix post submission forms and customize appearance forms */
.mceContentBody, #upload_image_form {
    background: #ffffff  !important;
    } 
.dashboard_iframe {
    background: transparent !important;
    }
/* follow button in notifications */
.control.follow.ui_follow {
    background: rgba(255,255,255,0.8) !important;
    }
/* "dog ear" permalink fix */
#dashboard_index .post_permalink, #dashboard_drafts .post_permalink, #dashboard_post_queue .post_permalink, #search_actions_search .post_permalink, #dashboard_posts_likes .post_permalink { 
    background: rgb(60,60,100);
    border-color: #000010 #000010 transparent transparent;
    }
.peepr-drawer .post_full .post_permalink {
    border-color: #fff #fff transparent transparent !important; 
    background: #777 !important; 
    }
/* remove blue background from left and right columns, remove errant gradient from the sidebar */
div#right_column,div#right_column:after, .left_column {
    background: none !important;
    }
/* Prev and Next page buttons */
#pagination a {
    color: #eaeaff;
    background: rgba(60,60,100,.3) !important;
    border: 1px solid #eaeaff;
    }
/* transparent icons in queue, add glow to avatars */
.post_avatar {
    background-color: transparent !important;
    border-color: transparent !important;
    }
.post_avatar_link {
    box-shadow: 0px 0px 8px #79A0C4;
    }
/* remove gradients from recommended blogs, discovery recommended, and radar section */
.controls_section.user_list li a:before, .radar_footer .radar_avatar:before {
    background: transparent !important;
    border: none !important;
    }
.discover .blog_row .info:before, .blogs-holder {
    background: none !important; 
    border-right: none !important;
    }
/* remove circular background from top-of-page elevator icon */
#discover_actions_discover .elevator, #search_actions_search .elevator {
    background-color: transparent !important;
    }
/* Use a standard white color for follow icons in recommended blogs section */
.icon_plus {
    color: rgba(255,255,255,0.32) !important;
    }
.icon_plus:hover {
    color: rgb(255, 255, 255) !important;
    }
/* transparent post box */    
.post.post_full, #search_actions_search .post_brick, #discover_actions_discover .post_brick {
    background: rgba(60,60,100,.3);
    transition: all 0.3s linear 0s;
    }
#new_post:hover, .post.post_full:hover, #search_actions_search .post_brick:hover, #discover_actions_discover .post_brick:hover {
    background: rgba(20,20,60,1);
    transition: all 0.3s linear 0s;
    }
.post-source-footer {
    background: transparent !important; 
    color: #eaeaff !important;
    }
  
/* majority of foreground text */
.post.post_full, .post .post_content a, .reblog-content a, .post.post_full .quote, span.quote, .new_post_label_text, .new_post_label i.icon_post_text, .post_brick {
    color: #eaeaff;
    }
/* link text */
.post_info_link, .note_link_current, .post_full .post_info, .posts .post_tag, .post_brick .post_tag, .post-info-tumblelog a {
    color: #79A0C4 !important;
    }
/* undo new link underline code */
.post .post_content a {
    text-decoration: underline;    
    }
/* remove extra underlining on links */	
.post .post_body a {
	background-image: none;
	}
/* featured tags */
.post_tag.featured {
    color: #eaeaff !important; 
    }
/* post nipple beside avatar (depricated) div.post_avatar:after {border-right: 9px solid rgba(60,60,100,.35) !important;} */
/* opacity for the reblog/favorite/other buttons */
.post_control:after { 
    opacity: .6 !important;
    }
.post_control.active:after, .post_control:hover:after {
    opacity: 1 !important;
    }
/* change post control icons to black, maintain red color for liked posts */
.post_control.share, .post_control.messaging::before, .post_controls .post_control::before {
    color: #000000 !important;
    }
.post_controls .post_control.like.liked::before {
    color: #d95e40 !important;
	}
/* graident on footer for greater visiblity on post controls */
.post_footer {
    background: linear-gradient(90deg, rgba(60,60,100,0),rgba(60,60,100,0.5),rgba(60,60,100,1));
    } 
/* maintain original color for the activity page and other items */
.notifications_page a, .indash_blog .reblog-content, .indash_blog .reblog-content a, .indash_blog .post_title, .indash_blog a, .audio_search_results li a, .audio_tabs li a, .popover_menu_item a, .indash_blog .answer.post_info, .indash_blog .post_notes_label a, .indash_blog .post_full.is_quote .post_title, .indash_blog h2, h2.heading.arrow, .indash_blog .post-source-footer, .post-typography-update .post .post-content h2, .note_item h2 { 
    color: #444 !important;
    }
.indash_blog .post_notes_label a {
    color: inherit !important; 
    }
.indash_blog .description a {
    color: inherit !important; 
    text-decoration: underline;
    }
.peepr-drawer a {
	background-image: none !important;
}
.indash_blog .post_tag {
    color: #A1A1A1 !important;
    }
.indash_header_wrapper .navigation a, .indash_header_wrapper .navigation .open_blog_button, .indash_header_wrapper .navigation .info_popover_button { 
    color: #fff !important; 
    }
.indash_blog .post_footer {
    background: inherit;
    }
/* maintain white bg in the settings page */
#settings_actions_tumblelog .settings_group_wrapper, #settings_actions_account #left_column, #settings_actions_dashboard #left_column, #settings_actions_notifications #left_column, #settings_actions_apps #left_column  {
    background: #fff !important;
    }
/* reblogged-from item in posts, maintain in indash blog */
.post_info .reblog_icon {
	color: #eaeaff !important;
}
.indash_blog .post_info .reblog_icon {
    color: #444 !important;
	}
.post_controls .post_control.reblog.reblogged::before {
	color: #56bc8a !important;                                             
    }
/* old img replacement code for .reblog_icon {
    background: url(data:image/png;base64,) center no-repeat !important; 
    opacity: .75 !important;
    } */
/* inbox color corrections */
.note_item {
    color: #444 !important;
    }
.note_item a.name, .note_item .text a, .post.fan_mail {
    color: #444 !important;
    }
.answer.post_info {
    color: #eaeaff !important;
    }
.recycled { 
    background: top left repeat url('https://www.tumblr.com/images/fan_mail/paper_recycled-1_repeat.png') #d6d2c7 !important; 
    }
.post .post_content .editor a {
    color:#444 !important; 
    }
.post_content blockquote {
    border-left: solid 3px #eaeaff;
    }
.indash_blog blockquote {
    border-left: solid 3px #eee;
    }
/* remove ending gradient in the tags field */
.post_tags:after {
    background:none !important;
    }
/* chat post correction */
.post_full.is_conversation .conversation_lines .chat_line:nth-child(2n), .post_brick.is_conversation .conversation_lines .chat_line:nth-child(2n) {
    background: #000020 !important;
    }   
pre {
    background: #000020 !important;
    color: #eaeaff !important;
    }
.indash_blog pre {
    background: none !important;
    color: #444 !important;
    }
/* alter bg and link text color on the link button for specific pages */
#dashboard_index .link-button, #dashboard_drafts .link-button, #search_actions_search .link-button, #tagged_actions_display .link-button, #discover_actions_discover .link-button { 
	background-color: rgb(20,20,60);
	}
.post_full.is_link .link-button.link-button--default .info-container .title, .post_full.is_link .link-button.link-button--default .info-container .excerpt, .post_brick.is_link .link-button.link-button--default .info-container .title, .post_brick.is_link .link-button.link-button--default .info-container .excerpt, .post_full.is_link .link-button.link-button--default .info-container .publisher-container .publisher {
    color: #eaeaff;
	}
.peepr-drawer .post_full.is_link .link-button.link-button--default .info-container .title, .peepr-drawer .post_full.is_link .link-button.link-button--default .info-container .excerpt, .peepr-drawer .post_full.is_link .link-button.link-button--default .info-container .publisher-container .publisher {
    color: #444;
	}
/* preserve text color for link posts indash, and text fields in popover blogs */
.peepr-drawer .post .post_content, .peepr-drawer .post .post_content a, .peepr-drawer .post.post_full .quote, .indash_blog .link_text a, .link_text .link_title a, .link_excerpt a {
    color: #444 !important;
    } 
.peepr-drawer .post_full.is_link .link-button.link-button--default.has-thumbnail .publisher-container .publisher { 
    color:#fff; 
	}
.post_micro .post_content, .post_micro .post_content a {
    color: #444 !important;
    }
.peepr-drawer .post_full.is_link .link-button {background: #f2f2f2 !important;}
/* color correction for reblog button in post headers */
a.reblog_follow_button:not(.icon_follow_rounded_small):before, .reblog_follow_button i {
    color: #fff !important;
    background-color: #000020 !important; 
    }
/* bg for the recommended popup on like/reblog */
.rapid-recs {
	background-image: linear-gradient(to bottom,rgba(60,60,100,0) 0%,rgba(60,60,100,1) 15%, rgba(60,60,100,.5) 85%,rgba(60,60,100,0)100%) !important; 
	}
/* restore link color in reblog form */ 
.post-form--form .reblog-content a {
    color: #444 !important; 
	}

/* EOF */
}
